head.script:
         
    scwin.onpageload = function() {
	 gcm._container = windowContainer1;
	 //첫페이지 로딩
	 windowContainer1.createWindow( "메인" , null , "/ux/Coding_List.xml" , "" , "" , null , "main" , null );
	 //windowContainer1.createWindow( "출력" , null , "/publish/guide/Input.xml" , "" , "" , null , "null" , null );
	 //메뉴셋팅
	 scwin.setMenu();		
	};
	
	scwin.onpageunload = function() {
		
	};
	var status = "MDI";
	var myScroll1;
	var myScroll2;
	
	//메뉴 열림
	scwin.setMenu = function(){
	
		//lnb 스크롤 디자인 적용
		myScroll1 = new IScroll('.l1 .sub_wrap .sub', {
		    scrollbars: true,
		    click: true ,
		    mouseWheel:true
		}); 
		myScroll2 = new IScroll('.l2 .sub_wrap .sub', {
		    scrollbars: true,
		    click: true ,
		    mouseWheel:true
		}); 
	
		//lnb 토글
		var _seledted = null
		$('.left .navi > ul > li .link_wrap').click(function(){
			var _select = $(this).parent()
			if(_select.hasClass('on')){//선택된 메뉴 클릭시
				var _delay = 300
				if(!_select.find('.sub_wrap').length) _delay = 0
				setTimeout(function() { _select.removeClass('on'); }, _delay)
				$('.wrap').removeClass('lnb_on');
			}else{//선택안된 메뉴 클릭시
				if(_seledted) _seledted.removeClass('on')
				_select.addClass('on');
				if(_select.find('.sub_wrap').length) $('.wrap').addClass('lnb_on');
				else $('.wrap').removeClass('lnb_on')
			}
			_seledted = _select
			myScroll1.refresh();
			myScroll2.refresh();
		});
		
		//lnb 아코디언 처리 및 링크
		$('.left .navi .sub_wrap ul > li a').click(function(){
			$(this).parent().parent().find(' > li').removeClass('on');
			$(this).parent().addClass('on');
			if($(this).parent().find(' > ul').length){
				
			}else{
				//링크
				$('.left').removeClass('on');
				$('.left .navi > ul > li.l1 > a.on').removeClass('on');
				$('.left .navi .sub_wrap > li.l1 > a').removeClass('on');
				$(this).parent().parent().find(' ul li').removeClass('on');
				//windowContainer1.createWindow( "일반 업무화면" , null , "/src/main/webapp/ux/ui/01_List_detail_V.xml" , "" , "" , null , "" , null );
			}
			myScroll1.refresh();
			myScroll2.refresh();
		});
		
		//lnb 닫힘(logo 왼쪽 버튼)
		$('.top .btn_lnb_close').click(function(){
			$('.wrap').removeClass('lnb_on');
			
		})
		
		//알림 푸쉬 토글
		$('.top .util > li.l3 > a').click(function(){
			$(this).toggleClass('on')
			$('.wrap').toggleClass('push_on');
		})
		$('.push .head_wrap .btn_close').click(function(){
			$('.top .util > li.l3 > a').removeClass('on')
			$('.wrap').removeClass('push_on');
		})
		
		
		//퀵메뉴 토글
		$('.footer .btn_quick').click(function(){
			$(this).toggleClass('on')
			$('.footer').toggleClass('on');
		})
		
		
		
		
	};
