	/**************************************************************************
	 * 함 수 명: scwin.btnMenu3_onclick
	 * 기 능: 메뉴 버튼에서 3단계 클릭
	 **************************************************************************/	
	scwin.btnMenu3_onclick = function() {
		var title = this.getValue();
		var menuId = this.getUserData("winId");
		var url = this.getUserData("tabUrl");				
 
		if(menuId=="MNU_MG_203090"){  //계정과목별명세서
		        var userId =  sessionStorage.getItem("userId");
		        var nowDate = new Date();
		        var ppupOptNm = "top=1, left=1, width=1400,height=800,resizable=yes,toolbar=no,scrollbars=yes,menubar=no,status=yes, location=no";
		        var targetNm = userId+"_"+$w.getFormattedDate( nowDate , "yyyyMMddHHmmss" ) + nowDate.getMilliseconds() ;
		        var ridNm = "REP73B43CEE3E9A408CA1AACD3A97791B5E";
 
                var $form = $("<form></form>");
                window.open('about:blank', targetNm, ppupOptNm);
                $form.attr("target", targetNm);
                $form.attr("action", "http://bi.sdcit.co.kr/sso_login.jsp");
                $form.attr("method", "post");
                $form.attr("accept-language", "ko")
                $form.appendTo('body');

                $input = $("<input></input>");
                $input.attr({type:"hidden",  name:"UID",  id:"UID",  value:userId });
                $input.appendTo($form);

                $input = $("<input></input>");
                $input.attr({type:"hidden",  name:"RID",  id:"RID",  value: ridNm});
                $input.appendTo($form);

                $form.submit();		   
		}else{
			scwin.tabTitle = title;
			scwin.winId = menuId;
			scwin.tabUrl = url;
			
			gcm.MENU_TITLE = title;
			gcm.MENU_ID = menuId;
			gcm.MENU_URL = url;
			
			scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
			scwin.fnRemoveWrapQucikMenu();
			
            scwin.authCheck(menuId);
            
            
            //메뉴 체크를 authCheck 함수내로 변경
			//com.setAuthInfo(menuId);
			
	        //scwin.fnOpenTabEvt();	
        }
	};
    
    scwin.authCheck = function(_menuId){
        com.createDataObj("dataMap", "dsAuth", [ "menuId", "etc" ]);
        var _dmObj = $w.getComponentById("dsAuth");
        _dmObj.set("menuId", _menuId);
        ajaxLib.ajax("/cf/CFCommon/selectPrcgAuthCheck", {
            mode : "synchronous"
        , requestData : _dmObj.getJSON()
        , callback: function(result,e){
                //alert("func");
                var menuAllObj = result.data.tcfAuthGrpList;
                //alert(menuAllObj[0].personalAccessYn);
                if (!menuAllObj.length > 0 || menuAllObj[0].personalAccessYn == "N") {
                    com.showMessage('CFW0010','등록된 권한이 없습니다. <br/>개인정보 프로그램의 경우 내부망에서만 이용 가능합니다.');
                    return;
                }
                com.setAuthInfo(_menuId);
			
	            scwin.fnOpenTabEvt();	        
            }
        ,  errCallback: function(result,e){
                return; 
        }
        });
        //return false;
    };
    
    /**************************************************************************
     * 함 수 명: scwin.btnAllMenu_onclick
     * 기 능: 전체메뉴보기 클릭
     **************************************************************************/        
    scwin.btnAllMenu_onclic2k = function() {
        ajaxLib.ajax("/ep/Main/selectMenuTotalInfo", {
            mode: "synchronous",
            method: "POST",
            requestData: '',
            callback: function(result,e){
                var menuAllObj = result.data.menuAllInfo;
                if (menuAllObj == null) {
                    com.showMessage('CFW0010','등록된 전체메뉴보기가 없습니다.');
                    return;
                }
                
                var fileDownloadUrl = "/cf/CF00350000P/downloadMAVPDF?aflp=" + menuAllObj.aflp;
                var popOps = {
                               url        :"/ux/common/pdfView.xml"
                             , type       :"browser"
                             , modal      :false
                             , height     :"900"
                             , width      :"1000"
                             , popup_name :"전체메뉴보기"
                             , resizable  :true
                             , data       :{fileDownloadUrl:fileDownloadUrl}
                };
                com.popup_open(popOps);             
            },
            errCallback: function(result,e){
        }
        }); 
                        
    };
	
	/**************************************************************************
	 * 함 수 명: scwin.autoMenuSearch_onitemclick
	 * 기 능: 메뉴 검색어 입력하거나 키보드 또는 마우스 조작을 통해 변경된 경우
	 **************************************************************************/	
	scwin.autoMenuSearch_onviewchange = function(info) {
		var jsn = dsMenuSearchList.getRowJSON( info.newSelectedIndex - 1);
		
		scwin.tabTitle = jsn.menuNm;
		scwin.winId = jsn.menuId;
		scwin.tabUrl = jsn.scrPathNm;	
			
		gcm.MENU_ID = jsn.menuId;
		gcm.MENU_TITLE = jsn.menuNm;
		gcm.MENU_URL = jsn.scrPathNm;
		
		scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
		scwin.fnRemoveWrapQucikMenu();
		
		com.setAuthInfo(jsn.menuId);
		
		scwin.fnOpenTabEvt();
		
		autoMenuSearch.setValue(-1);
		
	};
				
