	/**************************************************************************
	 * 함 수 명: scwin.myMenuNmEdit_onclick
	 * 기 능: 마이메뉴 폴더명 편집
	 **************************************************************************/
	scwin.myMenuNmEdit_onclick = function() {
		var objData = this.getUserData("myMenuData");
		var label = this.getUserData("myMenuLabel");
		var input = this.getUserData("myMenuInput");
		
		$("#"+genMyMenu1.id).find(" > li").each(function(){
			var id =  $(this).attr("id");
			
			var iptMyMenu1Id = $("#"+id).find("> input[id $= 'iptMyMenu1']").attr("id");
			var btnMyMenu1Id = $("#"+id).find("> a[id $= 'btnMyMenu1']").attr("id");
			
			if(input.id == iptMyMenu1Id)
			{
				if($("#"+label.id).is(":visible") == false )
				{
					$("#"+input.id).hide(); 
					$("#"+label.id).show();
				}
				else
				{
					$("#"+label.id).hide();
					$("#"+input.id).show();
				}
			}
			else
			{
				$("#"+iptMyMenu1Id).hide();
				$("#"+btnMyMenu1Id).show();
			}
		});
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.myMenuDelete_onclick
	 * 기 능: 마이메뉴 삭제버튼
	 **************************************************************************/
	scwin.myMenuDelete_onclick = function() {
	
		if(!confirm("삭제하시겠습니까?")) return; 
		
		var objData = this.getUserData("myMenuData");

		//메뉴삭제		
	 	scwin.fnDeleteMyMenu(objData)
	 	
	 	//메뉴셋팅
	 	scwin.fnMyMenuSetEvent();
	 	
	 	//메뉴 드롭앤드롭
	 	scwin.fnMyMenuDragAndDropEvent();
	 	
	 	//navigator 즐겨찾기 on class 제거
	 	scwin.fnRemoveBookMakeCalss();
	 	
	 	//최근 메뉴사용이력 5건
    	scwin.fnSelectMenuHistTop5();
	 	
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.iptMyMenu1_onkeyup
	 * 기 능: 마이메뉴 폴더명 input 엔터키
	 **************************************************************************/
	scwin.iptMyMenu1_onkeyup = function(e) {
		var objData = this.getUserData("myMenuData");
		var label = this.getUserData("myMenuLabel");
		var input = this.getUserData("myMenuInput");
		
		//엔터키 onKeyup
		if (e.keyCode == 13) {
			
			//입력받은 메뉴명과 기존 메뉴명이 틀릴시
			if(objData.menuNm != $("#"+input.id).val())
			{
			    $("#"+label.id).text($("#"+input.id).val());
				objData.menuNm = $("#"+input.id).val();
				objData.menuNmNavi = $("#"+input.id).val();
				objData.bmkGrpNm = $("#"+input.id).val();
				
				//변경된 데이터 useData 에 set
			    this.setUserData("myMenuData",objData); 
				label.setUserData("myMenuData",objData);
				
				//메뉴명 변경
				scwin.fnUpdateMyMenu(objData);
				
				//메뉴셋팅
	 			scwin.fnMyMenuSetEvent();
	 			
	 			//메뉴 드롭앤드롭
	 			scwin.fnMyMenuDragAndDropEvent();
				
				//input 창이 활성화 되어 있으면 input hide , 라벨 show
				if($("#"+input.id).is(":visible"))
				{
					$("#"+input.id).hide();
					$("#"+label.id).show();
				} 
			}
        }
	};
	
