            let sTabId = tab_main.getTabID(index);
                        
            if (sTabId.indexOf("MNU") > -1){
                arrTabId.push(index);
                //tab_main.deleteTab(index);
            }        
		});
        */
        
        let arrTabId = [];
        //home, 가이드 탭을 제외한 나머지 탭 찾기.
        tab_main.getTabInfo().forEach(function(value, index, array) {
            if (value.id.indexOf("MNU") > -1){
                arrTabId.push(index);
            }  
        });     
        
        for (let i=arrTabId.length-1; i>=0; i--) {
            console.log(arrTabId[i]);
            tab_main.deleteTab(arrTabId[i]);
        }                 
	};
	
