		}
		
		scwin.fnMakeQuickWarpGen();
	};
	
	/**************************************************************************
    * Event: scwin.genQuickWrapSpan_onclick
    * 기 능: 퀵메뉴 span onClick 시 
    **************************************************************************/
	scwin.genQuickWrapSpan_onclick = function(e) {
	    var gubun = this.getUserData("gubun");
	    
	    if("link" == gubun)
	    {// 링크 처리
	    	var quickMenuData = this.getUserData("quickMenuData");
	    	
	    	com.quickMenuPopup(quickMenuData);
	    }
	    else if("quickMore" == gubun)
	    {//
	    	var wfQuick = wfm_quick.getWindow();
		    wfQuick.scwin["fnWarpQuickArea"]();
	    }
	};
	
		
	/**************************************************************************
	 * 함 수 명: scwin.btnAllMenu_onclick
	 * 기 능: 전체메뉴보기 클릭
	 **************************************************************************/		
	scwin.btnAllMenu_onclick = function() {
	    ajaxLib.ajax("/ep/Main/selectMenuTotalInfo", {
	        mode: "synchronous",
	        method: "POST",
	        requestData: '',
	        callback: function(result,e){
			    var menuAllObj = result.data.menuAllInfo;
			    if (menuAllObj == null) {
				    com.showMessage('CFW0010','등록된 전체메뉴보기가 없습니다.');
				    return;
			    }
			    
				var fileDownloadUrl = "/cf/CF00350000P/downloadMAVPDF?aflp=" + menuAllObj.aflp;
				var popOps = {
				               url        :"/ux/common/pdfView.xml"
				             , type       :"browser"
				             , modal      :false
				             , height     :"900"
				             , width      :"1000"
				             , popup_name :"전체메뉴보기"
				             , resizable  :true
				             , data       :{fileDownloadUrl:fileDownloadUrl}
				};
				com.popup_open(popOps);			    
			},
	        errCallback: function(result,e){
		}
	    });	
	    				
	};
			
