	/**************************************************************************
	 * 함 수 명: scwin.fnSetMenu
	 * 기 능: 메뉴 열림
	 **************************************************************************/
	scwin.fnSetMenu = function(){
	
		var myScroll1;
	    var myScroll2;
	
		//lnb 스크롤 디자인 적용
		/*
		myScroll1 = new IScroll('.l1 .sub_wrap .sub', {
		    scrollbars: true,
		    click: true ,
		    mouseWheel:true
		}); 
		*/
		myScroll2 = new IScroll('.l2 .sub_wrap .sub', {
		    scrollbars: true,
		    click: true ,
		    mouseWheel:true
		});
		
		//lnb 토글
		//var _seledted = null
		$('.left .navi > ul > li .link_wrap').click(function(){
			//scwin.fnNewMailAprvCnt();
        				
			var _idx = $('.left .navi > ul > li .link_wrap').index(this);
//			console.log('메뉴열릴때 :: ');
//			console.log('_idx :: ' + _idx);
			var _select = $(this).parent();
			var id = $(this).attr("id");
			grpMainWrap.removeClass('on');
			
			//eHrms 클릭시 선택해제 및 return;
			if(id.indexOf("grpEhrmsLink") > -1){
				return;
			}
			
			//경영현향 제거
			$('.footer > .mg_wrap, .btn_mg').removeClass('on');
			
			if(_select.hasClass('on')){//선택된 메뉴 클릭시
//				console.log('선택된 메뉴 클릭시');
				
				if(_idx == 0 || _idx == 1)
				{
					var _delay = 300
					if(!_select.find('.sub_wrap').length) _delay = 0
					setTimeout(function() { _select.removeClass('on'); }, _delay)
					$('.wrap').removeClass('lnb_on');
				}
				
			}else{//선택안된 메뉴 클릭시
				//console.log(_seledted);
//				console.log('!!!!! 선택된 안된 메뉴 클릭시');
				//if(_seledted) _seledted.removeClass('on')
				$("#"+grpLeftArea.id).find("> li").removeClass('on'); 
				_select.addClass('on');
				
				if(_select.find('.sub_wrap').length){
					$('.wrap').addClass('lnb_on');
				}
				else{
					$('.wrap').removeClass('lnb_on')
				}
			}
			//_seledted = _select
			myScroll2.refresh();
			
		});
		
		//마이메뉴 lnb 아코디언 처리 및 링크
		$("#"+grpMyMenuL1.id).find(".sub_wrap ul > li a").click(function(){
			
			if($(this).parent().hasClass("on"))
			{
				$(this).parent().removeClass('on');
			}
			else
			{
				$(this).parent().addClass('on');
			}
			
			if($(this).parent().find(' > ul').length){
				
			}else{
				//링크
				$('.left').removeClass('on');
				$('.left .navi > ul > li.l1 > a.on').removeClass('on');
				$('.left .navi .sub_wrap > li.l1 > a').removeClass('on');
				$(this).parent().parent().find(' ul li').removeClass('on');
				
			}
		});
		
		//lnb  아코디언 처리 및 링크 grpMenuL2
		$("#"+grpMenuL2.id).find(".sub_wrap ul > li a").click(function(){
			
			if($(this).parent().hasClass("on"))
			{
				$(this).parent().removeClass('on');
			}
			else
			{
				$(this).parent().addClass('on');
			}
			
			if($(this).parent().find(' > ul').length){
				
			}else{
				//링크
				$('.left').removeClass('on');
				$('.left .navi > ul > li.l1 > a.on').removeClass('on');
				$('.left .navi .sub_wrap > li.l1 > a').removeClass('on');
				$(this).parent().parent().find(' ul li').removeClass('on');
				
			}
			myScroll2.refresh();
		});
		
		
		//lnb 닫힘(logo 왼쪽 버튼)
		$('.top .btn_lnb_close').click(function(){
			$('.wrap').removeClass('lnb_on');
			
		})
		
		//알림 푸쉬 토글
		$('.top .util > li.l3 > a').click(function(){
			$(this).toggleClass('on')
			$('.wrap').toggleClass('push_on');
		})
		$('.push .head_wrap .btn_close').click(function(){
			$('.top .util > li.l3 > a').removeClass('on')
			$('.wrap').removeClass('push_on');
		})
		
		
		//퀵메뉴 토글
		var _interID;
		$('.left .btn_quick').click(function(){
			$(this).toggleClass('on');
			$('.btn_mg, .btn_link').removeClass('on');
			$('.footer').removeClass('on mg');
			$('.footer .mg_wrap, .link_group').removeClass('on');
			$('.footer .quick_wrap').toggleClass('on');
			$('.quick_body').addClass('btmup');
			if($('.footer .quick_wrap').hasClass('on')) $('.footer').addClass('on');
			else {
				clearInterval(_interID);
				_interID = setTimeout(function(){
					$('.btn_mg').removeClass('on');
					$('.quick_body').removeClass('btmup');
					$('.footer').removeClass('on mg');
				},300);
			}
		})	

	
	    //경영현황 토글
		var _interID2;
		$('.left .btn_mg').click(function(){
			$(this).toggleClass('on');
			$('.btn_quick, .btn_link').removeClass('on');
			$('.footer .quick_wrap, .quick_body, .link_group').removeClass('on');
			$('.footer .mg_wrap').toggleClass('on');
			if($('.footer .mg_wrap').hasClass('on')) $('.footer').addClass('on mg');
			else {
				clearInterval(_interID2);
				_interID2 = setTimeout(function(){					
					$('.footer').removeClass('on mg');
				},300);
			}
		})	
		var myScroll3;
		myScroll3 = new IScroll('.mg_list_wrap .mg_list_scroll', {
		    scrollbars: true,
		    click: true ,
		    mouseWheel:true
		});
		
		//링크 토글
		var _interID3;
		$('.left .btn_link').click(function(){
			$(this).toggleClass('on');
			$('.btn_quick, .btn_mg').removeClass('on');
			$('.footer .quick_wrap, .quick_body, .mg_wrap').removeClass('on');
			$('.footer .link_group').toggleClass('on');
			if($('.footer .link_group').hasClass('on')) $('.footer').addClass('on');
			else {
				clearInterval(_interID3);
				_interID3 = setTimeout(function(){					
					$('.footer').removeClass('on');
				},300);
			}
		})	
	};
	
	//마이메뉴 메뉴 아코디언
	scwin.fnMyMenuSetEvent = function(){
		//lnb 아코디언 처리 및 링크
		$("#"+grpMyMenuL1.id).find(".sub_wrap ul > li a").click(function(){
			
			if($(this).parent().hasClass("on")){
				$(this).parent().removeClass('on');
			}
			else{
				$(this).parent().addClass('on');
			}
			
			if($(this).parent().find(' > ul').length){
				
			}else{
				//링크
				$('.left').removeClass('on');
				$('.left .navi > ul > li.l1 > a.on').removeClass('on');
				$('.left .navi .sub_wrap > li.l1 > a').removeClass('on');
				$(this).parent().parent().find(' ul li').removeClass('on');
				
			}
		});
	};
	
