    /**************************************************************************
     * Event: scwin.fnNewMailAprvCnt
     * 기 능: 텔레피아 새메일 갯수, 새결재 갯수 구하는 함수
     **************************************************************************/				
	scwin.fnNewMailAprvCnt = function() {
		ajaxLib.ajax("/ep/EP10070001U/selectUserNewMailAprvCnt", {
			mode         : "asynchronous"
		   	,method      : "Post"
		   	,requestData : ""
		   	,callback    : function(result,e){
				if (result.data.mailCnt == "0") {
					spnMailCnt.addClass("hide");
				} else {
					spnMailCnt.removeClass("hide");
					spnMailCnt.setLabel( result.data.mailCnt );
				}
				
				if (result.data.aprvCnt == "0") {
					spnAprvCnt.addClass("hide");
				} else {
					spnAprvCnt.removeClass("hide");
					spnAprvCnt.setLabel( result.data.aprvCnt );	
				}
				
				var oTabwindow = tab_main.getWindow(0);
		        
		        if(typeof oTabwindow == "object" &&
		             typeof oTabwindow["scwin"] == "object" &&
		             typeof oTabwindow.scwin["fnMainTelCnt"] == "function"){
		             oTabwindow.scwin["fnMainTelCnt"](result.data.mailCnt, result.data.aprvCnt);
		        }				
			},
			errCallback : function(result,e){
			}
		});				
	};
	
    /**************************************************************************
     * Event: scwin.fnAddRemoveClass
     * 기 능: 메인, 게시판, 메일, 결재 , 일정  보이기 안보이기 제어 함수
     * Y --> 보이기  N --> 안보이기 , "" --> 제어가 필요 없을 때
     **************************************************************************/	
	scwin.fnAddRemoveClass = function(main, board, mail, aprv,schedule,todo) {
		if (main == "Y") {
			grpMainWrap.addClass('on');
		} else if (main == "N") {
			grpMainWrap.removeClass('on');
		}

		if (board == "Y") {
			grpBoardWrap.addClass('on');
		} else if (board == "N") {
			grpBoardWrap.removeClass('on');
		}
		
		if (mail == "Y") {
			grpMailWrap.addClass('on');
		} else if (mail == "N") {
			grpMailWrap.removeClass('on');
		}

		if (aprv == "Y") {
			grpAprvWrap.addClass('on');
		} else if (aprv == "N") {
			grpAprvWrap.removeClass('on');
		}
		
		if(schedule == "Y")
		{
			grpScheduleWrap.addClass('on');
		}
		else if(schedule == "N")
		{
			grpScheduleWrap.removeClass('on');
		}
		 		
		if(todo == "Y")
		{
			grpTodoWarp.addClass('on');
		}
		else if(todo == "N")
		{
			grpTodoWarp.removeClass('on');
		} 		
		
	};
	
	/**************************************************************************
     * Event: scwin.fnTodoNtfyPoll
     * 기 능: TO-DO 알림 
     **************************************************************************/	
	scwin.fnTodoNtfyPoll = function(){
	
		var requestData = {};
		
		requestData = strLib.serialize(requestData);
        
		$.ajax({
	         url         : '/ep/Main/selectTodoNtfyList'
	        ,type        : 'POST'
	        ,data        : requestData 
	        ,contentType : 'application/json'
	        ,dataType    : 'json'
	        ,beforeSend  : function(xhr,settings) {
	        	// 요청 전에 실행되는 함수입니다. 여기서 false를 반환하면 작업이 중단됩나다.				
				/*
                if(location.href.indexOf("/ux/cf/CF00050000U.xml") == -1 && WebSquare.w2xPath  != "/ux/cf/CF00050000U.xml"){
	        		if(com.isEmpty(sessionStorage.getItem("accessToken"))){
						xhr.abort();
						alert("accessToken이 없습니다.\n로그아웃 처리 합니다.");
						com.logOut();
						return false;
					}
				}
                */
			}
	        ,success: function(result) {
	        	var todoNtfyList = result.data.todoNtfyList;
	        	
	        	
	        	if(todoNtfyList.length > 0)
	        	{
					$.each(todoNtfyList,function(index, item){
						
						if(dsNtfyList.getMatchedIndex( "todoNtfyNo" , item.todoNtfyNo , true ).length > 0)
						{
							return true;
						}
						
						var rowIndex = dsNtfyList.insertRow(0);
						dsNtfyList.setCellData( rowIndex , "todoNtfyNo"     , item.todoNtfyNo     );
						dsNtfyList.setCellData( rowIndex , "todoNtfyTtl"    , item.todoNtfyTtl    );
						dsNtfyList.setCellData( rowIndex , "todoNtfyPathNm" , item.todoNtfyPathNm );
						dsNtfyList.setCellData( rowIndex , "todoNtfyBbsNm"  , item.todoNtfyBbsNm  );
						dsNtfyList.setCellData( rowIndex , "fstRegDtm"      , item.fstRegDtm      );
					});
	        	}
	        	todoNtfyBadge.setLabel(dsNtfyList.getRowCount());
	        	tbGrpNtfyDate.setLabel($w.getCurrentServerDate( "yyyy.MM.dd" ));
	        	
	        	//알림영역 GEN
	        	scwin.setGenNtfy();
	        	
	        	if(genNtfy.getLength() > 0)
	        	{
	        		if(!grpNtfy.hasClass("on"))
	        		{
		        	 	grpNtfy.addClass( "on" );
		        	 	$('.wrap').addClass('push_on');	
	        		}
	        	}
	        	else
		       	{
		       	 	grpNtfy.removeClass( "on" );
		       	 	$('.wrap').removeClass('push_on');	
		       	}
	        		        		
	        }
	        ,error:function(request,status,error){
	        	if(scwin.todoNtfyPollTimeOut != null)
	        	{
		        	clearTimeout(scwin.todoNtfyPollTimeOut);	
	        	}
	        }
	        ,timeout: 3000
	        ,complete: scwin.todoNtfyPollTimeOut = setTimeout(function() { scwin.fnTodoNtfyPoll(); }, 600000)
	  })
	};
	
	/**************************************************************************
     * Event: scwin.fnReadTodoNtfy
     * 기 능: TO-DO 알림 알림
    **************************************************************************/
	scwin.fnReadTodoNtfy = function(data){
		ajaxLib.ajax("/ep/Main/readTodoNtfy", {
			mode        : "synchronous"
		   ,method      : "Post"
		   ,requestData : { todoNtfyNo : data.todoNtfyNo }
		   ,callback    : function(result,e){
				
		   },
			errCallback : function(result,e){
			}
		});
	};
	
	/**************************************************************************
     * Event: scwin.setGenNtfy
     * 기 능: 알림역역 제너레이터 SET 
     **************************************************************************/	
	scwin.setGenNtfy = function(){
		//알림영역 GEN
       	var todoNtfyData = dsNtfyList.getAllJSON();
       	var ntfyIndex , genNtfyObj,  genCloseObj;
       	genNtfy.removeAll();
       	$.each(todoNtfyData,function(index,item){
       		ntfyIndex = genNtfy.insertChild();
       		genNtfyObj    = genNtfy.getChild( ntfyIndex , "ntfyAlink" );
       		genCloseObj   = genNtfy.getChild( ntfyIndex , "btnTodoNtfyClose" );
       		genNtfy.getChild( ntfyIndex , "tbNtfyTitle" ).setLabel(item.todoNtfyTtl);;
       		genNtfy.getChild( ntfyIndex , "tbNtfyDate"  ).setLabel(item.fstRegDtm);;
       		genNtfy.getChild( ntfyIndex , "tbNtfyBbsNm" ).setLabel(item.todoNtfyBbsNm);;
       		genNtfyObj.setUserData("ntfyData",item);
       		genCloseObj.setUserData("ntfyData",item);
       		
       	});
	}
	 
	/**************************************************************************
     * Event: scwin.ntfyAlink_onclick
     * 기 능: 알림역역 A LINK onClick 
     **************************************************************************/ 
	scwin.ntfyAlink_onclick = function(e) {
		var ntfyData = this.getUserData("ntfyData");
		
		//TODO 알림테이블 읽음처리
		scwin.fnReadTodoNtfy(ntfyData);
		
		//읽은알림 제네레이터에서 삭제
		scwin.resetGenNtfy(ntfyData);
		
		if(genNtfy.getLength() == 0){
		
       		if(grpNtfy.hasClass("on"))
       		{
	       	 	grpNtfy.removeClass( "on" );
	       	 	$('.wrap').removeClass('push_on');	
       		}
       	}
       	
       	var popOps = {
         		     url        : ntfyData.todoNtfyPathNm
         		    ,modal      : false
	                ,type       :"browser"
	               	,height     :"900"
	               	,width      :"1400"
	               	,popup_name :ntfyData.todoNtfyBbsNm
	               	,resizable  :true
	               	,scrollbars :true 
	               	,data       :{menuNm    : ntfyData.todoNtfyBbsNm}
	              };
	 	com.popup_open(popOps);
       	
	};
	
	/**************************************************************************
     * Event: scwin.btnTodoNtfyClose_onclick
     * 기 능: 알림역역 close onClick 
     **************************************************************************/
	scwin.btnTodoNtfyClose_onclick = function() {
		
		var ntfyData = this.getUserData("ntfyData");
		
		//TODO 알림테이블 읽음처리
		scwin.fnReadTodoNtfy(ntfyData);
		
		//읽은알림 제네레이터에서 삭제
		scwin.resetGenNtfy(ntfyData);
		
		if(genNtfy.getLength() == 0){
		
       		if(grpNtfy.hasClass("on"))
       		{
	       	 	grpNtfy.removeClass( "on" );
	       	 	$('.wrap').removeClass('push_on');	
       		}
       	}
		
	};
	
	/**************************************************************************
     * Event: scwin.resetGenNtfy
     * 기 능: 알림역역 재설정 
     **************************************************************************/
	scwin.resetGenNtfy = function(data){
	
		if(data == null || data.todoNtfyNo == "" ) return;
		
		var arrindex = dsNtfyList.getMatchedIndex( "todoNtfyNo" , data.todoNtfyNo , true);
		
		$.each(arrindex,function(index,item){
			dsNtfyList.removeRow(item);
		});
		
		todoNtfyBadge.setLabel(dsNtfyList.getRowCount());
		
		//알림영역 GEN
	    scwin.setGenNtfy();
	};
	
