	/**************************************************************************
	 * 함 수 명: scwin.fnTimerCount
	 * 기 능: 자동로그아웃 setInterval
	 **************************************************************************/	
	scwin.fnTimerCount = function(){
		scwin.erpTimer = setInterval(function(){
			scwin.fnTimerCountRun();
		}, 1000); 
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnTimerCountRun
	 * 기 능: 자동로그아웃 실행메서드
	 **************************************************************************/
	scwin.fnTimerCountRun = function(){
		scwin.erpTimerRun = "Y"
		scwin.erpCount--;
		
		if(scwin.erpCount == 300)
		{//시간연장
			var param = {gubun : 'E' };
			var popOps = {
						     popup_name :"시간연장"
	               		   , url        :"/ux/common/alert_timeout.xml"
	               		   , width      :"280"
	               		   , height     :"200"
	               		   , data       : param
	             	};
		 	com.popup_open(popOps);
	 		
		}
		
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnTimerReset
	 * 기 능: 자동로그아웃 타이머 초기화
	 **************************************************************************/
	scwin.fnTimerReset = function(){
		if(scwin.erpTimerRun == "Y"){
		
			clearInterval(scwin.erpTimer);
			
			scwin.erpCount = parseInt(3600);//초기값(초단위)
			scwin.fnTimerCount();
		}
	}
	
	/**************************************************************************
	 * 함 수 명: scwin.fnTimeFormat
	 * 기 능: 자동로그아웃 타이머 시간:분:초 포맷터
	 **************************************************************************/
	scwin.fnTimeFormat = function(count){
		var nHour=0;
		var nMin=0;
		var nSec=0;
		if(count>0) {
			nMin = parseInt(count/60);
			nSec = count%60;
	
			if(nMin>60) {
				nHour = parseInt(nMin/60);
				nMin = nMin%60;
			}
		} 
		if(nSec<10) nSec = "0"+nSec;
		if(nMin<10) nMin = "0"+nMin;
	
		return ""+nHour+":"+nMin+":"+nSec; 
	};
	
