	/**************************************************************************
	 * 함 수 명: scwin.tab_main_ontabclose
	 * 기 능: 탭닫기시 메뉴 메뉴정보 set
	 **************************************************************************/
	scwin.tab_main_ontabclose = function(tabID,idx) {
	
		var len = gcm.tab_main.tabArr.length;
		var key = 0;
		
		if(len == idx){
			key = idx-1;
		}else{
			key = idx;
		}
	
		// 텝버튼 클릭시 메뉴아이디 변경처리
		gcm.MENU_ID = gcm.tab_main.tabArr[key].userTabID;
        if( gcm.MENU_ID != "" ) {
            var menuDatas = $.grep(JSON.parse(gcm.MENU_LIST), function( n, i ) { if( n.menuId == gcm.MENU_ID ) return n; });
            
            if(menuDatas.length > 0)
            {
	            gcm.MENU_TITLE = menuDatas[0].menuNm;
	            gcm.MENU_URL   = menuDatas[0].scrPathNm;
            }
        }
	};
	
