    scwin.onpageload = function() {
        // 세션스토로지 재입력
        if( sessionStorage.getItem("userId") == null ) {
            scwin.fnUserInfoForSessionStorage();
        }
	 	gcm.tab_main = tab_main;

		if (gcm.khProp.environment == "local") {
			//txtServerNm.setStyle( "display" , "none" );
			//txtServerNm.setValue( "로컬" );
			txtServerNm.setStyle( "display" , "none" );
		} else if (gcm.khProp.environment == "dev") {
			txtServerNm.setValue( "개발" );
		} else if (gcm.khProp.environment == "stg") {
			txtServerNm.setValue( "테스트" );
		} else if (gcm.khProp.environment == "prd") {
			//txtServerNm.setValue( "운영" );
			txtServerNm.setStyle( "display" , "none" );
			tab_main.deleteTab(2);
		}
		
		// 2021-04-14 상단 검색 DIV 숨기기
 		grpTopSrchList.hide();
 		
 		// 2021-06-01 탭쪽 안쓰는메뉴 숨기기
 		grpBoardLi.hide();
 		grpMailLi.hide();
 		grpAprvLi.hide();
 		grpToDoLi.hide();
 		grpScdlLi.hide();
 		grpEhrmsLi.hide();
 		
 		// 2021-04-14 최상단 검색바 숨기기 
 		mainSearch.hide();
 		
 		// 2021-04-13 현장포탈 탭 숨기기
 		var hideTap = tab_main.getTabIndex( "tabs12" );
 		tab_main.hideTab( hideTap );
 		  
    	//spnUserNm.setLabel( sessionStorage.getItem("userNm") );
 		
 		scwin.goToWorkYn = sessionStorage.getItem("goToWorkYn");
 		scwin.hrUserId = sessionStorage.getItem("hrUserId");
 		
 		/*
		if (scwin.goToWorkYn == 'Y') {
			btnGoWrkChk.setValue( "출근완료" );
			btnGoWrkChk.removeClass( "unchecked" );
		} else {
			btnGoWrkChk.setValue( "출근체크" );
			btnGoWrkChk.addClass( "unchecked" );
		} */		

		scwin.userTypeGb = sessionStorage.getItem("userTypeGb"); 

		if (scwin.userTypeGb == "1" || scwin.userTypeGb == "3") {
			// 새메일 갯수, 새결재 갯수 구하기
			scwin.fnNewMailAprvCnt();
		} else if (scwin.userTypeGb == "4" || scwin.userTypeGb == "5") {
			grpBoardLi.hide();
			grpMailLi.hide();
			grpAprvLi.hide();
			grpToDoLi.hide();
			grpScdlLi.hide();
			grpEhrmsLi.hide();
		}
 		
 		if (com.isEmpty(gcm.MENU_LIST)) {
    		com.setMyMenu(gcm.CD_KEY["Menu"]);
    	}
    	
    	//메뉴 genertor set
    	//scwin.fnSetMenuGenertor();
            	
	 	//메뉴셋팅
	 	//scwin.fnSetMenu();
	 	
	 	//TO-DO 알림 호출
		scwin.fnTodoNtfyPoll();
		
	 	//자동로그아웃 
	 	scwin.fnTimerCount();
	 	  		
 		scwin.fnSelectIntUserInfo();
	 	scwin.fnMakeMagrWarpGen();
	 	
	 	scwin.fnCheckMargAuth();
	 	
	 	//버튼 권한을 관리하는 전체 조회
    	if (com.isEmpty(gcm.AUTH_INCLUDE_IDS)) {
    		com.setAuthBtnAllInfo();
    	}
        
        history.replaceState({}, null, location.pathname);
        
        //통합검색 콤보박스 text-align : center 적용
        //$("#mf_sbxTopSrchType_label").attr("style", "text-align : center"); 
	};
	
    /************************************************************************************
     *  [3] Event 함수 
