	/**************************************************************************
	 * 함 수 명: scwin.sbxTopSrchType_onchange
	 * 기 능: 상단 검색 셀렉트박스 변경 이벤트
	 **************************************************************************/
	scwin.sbxTopSrchType_onchange = function() {
		scwin.topSearchType = sbxTopSrchType.getValue();
		ibxTopSrchWrd.setValue( "" );
		scwin.topSearchText = ibxTopSrchWrd.getValue();
    	genTopSrchUl.removeAll();
    	grpTopSrchList.hide();
		
		if (scwin.topSearchType == '1') {			// 사내직원	
		} else if (scwin.topSearchType == '2') {	// 그룹직원		
		} else if (scwin.topSearchType == '6') {	// 통합검색			
		}
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.ibxTopSrchWrd_onkeyup
	 * 기 능: 상단 검색어 입력시 자동 완성 데이터 가져오기 그리고 엔터 치면 검색하러 가기
	 **************************************************************************/	
	scwin.ibxTopSrchWrd_onkeyup = function(e) {
		scwin.topSearchType = sbxTopSrchType.getValue();
		scwin.topSearchText = ibxTopSrchWrd.getValue();
		var prevObj = "";
		var nextObj = "";
		
		if (e.key == "Enter") {
			grpTopSrchList.hide();
			if (scwin.topSearchType == '1' || scwin.topSearchType == '2') {	// 사내직원, 그룹직원
				scwin.fnUserSearch();
			} else if (scwin.topSearchType == '6' || scwin.topSearchType == '3' || scwin.topSearchType == '4' || scwin.topSearchType == '7') {	//통합검색 , 결재 , 게시판
				scwin.fnTotalSearch();
			}	
		} else if (e.key == "ArrowDown") {
			if ((scwin.cursorPos+1) < scwin.totalCount) { 
				if (scwin.cursorPos > -1 ) { 
					prevObj = genTopSrchUl.getChild( scwin.cursorPos , "grpTopSrchA" ); 
					$w.getComponentById(prevObj.getUserData('srchLiId')).setStyle("backgroundColor", "#ffffff");
				}
				
				if (scwin.cursorPos < 0 ) { 
					scwin.cursorPos = 0;
				} else {
					scwin.cursorPos = scwin.cursorPos + 1;
				}	
				nextObj = genTopSrchUl.getChild( scwin.cursorPos , "grpTopSrchA" );
				$w.getComponentById(nextObj.getUserData('srchLiId')).setStyle("backgroundColor", "#eeeeee"); 
				ibxTopSrchWrd.setValue(nextObj.getUserData('keyword'));
			}
		} else if (e.key == "ArrowUp") {
			if (scwin.cursorPos > -1 ) { 
				prevObj = genTopSrchUl.getChild( scwin.cursorPos , "grpTopSrchA" );
				$w.getComponentById(prevObj.getUserData('srchLiId')).setStyle("backgroundColor", "#ffffff");
			}
			
			if (scwin.cursorPos < 0 ) { 
				scwin.cursorPos = -1;
			} else {
				scwin.cursorPos = scwin.cursorPos - 1;
			}
			nextObj = genTopSrchUl.getChild( scwin.cursorPos , "grpTopSrchA" );
			$w.getComponentById(nextObj.getUserData('srchLiId')).setStyle("backgroundColor", "#eeeeee"); 
			ibxTopSrchWrd.setValue(nextObj.getUserData('keyword'));
		} else {
			if (scwin.topSearchType == '1') {			// 사내직원	
				//내부사용자 정보 가져오기
				//dsSearchParam.set( "keySearch" , scwin.topSearchText );
		    	scwin.fnSrchWrd();	
			} else if (scwin.topSearchType == '2') {	// 그룹직원	
			
			} else if (scwin.topSearchType == '6') {	// 통합검색
				scwin.fnSrchWrd();
			}    	
    	}			
	};

	/**************************************************************************
	 * 함 수 명: scwin.ibxTopSrchWrd_onclick
	 * 기 능: 상단 검색어 클릭 시
	 **************************************************************************/
	scwin.ibxTopSrchWrd_onclick = function(e) {
		scwin.topSearchType = sbxTopSrchType.getValue();
		scwin.topSearchText = ibxTopSrchWrd.getValue();
		
		if (scwin.topSearchType == '1' || scwin.topSearchType == '6') {			// 사내직원, 통합검색	
			if (scwin.topSearchText != "") {
	    		scwin.fnSrchWrd();	
	    	}
		}     	
	};
	
 	/**************************************************************************
	 * 함 수 명: scwin.tbxHkeyword_onclick
	 * 기 능: 자동 검색결과 클릭 이벤트
	 **************************************************************************/  	
	scwin.grpTopSrchA_onclick = function(e) {
		ibxTopSrchWrd.setValue(this.getUserData("keyword"));
		grpTopSrchList.hide();		
	};
		
 	/**************************************************************************
	 * 함 수 명: scwin.btnWrdSrch_onclick
	 * 기 능: 첫단어 또는 끝단어클릭 이벤트
	 **************************************************************************/ 	
	scwin.btnWrdSrch_onclick = function() {
		if (scwin.topSearchType == '6') {	// 통합검색
			if (btnWrdSrch.getValue() == "첫단어 더보기") {
				btnWrdSrch.setValue("끝단어 더보기");
				scwin.convert = "fw";
			} else {
				btnWrdSrch.setValue("첫단어 더보기");
				scwin.convert = "rw";
			}
			scwin.fnSrchWrd();
		} else {
			alert('통합 검색일 때만 사용 할 수 있습니다.');
			return;
		}
	};
	
 	/**************************************************************************
	 * 함 수 명: scwin.btnAuto_onclick
	 * 기 능: 자동완성 끄기
	 **************************************************************************/ 		
	scwin.btnAuto_onclick = function() {
		grpTopSrchList.hide();
	};
		
 	/**************************************************************************
	 * 함 수 명: scwin.btnTopSrch_onclick
	 * 기 능: 상단 검색 버튼 이벤트
	 **************************************************************************/    
	scwin.btnTopSrch_onclick = function() {
		scwin.topSearchType = sbxTopSrchType.getValue();
		scwin.topSearchText = ibxTopSrchWrd.getValue();
		grpTopSrchList.hide();
		
		if (scwin.topSearchType == '1') {			// 사내직원
			scwin.fnUserSearch();
		} else if (scwin.topSearchType == '2') {	// 그룹직원
			scwin.fnUserSearch();
		} else if (scwin.topSearchType == '6' || scwin.topSearchType == '3' || scwin.topSearchType == '4' || scwin.topSearchType == '7') {	//통합검색
			scwin.fnTotalSearch();
		}		
	}; 
			
