	/**************************************************************************
	 * 함 수 명: scwin.tabFrm_ontabclick
	 * 기 능: 탭 Click
	 **************************************************************************/		
	scwin.tabFrm_ontabclick = function(tabID,idx) {
		
		com.autoHeight();
		
    	if (scwin.userTypeGb == "1" || scwin.userTypeGb == "3") {
		    scwin.fnNewMailAprvCnt();
		}
	    scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
		grpBoardLi.removeClass('on');
		grpMailLi.removeClass('on');
		grpAprvLi.removeClass('on');
		grpToDoLi.removeClass('on');
		grpScdlLi.removeClass('on');
		scwin.fnRemoveWrapQucikMenu();
		
		//TODO 값세팅 값제거
		com.setParam("MNU_EP_TODO");
		
        // 텝버튼 클릭시 메뉴아이디 변경처리
		gcm.MENU_ID = gcm.tab_main.tabArr[idx].userTabID;
        if( gcm.MENU_ID != "" ) {
            var menuDatas = $.grep(JSON.parse(gcm.MENU_LIST), function( n, i ) { if( n.menuId == gcm.MENU_ID ) return n; });
            
            if(menuDatas.length > 0)
            {
	            gcm.MENU_TITLE = menuDatas[0].menuNm;
	            gcm.MENU_URL   = menuDatas[0].scrPathNm;
            }
        }

		var _comp = gcm.tab_main.childControlList[idx];
		if(_comp.getSrc() == ""){
			_comp.setSrc(_menuSrcArr[idx]);
		}  
		
		var oTabwindow = tab_main.getWindow(idx);
        
        if(typeof oTabwindow == "object" &&
             typeof oTabwindow["scwin"] == "object" &&
             typeof oTabwindow.scwin["fnRefresh"] == "function"){
             //해당페이지에 scwin.fnRefresh function 생성
             //sample ==>/sono/src/main/webapp/WEB-INF/ux/cf/CF00000001U.xml
             //sample ==>/sono/src/main/webapp/WEB-INF/ux/cf/CF00000002U.xml
             oTabwindow.scwin["fnRefresh"]();
        }		
		
		
	};
		
	/**************************************************************************
	 * 함 수 명: scwin.tabFrm_ontabclose
	 * 기 능: 탭 Close
	 **************************************************************************/
	scwin.tabFrm_ontabclose = function(tabID,idx) {
		// 화면 클로즈 시 파라미터 클리어
        var menuIds = gcm.tab_main.tabArr[idx].userTabID;
		com.setParam(menuIds);
	};
	
