	/**************************************************************************
	 * 함 수 명: scwin.btnMenu1_onclick
	 * 기 능: 메뉴 버튼에서 1단계 클릭
	 **************************************************************************/	
	scwin.btnMenu1_onclick = function() {
		var menuId = this.getUserData("firstLvMenuId");
		
    	//메세지 정보 가져오기
    	if (com.isEmpty(gcm.MSG_INFO_LIST)) {
    		com.setMsgInfo(gcm.CD_KEY["Msg"]);
    	}
    			
		if (menuId == "MNU_EM_" || menuId == "MNU_FD_") {
			//재무전표 귀속부서 조회
			if (com.isEmpty(gcm.EM_RVT_DEPT_LIST)) {
				com.setCcrtDeptInfo();
			}
	 	}
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.btnMenu3_onclick
	 * 기 능: 메뉴 버튼에서 3단계 클릭
	 **************************************************************************/	
	scwin.btnMenu3_onclick = function() {
		var title = this.getValue();
		var menuId = this.getUserData("winId");
		var url = this.getUserData("tabUrl");				
 
		if(menuId=="MNU_MG_203090"){  //계정과목별명세서
		        var userId =  sessionStorage.getItem("userId");
		        var nowDate = new Date();
		        var ppupOptNm = "top=1, left=1, width=1400,height=800,resizable=yes,toolbar=no,scrollbars=yes,menubar=no,status=yes, location=no";
		        var targetNm = userId+"_"+$w.getFormattedDate( nowDate , "yyyyMMddHHmmss" ) + nowDate.getMilliseconds() ;
		        var ridNm = "REP73B43CEE3E9A408CA1AACD3A97791B5E";
 
                var $form = $("<form></form>");
                window.open('about:blank', targetNm, ppupOptNm);
                $form.attr("target", targetNm);
                $form.attr("action", "http://bi.sdcit.co.kr/sso_login.jsp");
                $form.attr("method", "post");
                $form.attr("accept-language", "ko")
                $form.appendTo('body');

                $input = $("<input></input>");
                $input.attr({type:"hidden",  name:"UID",  id:"UID",  value:userId });
                $input.appendTo($form);

                $input = $("<input></input>");
                $input.attr({type:"hidden",  name:"RID",  id:"RID",  value: ridNm});
                $input.appendTo($form);

                $form.submit();		   
		}else{
			scwin.tabTitle = title;
			scwin.winId = menuId;
			scwin.tabUrl = url;
			
			gcm.MENU_TITLE = title;
			gcm.MENU_ID = menuId;
			gcm.MENU_URL = url;
			
			scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
			scwin.fnRemoveWrapQucikMenu();
			
			com.setAuthInfo(menuId);
			
	        scwin.fnOpenTabEvt();	
        }
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.autoMenuSearch_onitemclick
	 * 기 능: 메뉴 검색어 입력하거나 키보드 또는 마우스 조작을 통해 변경된 경우
	 **************************************************************************/	
	scwin.autoMenuSearch_onviewchange = function(info) {
		var jsn = dsMenuSearchList.getRowJSON( info.newSelectedIndex - 1);
		
		scwin.tabTitle = jsn.menuNm;
		scwin.winId = jsn.menuId;
		scwin.tabUrl = jsn.scrPathNm;	
			
		gcm.MENU_ID = jsn.menuId;
		gcm.MENU_TITLE = jsn.menuNm;
		gcm.MENU_URL = jsn.scrPathNm;
		
		scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
		scwin.fnRemoveWrapQucikMenu();
		
		com.setAuthInfo(jsn.menuId);
		
		scwin.fnOpenTabEvt();
		
		autoMenuSearch.setValue(-1);
		
	};
				
	/**************************************************************************
	* 함 수 명: scwin.grpMyMenuWarp_onclick
	* 기 능: 마이메뉴 클릭시
	**************************************************************************/		
    scwin.grpMyMenuWarp_onclick = function(e) {
		if (com.isEmpty(gcm.EM_RVT_DEPT_LIST)) {
			com.setCcrtDeptInfo();
		}    
		if(!grpMyMenuL1.hasClass('on'))
		{
			//마이메뉴
	    	scwin.fnSelectMyMenuInfo();
	    	
	    	//메뉴셋팅
	    	scwin.fnMyMenuSetEvent();
	    	
		 	//마이메뉴 드래그앤드롭
		 	scwin.fnMyMenuDragAndDropEvent();
		
	    	//최근 메뉴사용이력 5건
	    	scwin.fnSelectMenuHistTop5();
		}	
			
	};
	
	/**************************************************************************
	* 함 수 명: scwin.grpMenuWarp_onclick
	* 기 능: 메뉴 클릭시
	**************************************************************************/
	scwin.grpMenuWarp_onclick = function(e) {
		//타이머 초기화
		scwin.fnTimerReset();
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.myMenuNmEdit_onclick
	 * 기 능: 마이메뉴 폴더명 편집
	 **************************************************************************/
	scwin.myMenuNmEdit_onclick = function() {
		var objData = this.getUserData("myMenuData");
		var label = this.getUserData("myMenuLabel");
		var input = this.getUserData("myMenuInput");
		
		$("#"+genMyMenu1.id).find(" > li").each(function(){
			var id =  $(this).attr("id");
			
			var iptMyMenu1Id = $("#"+id).find("> input[id $= 'iptMyMenu1']").attr("id");
			var btnMyMenu1Id = $("#"+id).find("> a[id $= 'btnMyMenu1']").attr("id");
			
			if(input.id == iptMyMenu1Id)
			{
				if($("#"+label.id).is(":visible") == false )
				{
					$("#"+input.id).hide(); 
					$("#"+label.id).show();
				}
				else
				{
					$("#"+label.id).hide();
					$("#"+input.id).show();
				}
			}
			else
			{
				$("#"+iptMyMenu1Id).hide();
				$("#"+btnMyMenu1Id).show();
			}
		});
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.myMenuDelete_onclick
	 * 기 능: 마이메뉴 삭제버튼
	 **************************************************************************/
	scwin.myMenuDelete_onclick = function() {
	
		if(!confirm("삭제하시겠습니까?")) return; 
		
		var objData = this.getUserData("myMenuData");

		//메뉴삭제		
	 	scwin.fnDeleteMyMenu(objData)
	 	
	 	//메뉴셋팅
	 	scwin.fnMyMenuSetEvent();
	 	
	 	//메뉴 드롭앤드롭
	 	scwin.fnMyMenuDragAndDropEvent();
	 	
	 	//navigator 즐겨찾기 on class 제거
	 	scwin.fnRemoveBookMakeCalss();
	 	
	 	//최근 메뉴사용이력 5건
    	scwin.fnSelectMenuHistTop5();
	 	
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.iptMyMenu1_onkeyup
	 * 기 능: 마이메뉴 폴더명 input 엔터키
	 **************************************************************************/
	scwin.iptMyMenu1_onkeyup = function(e) {
		var objData = this.getUserData("myMenuData");
		var label = this.getUserData("myMenuLabel");
		var input = this.getUserData("myMenuInput");
		
		//엔터키 onKeyup
		if (e.keyCode == 13) {
			
			//입력받은 메뉴명과 기존 메뉴명이 틀릴시
			if(objData.menuNm != $("#"+input.id).val())
			{
			    $("#"+label.id).text($("#"+input.id).val());
				objData.menuNm = $("#"+input.id).val();
				objData.menuNmNavi = $("#"+input.id).val();
				objData.bmkGrpNm = $("#"+input.id).val();
				
				//변경된 데이터 useData 에 set
			    this.setUserData("myMenuData",objData); 
				label.setUserData("myMenuData",objData);
				
				//메뉴명 변경
				scwin.fnUpdateMyMenu(objData);
				
				//메뉴셋팅
	 			scwin.fnMyMenuSetEvent();
	 			
	 			//메뉴 드롭앤드롭
	 			scwin.fnMyMenuDragAndDropEvent();
				
				//input 창이 활성화 되어 있으면 input hide , 라벨 show
				if($("#"+input.id).is(":visible"))
				{
					$("#"+input.id).hide();
					$("#"+label.id).show();
				} 
			}
        }
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.myMenuChildDelete_onclick
	 * 기 능: 마이메뉴 자식메뉴 삭제버튼
