	/**************************************************************************
	 * 함 수 명: scwin.btnMyMenu2_onclick
	 * 기 능: 마이메뉴에서 메뉴클릭시 
	**************************************************************************/
	scwin.btnMyMenu2_onclick = function() {
		var obj = this.getUserData("myMenuData");
		
		scwin.tabTitle = obj.menuNm;;
		scwin.winId    = obj.menuId;
		scwin.tabUrl   = obj.menuPathNm;
		
		gcm.MENU_ID    = obj.menuId;
		gcm.MENU_TITLE = obj.menuNm;
		gcm.MENU_URL   = obj.menuPathNm;
		
		scwin.fnAddRemoveClass("N", "N", "N", "N","N","N");
		scwin.fnRemoveWrapQucikMenu();
		
		com.setAuthInfo(obj.menuId);
		
		scwin.fnOpenTabEvt();
	};
	
