	/**************************************************************************
     * Event: scwin.btnTacMainAllClose_onclick
     * 기 능: 탭 일괄닫기
    **************************************************************************/
	scwin.btnTacMainAllClose_onclick = function() {
		//tab_main.deleteAllTabs();
		if (scwin.userTypeGb == "1" || scwin.userTypeGb == "3") {
			scwin.fnNewMailAprvCnt();
		}
		$("li[id ^='mf_tab_main_tab']").each(function(cnt,item){
		    var id    = $(this).attr("id");
		    var index = $(this).attr("index");
		    
		    if(index > 3) 
		    {
			    tab_main.deleteTab(3);
		    }
		    
		});
		
	};
	
