    **************************************************************************/
	scwin.genQuickWrapSpan_onclick = function(e) {
	    var gubun = this.getUserData("gubun");
	    
	    if("link" == gubun)
	    {// 링크 처리
	    	var quickMenuData = this.getUserData("quickMenuData");
	    	
	    	com.quickMenuPopup(quickMenuData);
	    }
	    else if("quickMore" == gubun)
	    {//
	    	var wfQuick = wfm_quick.getWindow();
		    wfQuick.scwin["fnWarpQuickArea"]();
	    }
	};
	
	/**************************************************************************
	* Event: scwin.grpEvrnSet_onclick
	* 기 능: 텔레피아 환경설정
	**************************************************************************/
	scwin.grpEvrnSet_onclick = function(e) {
		ajaxLib.ajax("/ep/Main/selectEvrnInfo", {
			mode        : "synchronous"
		   ,method      : "Post"
		   ,requestData : ""
		   ,callback    : function(result,e){
		       var teleplaAuthInfo = result.data.teleplaAuthInfoXml;
		       var url             = result.data.url;
		       
		       var teleplaSettingPop = window.open('about:blank', "teleplaSetting", "top=1, left=1, width=1200,height=650,resizable=yes,toolbar=no,scrollbars=yes,menubar=no,status=yes, location=no");
		       
		       var $form = $("<form></form>");
		       
		       $form.attr("target", "teleplaSetting");
   			   $form.attr("action", url);
	           $form.attr("method", "post");
	           $form.attr("accept-language", "ko")
	           $form.appendTo('body');
	        	
               $input = $("<input></input>");
               $input.attr({type:"hidden",  name:"Message",  id:"Message",  value:teleplaAuthInfo});
	           $input.appendTo($form);
	        	
			   $input = $("<input></input>");
               $input.attr({type:"hidden",  name:"isTelepiaYN",  id:"isTelepiaYN",  value:"true"});
	           $input.appendTo($form);	  
   				
	           $form.submit();
			},
			errCallback : function(result,e){
			}
		});
	};
	
