	/**************************************************************************
	 * 함 수 명: scwin.btnAllMenu_onclick
	 * 기 능: 전체메뉴보기 클릭
	 **************************************************************************/		
	scwin.btnAllMenu_onclick = function() {
	    ajaxLib.ajax("/ep/Main/selectMenuTotalInfo", {
	        mode: "synchronous",
	        method: "POST",
	        requestData: '',
	        callback: function(result,e){
			    var menuAllObj = result.data.menuAllInfo;
			    if (menuAllObj == null) {
				    com.showMessage('CFW0010','등록된 전체메뉴보기가 없습니다.');
				    return;
			    }
			    
				var fileDownloadUrl = "/cf/CF00350000P/downloadMAVPDF?aflp=" + menuAllObj.aflp;
				var popOps = {
				               url        :"/ux/common/pdfView.xml"
				             , type       :"browser"
				             , modal      :false
				             , height     :"900"
				             , width      :"1000"
				             , popup_name :"전체메뉴보기"
				             , resizable  :true
				             , data       :{fileDownloadUrl:fileDownloadUrl}
				};
				com.popup_open(popOps);			    
			},
	        errCallback: function(result,e){
		}
	    });	
	    				
	};
			
	/**************************************************************************
	 *  [5] 사용자정의 함수
	 **************************************************************************/
	/**************************************************************************
	 * 함 수 명: scwin.fnOpenTabEvt
     * 기 능: 텝 오픈 이벤트
	 **************************************************************************/
	// 탭 호출
	scwin.fnOpenTabEvt = function() {
		if (scwin.userTypeGb == "1" || scwin.userTypeGb == "3") {
			scwin.fnNewMailAprvCnt();
		}
		//TODO 값세팅 값제거
		com.setParam("MNU_EP_TODO");
		com._openTab(scwin.tabTitle, scwin.winId, scwin.tabUrl);	
	};		
	
	/**************************************************************************
	 * 함 수 명: scwin.fnSrchWrd
	 * 기 능: 상단 검색창에 검색어 값 구하는 이벤트
	 **************************************************************************/		 
	scwin.fnSrchWrd = function(val,keyCode) {
		var searchText = scwin.topSearchText.trim();
		var genTopSrchIdx, genTopSrchLi, genTopSrchObj, genTopSrchAObj, grpTopSrchObj, genJbPosObj, genCpObj, genTeamObj;
		var searchPattern = "";	
		scwin.totalCount = 0;
						
		if (scwin.topSearchType == '1') {					// 사내직원
			dsUserSearchList.removeColumnFilterAll();
	        scwin.cursorPos = -1;
       		genTopSrchUl.removeAll();
       		grpTopSrchList.show();
 
       		if (scwin.topSearchText == "") {
				genTopSrchIdx = genTopSrchUl.insertChild();
				genTopSrchObj = genTopSrchUl.getChild( genTopSrchIdx , "tbxHkeyword" );
				genJbPosObj  = genTopSrchUl.getChild( genTopSrchIdx , "tbxJbPos" );
				genCpObj      = genTopSrchUl.getChild( genTopSrchIdx , "tbxCp" );
				genTeamObj    = genTopSrchUl.getChild( genTopSrchIdx , "tbxTeam" );
				genTopSrchObj.setValue("해당 단어로 시작하는 검색어가 없습니다.");	
				genJbPosObj.hide();
				genCpObj.hide();
				genTeamObj.hide();
				genTopSrchObj.removeClass('name');
				return;       			
       		} else {
	        	searchPattern = new RegExp(searchText, 'gi');
	        	dsUserSearchList.setColumnFilter( {type:'regExp', colIndex:'userInfo', key:searchPattern, condition:'and'});
	        }	
	        	
	        filterQbj = dsUserSearchList.getAllFilteredJSON();
	        scwin.totalCount = filterQbj.length;
	        
	        $.each(filterQbj, function(i, result) {
				genTopSrchIdx = genTopSrchUl.insertChild();
				genTopSrchLi  = genTopSrchUl.getChild( genTopSrchIdx , "grpTopSrchLi" );
				genTopSrchAObj= genTopSrchUl.getChild( genTopSrchIdx , "grpTopSrchA" );
				genTopSrchObj = genTopSrchUl.getChild( genTopSrchIdx , "tbxHkeyword" );
				genJbPosObj  = genTopSrchUl.getChild( genTopSrchIdx , "tbxJbPos" );
				genCpObj      = genTopSrchUl.getChild( genTopSrchIdx , "tbxCp" );
				genTeamObj    = genTopSrchUl.getChild( genTopSrchIdx , "tbxTeam" );

				searchPattern = new RegExp(searchText || '', 'gi');
				var userNm = result.userNm;
				var jbposGbNm = result.dsgnNm;
				var incoTelNo = result.incoTelNo;
				var deptNm = result.deptNm;
				var userMatch = userNm.match(searchPattern);
				var jbPoMatch = jbposGbNm.match(searchPattern);
				var teleMatch = incoTelNo.match(searchPattern);
				var deptMatch = deptNm.match(searchPattern);
				
				if (userNm.match(searchPattern) == null) {
					userNm = userNm ;
				} else {
					userNm = userNm.replace(searchPattern, '<font color=#ff7979>'+ userNm.match(searchPattern)[0] +'</font>') ;
				}
				if (jbposGbNm.match(searchPattern) == null) {
					jbposGbNm = jbposGbNm ;
				} else {				
					jbposGbNm = jbposGbNm.replace(searchPattern, '<font color=#ff7979>'+ jbposGbNm.match(searchPattern)[0] +'</font>');
				}					
				if (incoTelNo.match(searchPattern) == null) {
					incoTelNo = incoTelNo ;
				} else {				
					incoTelNo = incoTelNo.replace(searchPattern, '<font color=#ff7979>'+ incoTelNo.match(searchPattern)[0] +'</font>') ;
				}					
				if (deptNm.match(searchPattern) == null) {
					deptNm = deptNm ;
				} else {				
					deptNm = deptNm.replace(searchPattern, '<font color=#ff7979>'+ deptNm.match(searchPattern)[0] +'</font>');
				}
					
				var tbxHkeyword = "<font style='font-size:13px'>" + userNm + jbposGbNm + incoTelNo + deptNm + "</font>";
				
				genTopSrchAObj.setUserData("keyword", result.userNm);	
				genTopSrchAObj.setUserData("srchLiId", genTopSrchLi.id);					
				genTopSrchObj.setValue(userNm);
				genJbPosObj.setValue(jbposGbNm);
				genCpObj.setValue(incoTelNo);
				genTeamObj.setValue(deptNm);
				//genTopSrchObj.setUserData("keyword", result.userNm);
				//genTopSrchObj.setUserData("srchLiId", genTopSrchLi.id);
				//genJbPosObj.setUserData("keyword", result.userNm);
				//genCpObj.setUserData("keyword", result.userNm);
				//genTeamObj.setUserData("keyword", result.userNm);
	        });
	        
			if (scwin.totalCount == 0) {
				genTopSrchIdx = genTopSrchUl.insertChild();
				genTopSrchObj = genTopSrchUl.getChild( genTopSrchIdx , "tbxHkeyword" );
				genJbPosObj   = genTopSrchUl.getChild( genTopSrchIdx , "tbxJbPos" );
				genCpObj      = genTopSrchUl.getChild( genTopSrchIdx , "tbxCp" );
				genTeamObj    = genTopSrchUl.getChild( genTopSrchIdx , "tbxTeam" );				
				genTopSrchObj.setValue("해당 단어로 시작하는 검색어가 없습니다.");	
				genJbPosObj.hide();
				genCpObj.hide();
				genTeamObj.hide();
				genTopSrchObj.removeClass('name');
			}
			searchPattern = "";
			    
		} else if (scwin.topSearchType == '2') {			// 그룹직원
		} else if (scwin.topSearchType == '6') {			// 통합검색
			dsSearchParam.set("convert", scwin.convert);
			dsSearchParam.set("target", "");
			dsSearchParam.set("charset", "utf-8");
			dsSearchParam.set("query", scwin.topSearchText);
			dsSearchParam.set("datatype", "json");
			
	        ajaxLib.ajax("/ep/EP10010002P/selectAutoCmplSearch", {
	            mode: "synchronous",
	            method: "POST",
	            requestData: { param : dsSearchParam.getJSON() } ,
				callback : function(result,e) {
				
					scwin.cursorPos = -1;
					
					if (dsTotalSearchList.getTotalRow() > 0) {
						dsTotalSearchList.removeAll();
					}
					
					obj = JSON.parse(result.data.result);
					
					genTopSrchUl.removeAll();
					grpTopSrchList.show();
					
					$.each(obj.result, function(i, result) {
						var itemTotalCount = parseInt(result.totalcount);
						if (itemTotalCount > 0) {
							// 통합검색 데이터 콜렉션 셋팅
							$.each(result.items, function(num,item){
								
								var cntColor = "";
								var cntType = "";
								
								if (item.type >= 0 && item.type <= 4) {
									cntColor = "#989898";
								} else {
									cntColor = "#CC6633";
								}
															
								if (item.type == 0 || item.type == 5) {
									cntType = "<font style='font-size:11px;font-family:돋움;color:"+cntColor+"'>사전</font>";
								} else if(item.type == 1 || item.type == 6) {
									cntType = "<font style='font-size:11px;font-family:돋움;color:"+cntColor+"'>일반</font>"; //색인
								} else if(item.type == 2 || item.type == 7) {
									cntType = "<font style='font-size:11px;font-family:돋움;color:"+cntColor+"'>인기</font>";
								} else if(item.type == 3 || item.type == 8) {
									cntType = "<font style='font-size:11px;font-family:돋움;color:"+cntColor+"'>테마</font>";
								} else if(item.type == 4 || item.type == 9) {
									cntType = "<font style='font-size:11px;font-family:돋움;color:"+cntColor+"'>추천</font>";
								} else {
									cntType = "";
								}		
				
								genTopSrchIdx = genTopSrchUl.insertChild();
								genTopSrchLi  = genTopSrchUl.getChild( genTopSrchIdx , "grpTopSrchLi" );
								genTopSrchAObj= genTopSrchUl.getChild( genTopSrchIdx , "grpTopSrchA" );
								genTopSrchObj = genTopSrchUl.getChild( genTopSrchIdx , "tbxHkeyword" );
								genJbPosObj   = genTopSrchUl.getChild( genTopSrchIdx , "tbxJbPos" );
								genCpObj      = genTopSrchUl.getChild( genTopSrchIdx , "tbxCp" );
								genTeamObj    = genTopSrchUl.getChild( genTopSrchIdx , "tbxTeam" );
								
								genTopSrchAObj.setUserData("keyword", item.keyword);	
								genTopSrchAObj.setUserData("srchLiId", genTopSrchLi.id);	
								genTopSrchObj.setValue("&nbsp;&nbsp;" + cntType + "&nbsp;&nbsp;" + item.hkeyword);
								genJbPosObj.hide();
								genCpObj.hide();
								genTeamObj.hide();
								//genTopSrchObj.setUserData("keyword", item.keyword);
								//genTopSrchObj.setUserData("srchLiId", genTopSrchLi.id);								
								//genJbPosObj.setUserData("keyword", item.keyword);
								//genCpObj.setUserData("keyword", item.keyword);
								//genTeamObj.setUserData("keyword", item.keyword);
								genTopSrchObj.removeClass('name');								
							});
						}
						scwin.totalCount = scwin.totalCount + itemTotalCount;
					});
					
					if (scwin.totalCount == 0) {
						genTopSrchIdx = genTopSrchUl.insertChild();
						genTopSrchObj = genTopSrchUl.getChild( genTopSrchIdx , "tbxHkeyword" );
						genJbPosObj  = genTopSrchUl.getChild( genTopSrchIdx , "tbxJbPos" );
						genCpObj      = genTopSrchUl.getChild( genTopSrchIdx , "tbxCp" );
						genTeamObj    = genTopSrchUl.getChild( genTopSrchIdx , "tbxTeam" );
						genTopSrchObj.setValue("해당 단어로 시작하는 검색어가 없습니다.");				
						
						genJbPosObj.hide();
						genCpObj.hide();
						genTeamObj.hide();
						genTopSrchObj.removeClass('name');														
					}
				},
	            errCallback: function(result,e) { }
	        });	
        }
	} ;
		 
	/**************************************************************************
	 * 함 수 명: scwin.fnUserSearch
	 * 기 능: 상단 검색창에서 사내직원, 그룹직원 선택 후 텔레피아와 연동 시키는 함수
	 **************************************************************************/	
	scwin.fnUserSearch = function() {
		ajaxLib.ajax("/ep/EP10010001P/selectUserSearchXml", {
            mode: "synchronous",
            method: "POST",
            requestData: dsSearchParam.getJSON(),
			callback : function(result,e) {
				scwin.cursorPos = -1;

				if (scwin.topSearchType == '1') {			// 사내 직원
					scwin.searchCompanyType = "N";
				} else if (scwin.topSearchType == '2') {	// 그룹 직원
					scwin.searchCompanyType = "Y";
				}

	            var left = (screen.width / 2 - 350);
	            var top = (screen.height / 2 - 300);
				var url = "";
				var newin = window.open('about:blank', 'userSearch',  'toolbar=no,location=no,directories=no,status=yes,menubar=no,scrollbars=yes,resizable=yes,width=950,height=580,left=' + left + ',top=' + top);
				//newin.focus(); //새창을 띄울때 항상 위
                url = result.data.userSearchAction + "?searchText=" + encodeURI(scwin.topSearchText) + "&searchCompanyType=" + scwin.searchCompanyType;
                //window.open(url, 'userSearch',  'toolbar=no,location=no,directories=no,status=yes,menubar=no,scrollbars=yes,resizable=yes,width=950,height=580,left=' + left + ',top=' + top);
                                
	   			var $form = $("<form></form>");   				
	   			$form.attr("target", "userSearch");
	        	$form.attr("action", url);
	        	$form.attr("method", "post")
	        	$form.attr("accept-language", "ko")
	        	$form.appendTo('body');	   
            	var $input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"Message",  id:"Message",  value:result.data.userSearchXml});
	        	$input.appendTo($form);
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"isTelepiaYN",  id:"isTelepiaYN",  value:"true"});
	        	$input.appendTo($form);	  		        	     			        
	        	$form.submit();   
	        	
	        	setTimeout(function() { ibxTopSrchWrd.setValue(""); }, 10);                 					
			},
            errCallback: function(result,e) { }
        });	
	}
	
	/**************************************************************************
	 * 함 수 명: scwin.fnTotalSearch
	 * 기 능: 상단 검색창에서 통합검색 선택 후 텔레피아와 연동 시키는 함수
	 **************************************************************************/		
	scwin.fnTotalSearch = function() {	 
		if (scwin.topSearchType == '3') {	//결재 선택 일 때 
			scwin.topCollection = 'approval';
		} else if (scwin.topSearchType == '4') {	//게시판 선택 일 때
			scwin.topCollection = 'bbs'; 
		} else if (scwin.topSearchType == '5') {	//네이버 선택 일 때
			scwin.topCollection = 'naver'; 
		} else if (scwin.topSearchType == '6') {	//통합검색 선택 일 때 
			scwin.topCollection = 'ALL';
		} else if (scwin.topSearchType == '7') {	//임직원 
			scwin.topCollection = 'employee';
		}
					
					
        ajaxLib.ajax("/ep/EP10010002P/selectTotalSearchXml", {
            mode: "synchronous",
            method: "POST",
            requestData: dsSearchParam.getJSON(),
			callback : function(result,e) {
				scwin.cursorPos = -1;
				//ibxTopSrchWrd.setValue("");
				
	            var left = (screen.width / 2 - 350);
	            var top = (screen.height / 2 - 300);
				var url = "";
				var newin = window.open('about:blank', 'totalSearch',  'width=1270, height=700,left=1, top=1, scrollbars=yes, resizable=yes,left=' + left + ',top=' + top);
				newin.focus(); //새창을 띄울때 항상 위
                
                url = result.data.totalSearchAction + "&query=" + encodeURI(scwin.topSearchText) + "&collection=" + scwin.topCollection;
	   			var $form = $("<form></form>");   				
	   			$form.attr("target", "totalSearch");
	        	$form.attr("action", url);
	        	$form.attr("method", "post")
	        	$form.attr("accept-language", "ko")
	        	$form.appendTo('body');
            	var $input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"Message",  id:"Message",  value:result.data.totalSearchXml});
	        	$input.appendTo($form);
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"isTelepiaYN",  id:"isTelepiaYN",  value:"true"});
	        	$input.appendTo($form);	  
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"ctDeptId",  id:"ctDeptId",  value:''});
	        	$input.appendTo($form);	
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"ctCommunityId",  id:"ctCommunityId",  value:''});
	        	$input.appendTo($form);	
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"isExecutive",  id:"isExecutive",  value:'N'});
	        	$input.appendTo($form);		        			        			        	
	        	$form.submit();                    	
	        	
	        	setTimeout(function() { ibxTopSrchWrd.setValue(""); }, 10);				
			},
            errCallback: function(result,e) { }
        });		
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnSetMenuHistGenertor
	 * 기 능: 마이메뉴 history 부분 set
	 **************************************************************************/
	scwin.fnSetMenuHistGenertor = function(objList){
	
		if(objList == null || objList.length == 0) return;
		
		var genMenuHistIndex , genBtnMenuHist , genBtnFavorites;
		
		genMenuHist.removeAll();
		$.each(objList,function(index,item){
			genMenuHistIndex  = genMenuHist.insertChild();
			genBtnFavorites   = genMenuHist.getChild( genMenuHistIndex , "btnFavorites" );
			genBtnFavorites.setUserData("menuData",item);
			
			//마이메뉴 에 있으면 on class 추가 
			if(item.chkBmkMenuYn == "Y")
			{
				genBtnFavorites.addClass("on");
			}
			
			genBtnMenuHist   = genMenuHist.getChild( genMenuHistIndex , "btnMenuHist" );
			genBtnMenuHist.setValue(item.menuNm);
			genBtnMenuHist.setUserData("menuData",item);
		});
	};
	 
	/**************************************************************************
	 * 함 수 명: scwin.fnSetMyMenuGenertor
	 * 기 능: 마이메뉴 제네레이터에 세팅관련
	 **************************************************************************/
	 scwin.fnSetMyMenuGenertor = function(){
	 	
	 	  var myMenuData = dsMyMenuList.getAllJSON();
	 	  
	 	  var genMenu1Idx, genMenu1Obj, genMenu2, genMenu2Idx, genMenu2Obj;
	 	  
	 	  genMyMenu1.removeAll();
	 	  $.each(myMenuData,function(index, item){
	 	  	
	 	  	  if(item.bmkGrpLv == 1)
	 	  	  {
	 	  	  	var getMennu1Input , genMenu1EditObj , genMenu1DelObj ,grpEditArea;
	 	  	  	
	 	  	  	//폴더 btn
	 	  	  	genMenu1Idx  = genMyMenu1.insertChild();
	 	  	  	genMenu1Obj  = genMyMenu1.getChild( genMenu1Idx , "btnMyMenu1");
	 	  	  	genMenu1Obj.setValue(item.menuNm);
	 	  	  	genMenu1Obj.setUserData("myMenuData", item);
	 	  	  	
	 	  	  	if(item.menuId == "GRP999")
	 	  	  	{
		 	  	  	//드래그금지
			 	  	$("#"+genMenu1Obj.id).parent().addClass("on");
			 	  	$("#"+genMenu1Obj.id).parent().addClass("noSort");
			 	  		
			 	  	//이름변경버튼 삭제버튼 숨기기
			 	  	grpEditArea = genMyMenu1.getChild( genMenu1Idx , "grpEditArea");
			 	  	$("#"+grpEditArea.id).hide();
	 	  	  	}
	 	  	  	
	 	  	  	//폴더 input
	 	  	  	getMennu1Input  = genMyMenu1.getChild( genMenu1Idx , "iptMyMenu1");
	 	  	  	getMennu1Input.setValue(item.menuNm);
	 	  	  	getMennu1Input.setUserData("myMenuData", item);
	 	  	  	getMennu1Input.setUserData("myMenuLabel", genMenu1Obj);
	 	  	  	getMennu1Input.setUserData("myMenuInput", getMennu1Input);
	 	  	  	
	 	  	  	//폴더 메뉴명 변경 btn
	 	  	  	genMenu1EditObj = genMyMenu1.getChild( genMenu1Idx , "myMenuNmEdit");
	 	  	  	genMenu1EditObj.setUserData("myMenuData", item);
	 	  	  	genMenu1EditObj.setUserData("myMenuLabel", genMenu1Obj);
	 	  	  	genMenu1EditObj.setUserData("myMenuInput", getMennu1Input);
	 	  	  	
	 	  	  	genMenu1DelObj  = genMyMenu1.getChild( genMenu1Idx , "myMenuDelete");
	 	  	  	genMenu1DelObj.setUserData("myMenuData", item);
	 	  	  	
	 	  	  }
	 	  	  
	 	  	  if(item.bmkGrpLv == 2)
	 	  	  {
	 	  	  	var genMenu2DelObj , genMenu2Input;
	 	  	  	genMenu2 = genMyMenu1.getChild( genMenu1Idx , "genMyMenu2" );
 				genMenu2Idx = genMenu2.insertChild();
 				
 				genMenu2Obj = genMenu2.getChild( genMenu2Idx , "btnMyMenu2" );
 				genMenu2Obj.setValue(item.menuNm);
	 	  	  	genMenu2Obj.setUserData("myMenuData", item);
	 	  	  	
	 	  	  	genMenu2Input = genMenu2.getChild( genMenu2Idx , "genMyMenu2BmkMngNo" );
 				genMenu2Input.setValue(item.bmkMngNo);
	 	  	  	
	 	  	  	genMenu2DelObj = genMenu2.getChild( genMenu2Idx , "myMenuChildDelete" ); 
 				genMenu2DelObj.setUserData("myMenuData", item);
	 	  	  }
	 	  	 
	 	  });
	 	  
	 	  //처음 사용시 미지정그룹생성 
	 	  if(dsMyMenuList.getMatchedIndex( "menuId" , "GRP999" , true ).length == 0)
	 	  {
		 	  //미지정그룹 표시
		 	  $.each(dsMyMenuInitList.getAllJSON(),function(index,item){
		 	  	if(item.bmkGrpLv == 1)
		 	  	{
		 	  		var grpEditArea;
		 	  		genMenu1Idx  = genMyMenu1.insertChild();
		 	  		genMenu1Obj  = genMyMenu1.getChild( genMenu1Idx , "btnMyMenu1");
		 	  		genMenu1Obj.setValue(item.menuNm);
		 	  		genMenu1Obj.setUserData("myMenuData", item);
		 	  		
		 	  		//드래그금지
		 	  		$("#"+genMenu1Obj.id).parent().addClass("on");
		 	  		$("#"+genMenu1Obj.id).parent().addClass("noSort");
		 	  		
		 	  		//이름변경버튼 삭제버튼 숨기기
		 	  	  	grpEditArea = genMyMenu1.getChild( genMenu1Idx , "grpEditArea");
		 	  	  	$("#"+grpEditArea.id).hide();
		 	  	  	
		 	  	}
		 	  	
		 	  	if(item.bmkGrpLv == 2)
		 	  	{
		 	  		var genMenu2DelObj , genMenu2Input;
		 	  	  	genMenu2 = genMyMenu1.getChild( genMenu1Idx , "genMyMenu2" );
	 				genMenu2Idx = genMenu2.insertChild();
	 				
	 				genMenu2Obj = genMenu2.getChild( genMenu2Idx , "btnMyMenu2" );
	 				genMenu2Obj.setValue(item.menuNm);
		 	  	  	genMenu2Obj.setUserData("myMenuData", item);
		 	  	}
		 	  });
	 	  }
	 	  
	 };
	
	/**************************************************************************
	 * 함 수 명: scwin.fnSetMenuGenertor
	 * 기 능: 메뉴 제네레이터에 세팅관련
	 **************************************************************************/
	scwin.fnSetMenuGenertor = function(){
		if( gcm.MENU_LIST.length == 0) return; 
        
		var menuList = JSON.parse(gcm.MENU_LIST);
		
    	var len = menuList.length;
        var genMenu1Idx, genMenu1Obj, genMenu2, genMenu2Idx, genMenu2Obj, genMenu3, genMenu3Idx, genMenu3Obj;
        
        dsMenuList.setJSON(menuList);
		dsMenuSearchList.setJSON(dsMenuList.getMatchedJSON("level", "3"));
		
        for(i = 0; i < len; i++) {
 			if (menuList[i].level == 1) {
 			 	genMenu1Idx = genMenu1.insertChild();
 				genMenu1Obj = genMenu1.getChild( genMenu1Idx , "btnMenu1" );
 				genMenu1Obj.setValue(menuList[i].menuNm);
 				genMenu1Obj.setUserData("firstLvMenuId", menuList[i].menuId);
 			}
 			if (menuList[i].level == 2 && genMenu1Idx != null) {
 				genMenu2 = genMenu1.getChild( genMenu1Idx , "genMenu2" );
 				genMenu2Idx = genMenu2.insertChild();

 				genMenu2Obj = genMenu2.getChild( genMenu2Idx , "btnMenu2" );
 				genMenu2Obj.setValue(menuList[i].menuNm);
 			} 			
 			if (menuList[i].level == 3 && genMenu2Idx != null) {
 				genMenu3 = genMenu2.getChild( genMenu2Idx , "genMenu3" );
 				genMenu3Idx = genMenu3.insertChild();

 				genMenu3Obj = genMenu3.getChild( genMenu3Idx , "btnMenu3" );
 				genMenu3Obj.setValue(menuList[i].menuNm);
 				genMenu3Obj.setUserData("winId", menuList[i].menuId);
 				genMenu3Obj.setUserData("tabUrl", menuList[i].scrPathNm);
 			} 			
        }
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnRemoveBookMakeCalss
	 * 기 능: navigator 즐겨찾기 on class 제거
	**************************************************************************/
	scwin.fnRemoveBookMakeCalss = function(){
		$("a[id $='btnAddBookMark']").each(function(index, item){
	 		 var navi       = $w.getComponentById($(item).attr("id"));
		 	 var menuObj    = navi.getUserData('menu');
	 		   
	 		 if(dsMyMenuList.getMatchedIndex( "menuId" , menuObj.menuId , true ).length == 0)
	 		 {
	 		    if(navi.hasClass("on"))
	 		    {
	 				navi.removeClass("on")
	 			}
	 		}		 
	 	});
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnBookMarkChk
	 * 기 능: 즐겨찾기 체크
	**************************************************************************/
	scwin.fnBookMarkChk = function(){
	 	
		if(dsMyMenuList.getMatchedIndex( "menuId" , gcm.MENU_ID , true ).length > 0)
		{
			return true;
		}
		
		return false;
	};

	 /**************************************************************************
	 * 함 수 명: scwin.fnAddBookMark
	 * 기 능: 마이메뉴 메뉴즐겨찾기
	 **************************************************************************/
	 scwin.fnAddBookMark = function(menuObj){
	 
	 	var param = new Object();
		param.upperBmkGrpCd = "GRP999";
	 	if(menuObj == undefined)
	 	{
	 		param.menuId        = gcm.MENU_ID;
			param.menuNm        = gcm.MENU_TITLE;
			param.menuPathNm    = gcm.MENU_URL;
	 	}
	 	else
	 	{
	 		param.menuId        = menuObj.menuId;
			param.menuNm        = menuObj.menuTitle;
			param.menuPathNm    = menuObj.menuUrl;
	 	}
	 	 
		param.bmkGrpLv      = "2"
		
	 	var booleanResult = false;
	 	
		if(dsMyMenuList.getMatchedIndex( "menuId" , param.menuId , true ).length > 0)
	 	{
	 		com.showMessage('CFW0010',param.menuNm+'은 마이메뉴에 이미존재합니다.');
	 		return true;
	 	}
	 	
		
		if(confirm("즐겨찾기 추가하시겠습니까?"))
		{
			ajaxLib.ajax("/ep/Main/saveAddBookMark", {
				 mode        : "synchronous"
				,method      : "Post"
				,requestData : param 
				,callback    : function(result,e){
					
					//마이메뉴 li.on 되어진 menuId arr 가지고오기
					var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
					//마이메뉴 정보조회
					scwin.fnSelectMyMenuInfo();
					
					//최근 메뉴사용이력 5건
    				scwin.fnSelectMenuHistTop5();
					
					//마이메뉴 li on 처리하기
					scwin.fnSetMyMenuLiOn(onTargetIdArr);
					
					//메뉴셋팅
					scwin.fnMyMenuSetEvent();
					
					//마이메뉴 드랍앤드롭
					scwin.fnMyMenuDragAndDropEvent();
					
					booleanResult = true;
				},
				errCallback : function(result,e){
				}
			});
		}
		
		return booleanResult;
	 
	 };
	 
	 scwin.btnFavorites_onclick = function() {
		
		//즐겨찾기에 추가되어있으면 skip
		//if($("#"+this.id).hasClass("on")) return;
		
		var menuData = this.getUserData("menuData");
		var obj      = new Object();
		obj.menuId    = menuData.menuId;
		obj.menuTitle = menuData.menuNm;
		obj.menuUrl   = menuData.scrPathNm;
		
		scwin.fnAddBookMark(obj);
	};
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnMyMenuDragAndDropEvent
	 * 기 능: 마이메뉴 드래그앤드롭
	 **************************************************************************/
	 scwin.fnMyMenuDragAndDropEvent = function(){
	
	 	//마이메뉴 레벨1  
	 	$("#"+genMyMenu1.id).sortable({
	 	     handle   : "> a.w2anchor2"     //드래그앤드롭 핸들링 영역
	 	    ,items    : "> li:not(.noSort)" //미지정그룹은 드래그앤드롭 금지
	 	    ,axis     : "y"  //세로로 이동만가능
	 	    ,activate : function( event, ui ){
	 	    
            },start  : function (event,ui){
            
            },update : function (event, ui){
               var parentEl = $(this).closest('ul');
               var cnt = 0;
               var concaMenuId = "";
               $(parentEl).find("> li").each(function(index,el){
             	
               	  var obj = $w.getComponentById($(el).find("a[id $='btnMyMenu1']").attr("id")).getUserData("myMenuData");
               	  
				  if(cnt > 0)
				  {
					concaMenuId += "@";
				  }
				  concaMenuId += obj.menuId;
				  cnt++;
               	  
               });
 			   
 			   //초기그룹 순서 와 변경후 순서가 같은시 stop event skip	
               if(scwin.initMyMenuOrder == concaMenuId)
               {
                  scwin.booleanIsUpdate = false;
               }
               else
               {
                  scwin.booleanIsUpdate = true;  
               }
            },receive : function (event, ui){
            	
            },stop: function (event, ui) {
            	//순서변경이 없을시 skip
                if(!scwin.booleanIsUpdate) return;
                
            	var parentEl = $(this).closest('ul');
				var array    = new Array();
				var cnt = 1;
				
				$(parentEl).find("> li").each(function(index, el) {
					var obj = $w.getComponentById($(el).find("a[id $='btnMyMenu1']").attr("id")).getUserData("myMenuData");

						//미지정그룹이면 정열순서 999 세팅				    	
                    	if(obj.menuId == "GRP999")
                    	{
                    		obj.scrOdr = "999"
                    	}
                    	else
                    	{
                    		obj.scrOdr = String(cnt);
                    		cnt++;
                    	}
					array.push(obj);
                }); 
                
 				//마이메뉴 폴더 정렬순서 변경           	
            	scwin.fnSaveMyMenuMasterOrder(array);
            }
         }).disableSelection();
         
        var booleanPass = false;
        //마이메뉴 레벨2 
	 	$("#"+genMyMenu1.id).find(".sub1").sortable({
	 	     handle : " > a.w2anchor2"
	 	    ,axis     : "y"  //세로로 이동만가능
	 	    ,connectWith :  $("#"+genMyMenu1.id).find(".sub1")
            ,activate : function( event, ui ){
            
            },start  : function (event,ui){
            
            },update : function (event, ui){
            
            },receive : function (event, ui){
            	
            	//array 초기화
            	scwin.myMenuArray = new Array();
            	booleanPass       = true;
            	
                var parentLi = $(this).closest('li'); 
            	var parentObj = $w.getComponentById($(parentLi).find("a[id $='btnMyMenu1']").attr("id")).getUserData("myMenuData");
            	
            	//부모객체 array에 set
            	scwin.myMenuArray.push(parentObj);
            	
            	var parentEl = $(this).closest('ul');
				$(parentEl).find("> li").each(function(index, el) {
					var obj = $w.getComponentById($(el).find("a[id $='btnMyMenu2']").attr("id")).getUserData("myMenuData");
						obj.upperBmkGrpCd = parentObj.menuId;
						obj.scrOdr = String((index+1));
                    scwin.myMenuArray.push(obj);
                });
            },stop: function (event, ui) {
            	
            	//stop 만 실행시
            	if(!booleanPass)
            	{
            		scwin.myMenuArray = new Array();
            	}
            	
            	var parentLi = $(this).closest('li'); 
            	var parentObj = $w.getComponentById($(parentLi).find("a[id $='btnMyMenu1']").attr("id")).getUserData("myMenuData");
            	
            	//부모객체 array에 set
            	scwin.myMenuArray.push(parentObj);
            	
            	var parentEl = $(this).closest('ul');
				$(parentEl).find("> li").each(function(index, el) {
					var obj = $w.getComponentById($(el).find("a[id $='btnMyMenu2']").attr("id")).getUserData("myMenuData");
						obj.upperBmkGrpCd = parentObj.menuId;
						obj.scrOdr = String((index+1));
                    scwin.myMenuArray.push(obj);
                });
                 
                booleanPass = false 
                //마이메뉴 정렬순서변경
                scwin.fnSaveMyMenuDetailOrder(scwin.myMenuArray);
            }
         }).disableSelection();
         
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnSaveMyMenuMasterOrder
	 * 기 능: 마이메뉴 그룹 순서변경
	 **************************************************************************/
	 scwin.fnSaveMyMenuMasterOrder = function(menuInfoList){
	 	
	 	if(menuInfoList.length == 0) return;
	 	
	 	ajaxLib.ajax("/ep/Main/saveMyMenuOrderMaster", {
			 mode : "synchronous",
			 method : "Post"
			,requestData : {
							  bmkMenuList : menuInfoList	 
			               }
			,callback : function(result,e){
				
				//마이메뉴 li.on 되어진 menuId arr 가지고오기
				var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
				//마이메뉴 정보조회
				scwin.fnSelectMyMenuInfo();
				
				//마이메뉴 li on 처리하기
				scwin.fnSetMyMenuLiOn(onTargetIdArr);
				
				//메뉴셋팅
				scwin.fnMyMenuSetEvent();
				
				//마이메뉴 드랍앤드롭
				scwin.fnMyMenuDragAndDropEvent();
			},
			errCallback : function(result,e){
			}
		});
	 
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnGetMyMenuLiOn
	 * 기 능: 마이메뉴 그룹 li class >> on 된 menuId 리컨 array 
	 **************************************************************************/
	 scwin.fnGetMyMenuLiOn = function()
	 {
	 	var onTargetIdArr = new Array();
	 	$("#"+genMyMenu1.id).find(" > li.on > a").each(function(){
	 		var item =  $w.getComponentById($(this).attr("id")).getUserData("myMenuData");
	 		onTargetIdArr.push(item.menuId);
	 	});
	 	
	 	return onTargetIdArr;
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnSetMyMenuLiOn
	 * 기 능: 마이메뉴 그룹 li class on 처리 
	 **************************************************************************/
	 scwin.fnSetMyMenuLiOn = function(targetArr){
	 
	 	if(targetArr == null || targetArr.length == 0) return;
	 
	 	$("#"+genMyMenu1.id).find(" > li > a").each(function(){
			var parentId = $(this).parent().attr("id");
	 		var menuObj  = $w.getComponentById($(this).attr("id")).getUserData("myMenuData");
	 		if($.inArray(menuObj.menuId,targetArr) > -1)
	 		{
	 			$("#"+parentId).addClass('on');
	 		}
		});
	 
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnSetInitMyMenuOrder
	 * 기 능: 마이메뉴 초기 그룹순서 set 
	 **************************************************************************/
	 scwin.fnSetInitMyMenuOrder = function()
	 {
	 	var cnt = 1;
	 	var concatMenuId = "";
	 	$("#"+genMyMenu1.id).find(" > li > a").each(function(){
			var menuObj  = $w.getComponentById($(this).attr("id")).getUserData("myMenuData");
			
			if(cnt > 1)
	 		{
	 			concatMenuId += "@";
	 		}
	 		
	 		concatMenuId += menuObj.menuId;
	 		cnt++;
			
		});
		
		scwin.initMyMenuOrder = concatMenuId;
		scwin.booleanIsUpdate = false;				
		
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnMakeNewGroup
	 * 기 능: 그룹만들기 
	 **************************************************************************/
	 scwin.fnMakeNewGroup = function(){
	 
	 	ajaxLib.ajax("/ep/Main/saveNewGroup", {
			 mode        : "synchronous"
			,method      : "Post"
			,requestData : "" 
			,callback    : function(result,e){
			
				//마이메뉴 li.on 되어진 menuId arr 가지고오기
				var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
				//마이메뉴 정보조회
				scwin.fnSelectMyMenuInfo();
				
				//마이메뉴 li on 처리하기
				scwin.fnSetMyMenuLiOn(onTargetIdArr);
				
				//메뉴셋팅
				scwin.fnMyMenuSetEvent();
				
				//마이메뉴 드랍앤드롭
				scwin.fnMyMenuDragAndDropEvent();
			},
			errCallback : function(result,e){
			}
		});
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnSaveMyMenuDetailOrder
	 * 기 능: 마이메뉴 정렬순서변경
	 **************************************************************************/
	 scwin.fnSaveMyMenuDetailOrder = function(childList){
	 
	 	//자식객체 리스트 length가 0 이면 return
