			 mode        : "synchronous"
			,method      : "Post"
			,requestData : {
			                  bmkMenuList   : childList	 
			               }
			,callback    : function(result,e){
			
				//마이메뉴 li.on 되어진 menuId arr 가지고오기
				var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
				//마이메뉴 정보조회
				scwin.fnSelectMyMenuInfo();
				
				//마이메뉴 li on 처리하기
				scwin.fnSetMyMenuLiOn(onTargetIdArr);
				
				//메뉴셋팅
				scwin.fnMyMenuSetEvent();
				
				//마이메뉴 드랍앤드롭
				scwin.fnMyMenuDragAndDropEvent();
			},errCallback : function(result,e){
			
			}
		});
	 
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnUpdateMyMenu
	 * 기 능: 마이메뉴 메뉴명변경
	 **************************************************************************/
	 scwin.fnUpdateMyMenu = function(menuInfo){
	 
	 	ajaxLib.ajax("/ep/Main/updateMyMenuInfo", {
			 mode        : "synchronous"
			,method      : "Post"
			,requestData : menuInfo
			,callback    : function(result,e){
				com.showMessage('CFW0010','메뉴명을 변경했습니다.');
				
				//마이메뉴 li.on 되어진 menuId arr 가지고오기
				var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
				//마이메뉴 정보조회
				scwin.fnSelectMyMenuInfo();
				
				//마이메뉴 li on 처리하기
				scwin.fnSetMyMenuLiOn(onTargetIdArr);
		    },errCallback : function(result,e){
		    
			}
		});
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnDeleteMyMenu
	 * 기 능: 마이메뉴 삭제
	 **************************************************************************/
	 scwin.fnDeleteMyMenu = function(menuInfo){
	 	
	 	ajaxLib.ajax("/ep/Main/deleteMyMenuInfo", {
			mode        : "synchronous"
		   ,method      : "Post"
		   ,requestData : menuInfo
		   ,callback    : function(result,e){
				//com.showMessage("SCN0002", "삭제");			//$1 되었습니다.
				
				//마이메뉴 li.on 되어진 menuId arr 가지고오기
				var onTargetIdArr = scwin.fnGetMyMenuLiOn();
				
				//마이메뉴 정보조회
				scwin.fnSelectMyMenuInfo();
				
				//마이메뉴 li on 처리하기
				scwin.fnSetMyMenuLiOn(onTargetIdArr);
		   },
			errCallback : function(result,e){
			}
		});
	 };
	 
	 /**************************************************************************
	 * 함 수 명: scwin.fnSelectMyMenuInfo
	 * 기 능: 마이메뉴 조회
	 **************************************************************************/
	 scwin.fnSelectMyMenuInfo = function(){
	 
	 	//마이메뉴 정보호출 
		ajaxLib.ajax("/ep/Main/selectMyMenuInfo", {
			mode        : "synchronous"
		   ,method      : "Post"
		   ,requestData : ""
		   ,callback    : function(result,e){
				
				//마이메뉴 data collection 에 set
				dsMyMenuList.setJSON(result.data.myMenuInfo);
				
				//마이메뉴 genertor set
				scwin.fnSetMyMenuGenertor();
				
				//마이메뉴 그룹 ORDER 순서저장
				scwin.fnSetInitMyMenuOrder();
				
			},
			errCallback : function(result,e){
			}
		});
	 
	 }; 
	 
