	/**************************************************************************
     * 함 수 명: scwin.fnGeneratorQuickWarp
     * 기 능: 내 퀵메뉴 제네레이터 
    **************************************************************************/
	scwin.fnGeneratorQuickWarp = function(myQuMngList){
		
		var genIndex , genQuickWrapLiComp,genQuickWrapAtagComp , genQuickWrapSpanComp , genQuickWrapLabelComp , imgSrcUrl ;
		genQuickWrap.removeAll();
		$.each(myQuMngList,function(index,item){
			//imgSrcUrl =  "http://kofficed.sdcit.co.kr/cf/CF00350000P/downloadMAV?aflSeq=" + item.aflSeq + "&aflId=" + item.aflId;
			imgSrcUrl =  "/cf/CF00350000P/downloadMAV?aflp=" + item.aflp;
			
			var background = "url("+imgSrcUrl+")"; 
			genIndex = genQuickWrap.insertChild();
			genQuickWrapAtagComp  = genQuickWrap.getChild( genIndex , "genQuickWrapAtag" ); 
			genQuickWrapSpanComp  = genQuickWrap.getChild( genIndex , "genQuickWrapSpan" ); 
			genQuickWrapLabelComp = genQuickWrap.getChild( genIndex , "genQuickWrapLabel" );
			
			genQuickWrapSpanComp.setStyle("background-image",background);
			genQuickWrapSpanComp.setUserData("gubun","link");
			genQuickWrapSpanComp.setUserData("quickMenuData",item);
			genQuickWrapLabelComp.setLabel(item.quickMenuTtl);
			 
		}); 
		
		//더보기 상태
		genIndex = genQuickWrap.insertChild();
		genQuickWrapAtagComp  = genQuickWrap.getChild( genIndex , "genQuickWrapAtag" ); 
		genQuickWrapSpanComp  = genQuickWrap.getChild( genIndex , "genQuickWrapSpan" ); 
		genQuickWrapLabelComp = genQuickWrap.getChild( genIndex , "genQuickWrapLabel" );
		genQuickWrapLiComp    = genQuickWrap.getChild( genIndex , "genQuickWrapLi" ); 
		
		genQuickWrapLiComp.addClass("noSort")
		genQuickWrapAtagComp.addClass("more")
		genQuickWrapSpanComp.setUserData("gubun","quickMore");
		genQuickWrapLabelComp.setLabel("더보기");
		
	};
	
	/**************************************************************************
     * 함 수 명: scwin.fnMyQuickMenuDragAndDorpEvent
     * 기 능: 퀵메뉴 역역 드래그앤 드롭 
    **************************************************************************/
	scwin.fnMyQuickMenuDragAndDorpEvent = function(){
		$("#"+genQuickWrap.id).sortable({
		     items    : "> li:not(.noSort)" //더보기는 드래그앤드롭 금지
		    ,handle   : $("#"+genQuickWrap.id).find("> li > a").find("span[id $='genQuickWrapSpan']")
		    ,axis     : "x"  //가로로 이동만가능
	 	    ,stop: function (event, ui) {
            	var parentEl = $(this).closest('ul');
            	var array    = new Array();
            	$(parentEl).find("> li").each(function(index, el){
            	     var compId = $(el).find(" > a").find("span[id $='genQuickWrapSpan']").attr("id");
            	     var comp   = $w.getComponentById(compId);
            	     var gubugn = comp.getUserData("gubun");
            	     var obj    = null; 
            	     if("quickMore" != gubugn)
            	     {
            	     	obj = comp.getUserData("quickMenuData");
            	     	obj.quickMenuOdr = index + 1;
            	     	array.push(obj);
            	     }
            	});
            	
            	//마이퀵메뉴 정렬순서변경 함수 호출
            	scwin.fnMyQuickMenuOrderReSet(array);        	
            	
            }
         }).disableSelection();
         
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnMyQuickMenuOrderReSet
	* 기 능: 마이퀵메뉴 정렬순서 변경
	**************************************************************************/
	scwin.fnMyQuickMenuOrderReSet = function(array){
		
		ajaxLib.ajax("/ep/Main/updateTepMyQuMenuOrder", {
			mode        : "synchronous"
		   ,method      : "Post"
		   ,requestData : {
		                    tepMyquMngVOList : array
		                  }
		   ,callback    : function(result,e){
				//scwin.fnMakeQuickWarpGen();
			},
			errCallback : function(result,e){
			}
		});
	};
	
