                  ];		

	/**************************************************************************
	 * 함 수 명: scwin.fnWorkGoCheck
	 * 기 능: 메인 메일갯수, 결재갯수,전표갯수,법인카드갯수,기타갯수,출근체크조회
	 **************************************************************************/	
	scwin.fnWorkGoCheck = function() {
		var oTabwindow = tab_main.getWindow(0);
        
        if(typeof oTabwindow == "object" &&
             typeof oTabwindow["scwin"] == "object" &&
             typeof oTabwindow.scwin["fnWorkGoCheck"] == "function"){
             oTabwindow.scwin["fnWorkGoCheck"]();
        }
		
	};

