	/**************************************************************************
     * Event: scwin.btnMagr_onclick
     * 기 능: 경영현황 onClick 
    **************************************************************************/
	scwin.btnMagr_onclick = function() {
		scwin.fnNewMailAprvCnt();
		
		if(btnMagr.hasClass('on')){
			return;
		}
		
		scwin.fnMakeMagrWarpGen();
	};

	/**************************************************************************
     * 함 수 명: scwin.fnMakeMagrWarpGen
     * 기 능: 내 경영현황 조회 
    **************************************************************************/
	scwin.fnMakeMagrWarpGen = function(){
		//마이메뉴 정보호출
		ajaxLib.ajax("/ep/EP20030001U/selectTepMagrSituMng", {
			mode        : "synchronous"
		   ,method      : "POST"
		   ,requestData : {
		   		empNo :  sessionStorage.getItem("userEmpno")
		   		,magrSituGb : "1"
		   	}
		   ,callback    : function(result,e){
				var magrSituMngList = result.data.magrSituMngList;
				scwin.fnGeneratorMagrWarp(magrSituMngList);
			},
				errCallback : function(result,e){
			}
		});
	};

	/**************************************************************************
     * 함 수 명: scwin.fnGeneratorMagrWarp();
     * 기 능: 내 경영현황 제네레이터 
    **************************************************************************/
	scwin.fnGeneratorMagrWarp = function(magrSituMngList){
		
		var genIndex
		, genMagrWrapLiComp
		, genMagrWrapSpanComp
		, genMagrWrapLabelComp
		, mgNewIconComp;
		
		genMagrWrap.removeAll();
		
		$.each(magrSituMngList,function(index,item){
			genIndex = genMagrWrap.insertChild();
			genMagrWrapSpanComp  = genMagrWrap.getChild( genIndex , "genMagrWrapSpan" );
			genMagrWrapLabelComp = genMagrWrap.getChild( genIndex , "genMagrWrapLabel" );
			genMagrWrapLabelComp.setUserData("gubun","link");
			genMagrWrapLabelComp.setUserData("magrData",item);
			mgNewIconComp = "mf_genMagrWrap_"+genIndex+"_mgNewIcon";
			
			if (item.newYn == 'Y') {
				$("#"+mgNewIconComp).show();
			} else {
				$("#"+mgNewIconComp).hide();
			}	
			genMagrWrapLabelComp.setValue(item.ptupTtl);
		});
	};

	/**************************************************************************
    * Event: scwin.genMagrWrapSpan_onclick
    * 기 능: 경영현황 span onClick 시 
    **************************************************************************/
	scwin.genMagrWrapLabel_onclick = function(e) {
	    var gubun = this.getUserData("gubun");
	    var magrData = this.getUserData("magrData");
	    
	    var aflId   = magrData.aflId; // 아이디 매핑
	    var aflCount = magrData.aflCount; // 아이디 매핑
 //         var aflData = com.udcAflSingle(aflId); // 싱글 데이터 불러오기  
        var aflData = com.udcAflMinSeqSingle(aflId); // 싱글 데이터 불러오기
	    var fileDownloadUrl = "/cf/CF00350000P/downloadMAV?aflp="+aflData.aflp
	    
	    if(sessionStorage.getItem("userEmpno") == "049027"){
		    var line_count = this.id.split("_")[2];
		    var mgNewIconComp2 = "mf_genMagrWrap_"+line_count+"_mgNewIcon";
		    $("#"+mgNewIconComp2).hide();
	    	ajaxLib.ajax("/ep/EP20030001U/saveTepMagrSitu2", {
	             mode       : "synchronous"
	            ,method     : "POST"
	            ,requestData: {
	            				magrSituNo : magrData.magrSituNo
	              		        ,newYn	: "N"
	                          } 
	            ,callback   : ""
	            ,errCallback: ""
        	});
	    }
	    
	    if("" == fileDownloadUrl ) {
			com.showMessage('CFW0010','등록된 메뉴도움말이 없습니다.');
			return;
		}
	    
		if(aflCount > 1){
			var pop_close_id = $("div.w2popup_windows").context.body.lastChild.id;
			com.closePopup(pop_close_id);
			
			var popOps = {
		        popup_name :"경영현황 파일리스트",
		        url :"/ux/common/index_tab_genMagrPop.xml",
		        data : {
		            magrSituNo : magrData.magrSituNo,
		            fileDownloadUrl : fileDownloadUrl
		        },
		        width:"700",
		        height:"540",
		        callbackFn : ""
		    };
			com.popup_open(popOps);
		}else{
			var popOps = {
					  	  url        :"/ux/common/pdfView.xml"
                    	, type       :"browser"
						, modal      :false
						, height     :"900"
						, width      :"1000"
						, popup_name :gcm.MENU_TITLE
						, resizable  :true
						, data       :{fileDownloadUrl:fileDownloadUrl}
			};
			com.popup_open(popOps);
		}
	};
	
	/**************************************************************************
    * 함수명: scwin.fnCheckMargAuth
    * 기 능 : 경영현황 권한체크 후 경영현야 show hide 
    **************************************************************************/
	scwin.fnCheckMargAuth = function(){
		ajaxLib.ajax("/ep/Main/selectAuthMagrSitu", {
	             mode       : "asynchronous"
	            ,method     : "POST"
	            ,requestData: ""
	            ,callback    : function(result,e){
	            
	            	var authYn = result.data.authYn;
	            	
	            	if(authYn == "Y"){
						grpMagrLi.setStyle( "display" , "" );
	            	}
	            	else
	            	{
						grpMagrLi.setStyle( "display" , "none" );
	            	}
				},
				errCallback : function(result,e){
				}
        	});
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.tab_main_ontabclose
	 * 기 능: 탭닫기시 메뉴 메뉴정보 set
	 **************************************************************************/
	scwin.tab_main_ontabclose = function(tabID,idx) {
	
		var len = gcm.tab_main.tabArr.length;
		var key = 0;
		
		if(len == idx){
			key = idx-1;
		}else{
			key = idx;
		}
	
		// 텝버튼 클릭시 메뉴아이디 변경처리
		gcm.MENU_ID = gcm.tab_main.tabArr[key].userTabID;
        if( gcm.MENU_ID != "" ) {
            var menuDatas = $.grep(JSON.parse(gcm.MENU_LIST), function( n, i ) { if( n.menuId == gcm.MENU_ID ) return n; });
            
            if(menuDatas.length > 0)
            {
	            gcm.MENU_TITLE = menuDatas[0].menuNm;
	            gcm.MENU_URL   = menuDatas[0].scrPathNm;
            }
        }
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.ibxInnerEmployeeVal_onkeyup
	 * 기 능: 탭닫기시 메뉴 메뉴정보 set
	 **************************************************************************/
	scwin.ibxInnerEmployeeVal_onkeyup = function(e) {
		if (e.key == "Enter") {
			scwin.fnInnerGroupEmployeeCall();
		} 
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.btnEmpyloeeFind_onclick
	 * 기 능: 내부직원검색 , 그룹직원 검색 onClick
	 **************************************************************************/
	scwin.btnEmpyloeeFind_onclick = function() {
		scwin.fnInnerGroupEmployeeCall();
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnInnerGroupEmployeeCall
	 * 기 능: 내부직원검색 , 그룹직원 검색 로직
	 **************************************************************************/
	scwin.fnInnerGroupEmployeeCall = function(){
	
		var chkVal     = sbxEmpyloeeDiv.getValue();
		var searchText = ibxInnerEmployeeVal.getValue();
		var searchType = "";
			
		if(chkVal == "Y" )
		{//그룹직원
			searchType = "Y";
		}
		else
		{//사내직원
			searchType = "N";
		}
			
		scwin.fnInnerUserSearch(searchText,searchType);
	
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.sbxEmpyloeeDiv_onchange
	 * 기 능: 내부직원 , 그룹직원 체크박스 onChange 
	 **************************************************************************/
	scwin.sbxEmpyloeeDiv_onchange = function() {
		var chkVal     = sbxEmpyloeeDiv.getValue();
			
		if(chkVal == "Y" )
		{//그룹직원
			ibxInnerEmployeeVal.setPlaceholder( "그룹직원 검색" );
		}
		else
		{//사내직원
			ibxInnerEmployeeVal.setPlaceholder( "사내직원 검색" );
		}
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.fnInnerUserSearch
	 * 기 능: 상단 오른쪽 검색창에에서 검색한 사내직원 , 그룹직원 창호출
	 **************************************************************************/	
	scwin.fnInnerUserSearch = function(searchText , searchType) {
		ajaxLib.ajax("/ep/EP10010001P/selectUserSearchXml", {
            mode: "synchronous",
            method: "POST",
            requestData: "",
			callback : function(result,e) {
				scwin.cursorPos = -1;


	            var left = (screen.width / 2 - 350);
	            var top = (screen.height / 2 - 300);
				var url = "";
				
				// prdMail 메일시스템 운영 전환 by shryu1 2020-01-31				
				window.open('about:blank', 'userInnerSearch',  'toolbar=no,location=no,directories=no,status=yes,menubar=no,scrollbars=yes,resizable=yes,width=1020,height=650,left=' + left + ',top=' + top);
	
                url = result.data.userSearchAction + "?searchText=" + encodeURI(searchText) + "&searchCompanyType=" + searchType;                
                                
	   			var $form = $("<form></form>");   				
	   			$form.attr("target", "userInnerSearch");
	        	$form.attr("action", url);
	        	$form.attr("method", "post")
	        	$form.attr("accept-language", "ko")
	        	$form.appendTo('body');	   
            	var $input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"Message",  id:"Message",  value:result.data.userSearchXml});
	        	$input.appendTo($form);
				$input = $("<input></input>");
            	$input.attr({type:"hidden",  name:"isTelepiaYN",  id:"isTelepiaYN",  value:"true"});
	        	$input.appendTo($form);	  		        	     			        
	        	$form.submit();   
	        	
	        	setTimeout(function() { ibxInnerEmployeeVal.setValue(""); }, 10);                 					
			},
            errCallback: function(result,e) { }
        });	
	}
