 	scwin.onpageload = function() {
 
        // 임원인 경우 개봉대기목록 표시
        if(sessionStorage.getItem("empGbCd") ==  'CF01302') { // 임원인 경우
            tndrOpn.visible( true );
            grdTndcOpn.visible( true );
            aflidArea.setSize( '' , '103px' );
            
            tndcOpn.visible( true );
            grdTcsTndrOpnList.visible( true );
        } else {
            tndrOpn.visible( false );
            grdTndcOpn.visible( false );
            grdAprvList.setGridHeight( "328px" );
            aflidArea.setSize( '' , '78px' );
            
            tndcOpn.visible( false );
            grdTcsTndrOpnList.visible( false );
        }
 
 		//scwin.fnSelectChartInfo(); // 경영목표 차트정보
        
        /* 공지사항 조회 전 관련항목 Clear */
		grpTcfNotiMap.init();
		dsTcfNotiMap.setEmptyValue();
		dsTcfNotiList.removeAll();
        
        scwin.fnSelectNotiList(); // 공지사항 조회
        
        // 첨부파일 처리
        com.getUdcComp("udcAflId", "gen_aflList").addClass("col1");   // 첨부파일 LIST 1개씩 : UDC.xml 참조
        com.setUdcValue( "udcAflId", "jobGbCd", "CF");
        com.setUdcValue( "udcAflId", "menuId", "CF08000001U");
        com.setUdcValue( "udcAflId", "fileExt", "*.*");
	    com.getUdcComp("udcAflId", "btnAddFile").addClass("hide");//파일첨부 비활성화
		com.setUdcValue( "udcAflId", "usage", ""); // 버튼숨기기
        com.setUdcValue( "udcAflId", "fileSize", "INS");              // 용량표시안함(표시함:INS) : 필수값       
        
		// 업무구분코드[CF004] : 결재상태코드
		com.selCdListVrbl("sbxAprvStusCd", {"upperCd": "CF004","cd": "'CF004110','CF004120'"});
		com.selCdListVrbl("grdAprvList:aprvStusCd", {"upperCd": "CF004","cd": "'CF004110','CF004120'"});
        
		//조회
		scwin.fnSelectAprvList();

    	// 개봉대기목록(외주) 조회
		scwin.fnSearchTndcOpnList();
		
		// 개봉대기목록(하자) 하자
		scwin.fnSelectTcsTndrOpnList();
        
	};
	
