    // 날짜 데이터 문자로 변환 ex) 20220505
    scwin.getYYYYMMDDToString = function(date) {
	   
        let dd = date.getDate();
        let mm = date.getMonth()+1; //January is 0
    		
    	let yyyy = date.getFullYear();
    	
        if( dd < 10 ) {
            dd = '0' + dd;
        }
        
        if ( mm < 10 ) {
            mm = '0' + mm;
        }
    			
        yyyy = yyyy.toString();
        mm = mm.toString();
        dd = dd.toString();
    		
        let s1 = yyyy+mm+dd;
    	return s1;
        
    };
     
	// 차트 정보 조회
	scwin.fnSelectChartInfo = function(){
		ajaxLib.ajax("/ep/Main/selectChartInfo", {
	    	mode: "asynchronous"
	        , method: "POST"
	        , requestData: ""
	        , callback: function(result,e) {
	        	//수주매출 달성그래프
	        	scwin.fnSetChart_1(result);
                scwin.fnSetChart_2(result);
                scwin.fnSetChart_3(result);
                scwin.fnSetChart_4(result);
	        }
	        , errCallback: function(result,e) { 
	        }
	    });
	
	};
    
    // 공지사항 조회
	scwin.fnSelectNotiList = function() {
		ajaxLib.ajax("/cf/cf0800/CF08000001U/selectTcfNotiList", {
             mode: "synchronous",
             method: "POST",
             requestData: {
                    notiDt : $w.getCurrentServerDate("yyyyMMdd"),
                    isMain : "Y"
             },
             callback: function(result, e) {
                           dsTcfNotiList.setJSON(result.data);                          
                           com.setUdcValue( "udcAflId", "aflId", dsTcfNotiMap.get("aflId"));  // 첨부파일ID
             },
             errCallback: function(e) {console.log(e);}    
        });
	};

	// 개봉대기목록(외주) 조회
	scwin.fnSearchTndcOpnList = function() {
        let date = dateLib.getCurrentDate("yyyyMMdd");
        toDate = date;
        frDate = dateLib.addMonths(date, -3);
        
        dsRequestParamMap.set( 'tndrEndDtmTo'	, toDate);
        dsRequestParamMap.set( 'tndrEndDtmFrom'	, frDate);
    	dsRequestParamMap.set( 'userEmpno'	, sessionStorage.getItem('userEmpno'));
    	dsRequestParamMap.set( 'deptCd'		, sessionStorage.getItem('deptCd'));
        
    
        ajaxLib.ajax("/sc/sc0005/SC00051000U/selectSearchTndcOpnListMain", {
             mode: "synchronous",
             method: "POST",
             requestData: dsRequestParamMap.getJSON(),                
             callback: function(result, e) {
                    dsTndcOpnList.setJSON( result.data.codeList );
                    com.setUdcValue("dsTndrCount", "udcListCount", dsTndcOpnList.getRowCount());
             },
             errCallback: function(e) {console.log(e);}    
        });
	};
	
	// 개봉대기목록(하자) 리스트 조회
	scwin.fnSelectTcsTndrOpnList = function() {
	    //서비스 호출
	    ajaxLib.ajax("/cs/cs4000/CS40002000U/selectTcsTndrOpnList", {
	        mode        : "asynchronous",
	        requestData : "",
	        callback    : function(result, e) {
				dsTcsTndrOpnList.setJSON(result.data.dsTcsTndrOpnList);
				com.setUdcValue("dsTndcCount", "udcListCount", dsTcsTndrOpnList.getTotalRow());
			},    
	        errCallback : "fnErrCallback"
	    });
	};
    
	// 결재현황 정보 리스트 조회
    scwin.fnSelectAprvList = function() {

        let now = new Date();
        let lastWeek = new Date(now.setDate(now.getDate() - 7));
        
        console.log(scwin.getYYYYMMDDToString(lastWeek));
        
		ajaxLib.ajax("/cf/cf0400/CF04000010U/selectAprvPrgsListMain", {
			mode : "asynchronous",
			method : "POST",
		    requestData : dsSearchMap.getJSON(),
            callback: function(result, e) {
                        //페이지 조회결과 표시
                    dsAprvList.setJSON(result.data.dsAprvList);
                    com.setUdcValue("dsAprvCount", "udcListCount", dsAprvList.getRowCount());
            },
            errCallback: function(e) {console.log(e);}    
		}); 
    };
    
    
