     scwin.fnSetChart_1 = function(result){
		
     	var goalPerfromenceInfo = result.data.goalPerfromenceInfo;

     	var magrGoalTtl = "";
        var orderMonthPfmcRt = "";
        var orderTotalPfmcRt = "";
     	
     	var nowDate = new Date();
     	var month   = $w.getFormattedDate( nowDate , "MM" );
        month = "08";
     	
     	if(goalPerfromenceInfo != null && goalPerfromenceInfo.magrGoalTtl != undefined){
     		magrGoalTtl = goalPerfromenceInfo.magrGoalTtl;
     	} else{
     		magrGoalTtl = "수주 [단위:억원]"; 
     	}
     	
     	if(goalPerfromenceInfo != null && goalPerfromenceInfo.orderMonthPfmcRt != undefined){
     		orderMonthPfmcRt = goalPerfromenceInfo.orderMonthPfmcRt;
     	}else{
     		orderMonthPfmcRt ="0";
     	}
     	
     	if(goalPerfromenceInfo != null && goalPerfromenceInfo.orderTotalPfmcRt != undefined){
     		orderTotalPfmcRt = goalPerfromenceInfo.orderTotalPfmcRt;
     	}else {
     		orderTotalPfmcRt ="0";
     	}

		var jsonData = [
						 {label: String(Number(month))+"월", goal:  "150" , Performance : orderMonthPfmcRt },
						 {label: "누계", goal:  "1650" , Performance : orderTotalPfmcRt },
		               ];

		var options = {
				// Style
				animation: "1",
				showBorder: "0",
				canvasPadding: "30",
				showValues: "1",
                numbersuffix : "",
                subCaption : magrGoalTtl,
                // Axis
				//showAxisLines: "0",
				yAxisMaxvalue: "2000",
                yAxisMinValue: "0",
                numDivLines  : "7" , // 가로축 분할선 수
                showToolTip  : "0",
                yAxisName : "",
                // Data Values
				rotateValues: "0",
				placevaluesInside: "0",			
				// baseFont 
				baseFont: "Malgun Gothic",
				baseFontSize: "11", // 전체 폰트 사이즈
				baseFontColor: "#212121",
				// Legend(범례)
				showLegend: "0",
				legendBorderColor: "#ffffff",
				legendBgColor: "#ffffff",
				legendShadow: "0",
				legendIconScale: "1",
				// Format
				formatNumberScale: "0",
			};
			
			fusionChartGoalPerform_1.setChartAttribute(options);  
            dsChartList1.setJSON(jsonData);             
	}	
    
