	/** 그리드 셀 클릭시 처리 **/
	scwin.grdTndcOpn_oncellclick = function(row,col) {
	
        let columnId = grdTndcOpn.getColumnID( col );
		if(row < 0) return;
		if(columnId == "subctConstNm"){
		
        	dsRequestParamMap.set( 'tndrEndDtmTo'	, toDate);
        	dsRequestParamMap.set( 'tndrEndDtmFrom'	, frDate);
    		dsRequestParamMap.set( 'userEmpno'	, sessionStorage.getItem('userEmpno'));
    		dsRequestParamMap.set( 'deptCd'		, sessionStorage.getItem('deptCd'));
		
			// 상세페이지로 이동
        	let title       = "입찰서개봉";
        	let url         = "/ux/sc/sc0005/SC00051000U.xml";
        	let openOpt     = "2";   //호출유형(신규 탭)
            let navi = new Array();
            navi[0] = "SC00051000U";
            navi[1] = title;
            navi[2] = url;
        	let data = {
	        	    "siteCd" : dsTndcOpnList.getCellData( row , 'siteCd' ),
					"poNo" : dsTndcOpnList.getCellData( row , 'poNo' ),
					"tndrDgr" : dsTndcOpnList.getCellData( row , 'tndrDgr' ),
					"callId" : "callMain",
					"callTitle" : "입찰서개봉",
                    "searchParam" : JSON.stringify(dsRequestParamMap.getJSON())
            };
        	com.detailOpen(title, data.callId, url, openOpt, data, navi);
		}
		
	}; 
	
	//그리드 셀 클릭시
	scwin.grdTcsTndrOpnList_oncellclick = function(row, col) {
	    debugger;
        
        if (row < 0) return;
	    
	    let rowData = dsTcsTndrOpnList.getRowJSON(row);
	    
	    if (grdTcsTndrOpnList.getColumnID(col) === "constNm") {

	        // 상세페이지로 이동
	        let title   = "입찰개봉";
	        let url     = "/ux/cs/cs4000/CS40002000U.xml";
	        let openOpt = "2";   //호출유형(화면전환)
	        
	        let data = {
				"tndrNo"    : rowData.tndrNo,
