head.script:
/***************************************************************************
 * Program ID : CF00000000U.xml 
 * Program Name : 공통코드POOL 관리 
 * Author : 이석진 (tplsj)
 * Date : 2019.02.15
 * 
 * History 
 * ======================================== 
 * 변경일자     수정자   내 용 
 * ======================================== 
 * 2019.02.15   이석진   최초작성
 *     
 **************************************************************************/ 

    /************************************************************************************
     *  [1] 전역변수 
     ************************************************************************************/    
	var svcId;		//callback 처리용 서비스ID

    /************************************************************************************
     *  [2] onpageload(초기화포함)
     ************************************************************************************/  
    scwin.onpageload = function() {
        //화면초기화 호출
        scwin.fnInitial();

        //SelectBox 코드값 맵핑 
		dsCdCF035.setJSON(com.sessionTcfCodeSearch("CF035"));
    };
    
    /** 화면초기화 **/
    scwin.fnInitial = function() {
        grpSearch.init();					//조회그룹 초기화
        grdTcfCodePoolList.initGrid();		//공통코드목록 초기화
		com.setUdcValue("dsTcfCodePoolListTotalCount", "udcListCount", 0);
        grpTcfCodePool.init();				//공통코드내용 초기화
        grdTcfCodeDtlPoolList.initGrid();	//공통상세코드목록 초기화
		com.setUdcValue("dsTcfCodeDtlPoolListTotalCount", "udcListCount", 0);
        grpTcfCodeDtlPool.init();			//공통상세코드내용 초기화
        rdoUseYn.setSelectedIndex( 0 );		//사용여부Set

        btnNewCd.setDisabled(true);			//공통코드 신규버튼 Disable
        btnSaveCd.setDisabled(true);		//공통코드 저장버튼 Disable
        btnDelCd.setDisabled(true);			//공통코드 삭제버튼 Disable
        btnNewCdDtl.setDisabled(true);		//공통상세코드 신규버튼 Disable
        btnSaveCdDtl.setDisabled(true);		//공통상세코드 저장버튼 Disable
        btnDelCdDtl.setDisabled(true);		//공통상세코드 삭제버튼 Disable
    };

    /************************************************************************************
     *  [3] 사용자 정의함수 함수 
     ************************************************************************************/
	/**************************************************************************
	 * 함 수 명: scwin.fnSelectTcfCodePoolList
	 * 기 능: 공통상세코드POOL 정보를 리스트 조회
	 **************************************************************************/
    scwin.fnSelectTcfCodePoolList = function() {
    	//callback 처리용 서비스ID 부여
    	svcId = "selectTcfCodeDtlPoolList";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/selectTcfCodeDtlPoolList", {
            mode		: "asynchronous",
            method 		: "Post",
            requestData	: dsTcfCodeDtlPoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
    };

	/**************************************************************************
	 * Event: scwin.fnInsertTcfCodePool
	 * 기 능: 공통코드POOL 정보를 등록
	 **************************************************************************/
	scwin.fnInsertTcfCodePool = function() {
		//data유효성체크(개별)
		if (!com.validateGroup([sbxJobGbCd, ibxCommCdNm])) {
			return;
		};

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodePool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/insertTcfCodePool", {
            mode		: "asynchronous",
            method 		: "Post",
            requestData	: dsTcfCodePoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

	/**************************************************************************
	 * Event: scwin.fnUpdateTcfCodePool
	 * 기 능: 공통코드POOL 정보를 수정
	 **************************************************************************/
	scwin.fnUpdateTcfCodePool = function() {
		//data유효성체크(전체)
		if (!com.validateGroup(grpTcfCodePool)) {
			return;
		};

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodePool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/updateTcfCodePool", {
            mode		: "asynchronous",
            method 		: "POST",
            requestData	: dsTcfCodePoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

	/**************************************************************************
	 * Event: scwin.fnInsertTcfCodeDtlPool
	 * 기 능: 공통상세코드POOL 정보를 등록
	 **************************************************************************/
	scwin.fnInsertTcfCodeDtlPool = function() {
		//data유효성체크(그룹)
		if (!com.validateGroup(grpTcfCodeDtlPool)) {
			return;
		};

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodeDtlPool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/insertTcfCodeDtlPool", {
            mode		: "asynchronous",
            method 		: "Post",
            requestData	: dsTcfCodeDtlPoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

	/**************************************************************************
	 * Event: scwin.fnUpdateTcfCodeDtlPool
	 * 기 능: 공통상세코드POOL 정보를 수정
	 **************************************************************************/
	scwin.fnUpdateTcfCodeDtlPool = function() {
		//data유효성체크(그룹)
		if (!com.validateGroup(grpTcfCodeDtlPool)) {
			return;
		};

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodeDtlPool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/updateTcfCodeDtlPool", {
            mode		: "asynchronous",
            method 		: "POST",
            requestData	: dsTcfCodeDtlPoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

    /************************************************************************************
     *  [4] Callback 함수 
     ************************************************************************************/
    /** 오류시 처리 **/
	scwin.fnErrCallback = function(result, e) {

	}

    /** 정상시 후처리 **/
    scwin.fnCallback = function(result, e) {
    	switch(svcId) {
			case "selectTcfCodePoolList" : 
				//서버에서 넘겨주는 값 세팅
				dsTcfCodePoolList.setJSON(result.data.dsTcfCodePoolList);
				com.setUdcValue("dsTcfCodePoolListTotalCount", "udcListCount", dsTcfCodePoolList.getTotalRow());

				//조회후 처리
		        grpTcfCodePool.init();				//공통코드내용 초기화
				grdTcfCodeDtlPoolList.initGrid();	//공통상세코드목록 초기화
				com.setUdcValue("dsTcfCodeDtlPoolListTotalCount", "udcListCount", 0);
				grpTcfCodeDtlPool.init();			//공통상세코드내용 초기화
		        rdoUseYn.setSelectedIndex( 0 );		//사용여부Set

		        btnNewCd.setDisabled(false);		//공통코드 신규버튼 Enable
		        btnSaveCd.setDisabled(false);		//공통코드 저장버튼 Enable
		        btnDelCd.setDisabled(false);		//공통코드 삭제버튼 Enable
				btnNewCdDtl.setDisabled(true);		//공통상세코드 신규버튼 Disable
				btnSaveCdDtl.setDisabled(true);		//공통상세코드 저장버튼 Disable
				btnDelCdDtl.setDisabled(true);		//공통상세코드 삭제버튼 Disable
				break;

			case "resetTcfCodePool" : 
				//공통코드POOL 등록, 수정, 삭제 후 재조회
				scwin.btnFind_onclick();
				break;

			case "selectTcfCodeDtlPoolList" : 
				//서버에서 넘겨주는 값 세팅
				dsTcfCodeDtlPoolList.setJSON(result.data.dsTcfCodeDtlPoolList);
				com.setUdcValue("dsTcfCodeDtlPoolListTotalCount", "udcListCount", dsTcfCodeDtlPoolList.getTotalRow());

				//조회후 처리
				btnNewCdDtl.setDisabled(false);		//공통상세코드 신규버튼 Disable
				btnSaveCdDtl.setDisabled(false);	//공통상세코드 저장버튼 Disable
				btnDelCdDtl.setDisabled(false);		//공통상세코드 삭제버튼 Disable
				break;

			case "resetTcfCodeDtlPool" : 
				//공통코드상세POOL 등록, 수정, 삭제 후 재조회
		        scwin.fnSelectTcfCodePoolList();
				break;

			default :
				break;
    	}
    };
     
    /************************************************************************************
     *  [5] Event 함수 
     ************************************************************************************/      
	/**************************************************************************
	 * Event: scwin.btnFind_onclick
	 * 기 능: 공통코드POOL 정보를 리스트 조회
	 **************************************************************************/
    scwin.btnFind_onclick = function() {
    	//callback 처리용 서비스ID 부여
    	svcId = "selectTcfCodePoolList";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/selectTcfCodePoolList", {
            mode		: "asynchronous",
            method 		: "Post",
            requestData	: dsKeyTcfCodePoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
            
        });
    };

	/**************************************************************************
	 * Event: scwin.btnNewCd_onclick
	 * 기 능: 공통코드POOL 정보를 등록
	 **************************************************************************/
	scwin.btnNewCd_onclick = function() {
        grpTcfCodePool.init();				//공통코드내용 초기화
	};

	/**************************************************************************
	 * Event: scwin.btnSaveCd_onclick
	 * 기 능: 공통코드POOL 정보를 저장
	 **************************************************************************/
	scwin.btnSaveCd_onclick = function() {
		//공통코드 여부체크
		if ( strLib.isEmpty(ibxCommCd.getValue()) ) {
			//공통코드POOL 등록
	        scwin.fnInsertTcfCodePool();
		} else {
			//공통코드POOL 수정
	        scwin.fnUpdateTcfCodePool();
		};
	};

	/**************************************************************************
	 * Event: scwin.btnDelCd_onclick
	 * 기 능: 공통코드POOL 정보를 삭제
	 **************************************************************************/
	scwin.btnDelCd_onclick = function() {
		//data유효성체크(개별)
		if (!com.validateGroup([sbxJobGbCd, ibxCommCd])) {
			return;
		};

		if (!confirm("공통코드POOL 정보를 삭제 하시겠습니까?")) return;

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodePool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/deleteTcfCodePool", {
            mode		: "asynchronous",
            method 		: "Delete",
            requestData	: dsTcfCodePoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

	/**************************************************************************
	 * Event: scwin.grdTcfCodePoolList_oncellclick
	 * 기 능: 공통상세코드목록 선택시 처리
	 **************************************************************************/
	scwin.grdTcfCodePoolList_oncellclick = function(row,col) {
        //선택 ROW값 MOVE
        grpTcfCodePool.init();				//공통코드내용 초기화
        dsTcfCodePoolMap.setJSON(grdTcfCodePoolList.getRowJSON(row));

        //공통상세코드POOL 정보를 리스트 조회 호출
		grpTcfCodeDtlPool.init();			//공통상세코드내용 초기화
		dsTcfCodeDtlPoolMap.setJSON(grdTcfCodePoolList.getRowJSON(row));
        scwin.fnSelectTcfCodePoolList();
        rdoUseYn.setSelectedIndex( 0 );		//사용여부Set
	};

	/**************************************************************************
	 * Event: scwin.btnNewCdDtl_onclick
	 * 기 능: 공통상세코드POOL 정보를 등록
	 **************************************************************************/
	scwin.btnNewCdDtl_onclick = function() {
        ibxCommDtlCd.setValue( "" );		//공통상세코드Set
        ibxCommCdOdr.setValue( "0" );		//순서Set
        rdoUseYn.setSelectedIndex( 0 );		//사용여부Set
        ibxCommDtlCdNm.setValue( "" );		//공통상세코드명Set
	};

	/**************************************************************************
	 * Event: scwin.btnSaveCdDtl_onclick
	 * 기 능: 공통상세코드POOL 정보를 저장
	 **************************************************************************/
	scwin.btnSaveCdDtl_onclick = function() {
	    //기존건이 없으면 등록 한다
		var matchedIdx = grdTcfCodeDtlPoolList.getMatchedIndex("commDtlCd" , ibxCommDtlCd.getValue());
		if (matchedIdx == ""){
			//공통상세코드POOL 등록
	        scwin.fnInsertTcfCodeDtlPool();
		} else {
			//공통상세코드POOL 수정
	        scwin.fnUpdateTcfCodeDtlPool();
		};
	};

	/**************************************************************************
	 * Event: scwin.btnDelCdDtl_onclick
	 * 기 능: 공통상세코드POOL 정보를 삭제
	 **************************************************************************/
	scwin.btnDelCdDtl_onclick = function() {
		//data유효성체크
		if (!com.validateGroup([ibxCommDtlCd])) {
			return;
		};

		if (!confirm("공통상세코드POOL 정보를 삭제 하시겠습니까?")) return;

    	//callback 처리용 서비스ID 부여
    	svcId = "resetTcfCodeDtlPool";

    	//서비스 호출
		ajaxLib.ajax("/cf/CF00000000U/deleteTcfCodeDtlPool", {
            mode		: "asynchronous",
            method 		: "Delete",
            requestData	: dsTcfCodeDtlPoolMap.getJSON(),
			callback	: "fnCallback",	
            errCallback	: "fnErrCallback"
        });
	};

	/**************************************************************************
	 * Event: scwin.grdTcfCodeDtlPoolList_oncellclick
	 * 기 능: 공통상세상세코드목록 선택시 처리
	 **************************************************************************/
	scwin.grdTcfCodeDtlPoolList_oncellclick = function(row,col) {
        //선택 ROW값 MOVE
        dsTcfCodeDtlPoolMap.setJSON(dsTcfCodeDtlPoolList.getRowJSON(row));
	};

	/**************************************************************************
	 * Event: scwin.ibxKeyCommCd_onkeyup
	 * 기 능: 공통코드 입력후 Enter시 처리
	 **************************************************************************/
	scwin.ibxKeyCommCd_onkeyup = function(e) {
		if (e.keyCode == '13') {
	   		scwin.btnFind_onclick();
	   	}
	};
	
	/**************************************************************************
	 * Event: scwin.ibxKeyCommCdNm_onkeyup
	 * 기 능: 공통코드명 입력후 Enter시 처리
	 **************************************************************************/
	scwin.ibxKeyCommCdNm_onkeyup = function(e) {
		if (e.keyCode == '13') {
	   		scwin.btnFind_onclick();
	   	}
	};
