head.script:
    /***************************************************************************
	 * Program ID : CF00000002U.xml 
	 * Program Name : 공통코드 관리 
	 * Author : 강상국 (tpksg)
	 * Date : 2019.02.15
	 * 
	 * History 
	 * ======================================== 
	 * 변경일자     	  수정자   내 용 
	 * ======================================== 
	 * 2019.02.15   강상국   최초작성
	 *     
	 **************************************************************************/
	/************************************************************************************
	 *  [1] 전역변수 
	 ************************************************************************************/
	scwin.svcId = "";
	scwin.cdLv = 1;
	scwin.upperCd = "";

	/************************************************************************************
	 *  [2] 초기화 
	 ************************************************************************************/
	scwin.onpageload = function () {
	    com.selDtlCdList("sbxKeyJobGbCd", "CF035", "Y", "Y", "", "", "", "", "", "", "", "", "", "");
	    // 트리 조회
	    scwin.fnTreeList();
	};

	/**************************************************************************
	 *  [3] 사용자정의 함수
	 **************************************************************************/

	/**************************************************************************
	 * Event: scwin.fnRefresh
	 * 기 능: 탭을 선택하면 호출되는 함수(최초 OPEN시에는 호출 안됨)
	 **************************************************************************/
//	scwin.fnRefresh = function () {
//	    //dataCollection 초기화
//	    dsSearchParam.setEmptyValue();
//	    dsTcfCodeList.removeAll();
//	    com.setAuthInfo('CF00000002U');
//	};

	/**************************************************************************
	 * Event: scwin.fnTreeList
	 * 기 능: 공통 트리 조회
	 **************************************************************************/
	scwin.fnTreeList = function () {
	    scwin.svcId = "selectTcfCodeTreeList";
	    ajaxLib.ajax("/cf/CF00000002U/selectTcfCodeTreeList", {
	        mode: "synchronous",
	        method: "POST",
	        requestData: "",
	        callback: "fnCallback",
	        errCallback: "fnErrCallBack"
	    });
	};

	/**************************************************************************
	 * Event: scwin.fnSearch
	 * 기 능: 공통코드 조회
	 **************************************************************************/
	scwin.fnSearch = function () {
	    scwin.svcId = "selectTcfCodeList";
	    ajaxLib.ajax("/cf/CF00000002U/selectTcfCodeListAuthBtn", {
	        mode: "asynchronous",
	        method: "POST",
	        requestData: dsSearchParam.getJSON(),
	        callback: "fnCallback",
	        errCallback: "fnErrCallBack"
	    });
	};

	/**************************************************************************
	 * Event: scwin.fnTreeSearch
	 * 기 능: 공통트리 클릭시 공통코드 검색
	 **************************************************************************/
	scwin.fnTreeSearch = function (keyUpperCd) {
	    dsSearchParam.set("keyUpperCd", keyUpperCd);
	    dsSearchParam.set("keyTreeYn", "Y");
	    
	    scwin.svcId = "selectTcfCodeTreeSearchList";
	    ajaxLib.ajax("/cf/CF00000002U/selectTcfCodeList", {
	        mode: "asynchronous",
	        method: "POST",
	        requestData: dsSearchParam.getJSON(),
	        callback: "fnCallback",
	        errCallback: "fnErrCallBack"
	    });
	};

	/**************************************************************************
	 * Event: scwin.fnComCdPool
	 * 기 능: 공통코드Pool검색 링크 (팝업)으로 이동 시켜야 함
	 **************************************************************************/
	scwin.fnComCdPool = function () {
	    if (scwin.upperCd == '') {
	        com.showMessage("EMW0004", "공통코드 트리를 선택 한 후 공통코드Pool");	// $1을(를) 조회 하십시오.
	        return;
	    }
	    if (scwin.cdLv == 1) {
	        com.showMessage("CFA0014", "레벨 1은 공통코드 POOL 검색");	// $1을(를) 조회할 수 없습니다.
	        return;
	    }
	    var popOps = {
	        popup_name: "공통코드POOL검색",
	        url: "/ux/cf/CF00000003P.xml",
	        data: {},
	        width: "1047",
	        height: "520",
	        callbackFn: "scwin.popCallback"
	    };
	    com.popup_open(popOps);
	};

	/**************************************************************************
	 * Event: scwin.fnSave
	 * 기 능: 공통코드 저장
	 **************************************************************************/
	scwin.fnSave = function () {
	    scwin.svcId = "saveTcfCode";
	    if (confirm("저장 하시겠습니까?")) {
	        ajaxLib.ajax("/cf/CF00000002U/saveTcfCode", {
	            mode: "asynchronous",
                method: "POST",
		        requestData: {
	                param: dsSearchParam.getJSON(),
	                codeList: dsTcfCodeList.getModifiedJSON()
	            },
	            callback: "fnCallback",
	            errCallback: "fnErrCallBack"
	        });
	    }
	};

	/**************************************************************************
	 * Event: scwin.fnAdd
	 * 기 능: 공통코드 추가
	 **************************************************************************/
	scwin.fnAdd = function () {
	    var rowIndex = 0;
	    if (grdTcfCodeList.getFocusedRowIndex() == -1) {
	        rowIndex = dsTcfCodeList.insertRow();
	    } else {
	        rowIndex = grdTcfCodeList.getFocusedRowIndex() + 1;
	        dsTcfCodeList.insertRow(rowIndex);
	    }
	    if (scwin.cdLv == 1) {
	        dsTcfCodeList.setCellData(rowIndex, "upperCd", "*");
	    } else {
	        dsTcfCodeList.setCellData(rowIndex, "upperCd", scwin.upperCd);
	    }
	    grdTcfCodeList.setReadOnly("cell", rowIndex, "cd", false);
	    dsTcfCodeList.setCellData(rowIndex, "cdLv", scwin.cdLv);
	    dsTcfCodeList.setCellData(rowIndex, "useYn", "Y");
		dsTcfCodeList.setCellData(rowIndex, "epntYn", "Y");	    
	};

	/**************************************************************************
	 * Event: scwin.fnDel
	 * 기 능: 공통코드 삭제
	 **************************************************************************/
	scwin.fnDel = function () {

	    if (grdTcfCodeList.getFocusedRowIndex() == -1) {
	        com.showMessage("CFW0004", "삭제할 행");	// $1을(를) 선택하십시오.
	        return;
	    }

	    //행추가 건이면 바로 삭제한다
        var rowIndex = grdTcfCodeList.getFocusedRowIndex();
		if ( strLib.isEmpty(dsTcfCodeList.getCellData(rowIndex, "allCd")) ) {
	        dsTcfCodeList.removeRow(rowIndex);
	        return;
		} 
        
	    //하위코드값이 있는지 체크후 처리한다
	    scwin.svcId = "selectTcfCodeChildCnt";
        dsTcfCodeCnt.set("upperCd", dsTcfCodeList.getCellData(rowIndex, "allCd"));

	    ajaxLib.ajax("/cf/CF00000002U/selectTcfCodeChildCnt", {
	        mode: "asynchronous",
	        method: "POST",
            requestData	: dsTcfCodeCnt.getJSON(),
	        callback: "fnCallback",
	        errCallback: "fnErrCallBack"
	    });
	}

	/**************************************************************************
	 * Event: scwin.fnValid
	 * 기 능: Valid 검사
	 **************************************************************************/
	scwin.fnValid = function () {
	    var cnt = 0;
	    var _idxArr = dsTcfCodeList.getModifiedIndex();

	    //1. 변경된 데이터 validation [status : C,U 만해당]
	    for (var i = 0; i < _idxArr.length; i++) {
	        var _row = _idxArr[i];
	        var obj = dsTcfCodeList.getRowJSON(_row);
	        if (obj.rowStatus == "C" || obj.rowStatus == "U") {
	            if (strLib.isEmpty(obj.cd, false)) {
	                com.showMessage("CFW0007", (_row + 1) + "번째행 [코드] ", "", "", grdTcfCodeList.id, _row, 0);		// $1을(를) 입력하십시오.
	                return false;
	            }
	            if (strLib.isEmpty(obj.cdNm, false)) {
	                com.showMessage("CFW0007", (_row + 1) + "번째행 [코드명] ", "", "", grdTcfCodeList.id, _row, 1);	// $1을(를) 입력하십시오.
	                return false;
	            }
	            if (strLib.isEmpty(obj.useYn, false)) {
	                com.showMessage("CFW0007", (_row + 1) + "번째행 [사용여부] ", "", "", grdTcfCodeList.id, _row, 8);	// $1을(를) 입력하십시오.
	                return false;
	            }
	            if (strLib.isEmpty(obj.epntYn, false)) {
	                com.showMessage("CFW0007", (_row + 1) + "번째행 [말단여부] ", "", "", grdTcfCodeList.id, _row, 9);	// $1을(를) 입력하십시오.
	                return false;
	            }
	            if (strLib.isEmpty(obj.cdOdr, false)) {
	            	dsTcfCodeList.setCellData( _row, "cdOdr", "0");
	            }

			    //중복코드값 체크
				var matchedEa = grdTcfCodeList.getMatchedColumnData("cd" , obj.cd).length;
				if (matchedEa > 1){
	                com.showMessage("CMW0001", "[코드] ", "", "", grdTcfCodeList.id, _row, 0);		// 중복 된 $1가(이) 존재합니다..
	                return false;
				}
	        }
	    }

	    //2. 변경된 데이터 건수 체크				
	    var jsn = dsTcfCodeList.getModifiedJSON();
	    cnt = jsn.length;
	    if (cnt == 0) {
	        com.showMessage("CMW0024");	//	저장(변경)할 내용이 없습니다.
	        return false;
	    }
	    
	    return true;
	};

	/**************************************************************************
	 *  [4] Callback
	 **************************************************************************/
	// 결과 후처리
	scwin.fnCallback = function (result, e) {

	    switch (scwin.svcId) {
	        case "selectTcfCodeTreeList":
	            dsTcfCodeTreeList.setJSON(result.data.codeTreeList);
	            trvTcfCodeList.selectNode(scwin.upperCd);
	            trvTcfCodeList.expandNode(scwin.upperCd);
	            break;
	        case "selectTcfCodeList":
	            scwin.cdLv = 1;
	            scwin.upperCd = "";
	            dsTcfCodeList.setJSON(result.data.codeList);
	            dsCopyTcfCodeList.setJSON(result.data.codeList);
	            break;
	        case "selectTcfCodeTreeSearchList":
	            dsSearchParam.set("keyUpperCd", "");
	            dsSearchParam.set("keyTreeYn", "");
	            dsTcfCodeList.setJSON(result.data.codeList);
	            dsCopyTcfCodeList.setJSON(result.data.codeList);
	            break;
	        case "selectTcfCodeChildCnt":
	            if (result.data.childCnt > 0) {
	                if (confirm("하위레벨이 존재합니다. 하위레벨도 같이 삭제가 됩니다. \n화면에서만 삭제되고, 저장버튼 클릭시 실제로 삭제가 됩니다. \n삭제 하시겠습니까?")) {
				        rowIndex = grdTcfCodeList.getFocusedRowIndex();
				        dsTcfCodeList.removeRow(rowIndex);
	                }
	            } else {
	                if (confirm("화면에서만 삭제되고, 저장버튼 클릭시 실제로 삭제가 됩니다. \n삭제 하시겠습니까?")) {
				        rowIndex = grdTcfCodeList.getFocusedRowIndex();
				        dsTcfCodeList.removeRow(rowIndex);
	                }
	            }
	            break;
	        case "saveTcfCode":
	            com.showMessage("CFA0012"); // 저장되었습니다.
	            scwin.fnTreeList();
	            if (scwin.upperCd == "") {
	                scwin.fnSearch();
	            } else {
	                scwin.fnTreeSearch(scwin.upperCd);
	            }
	            break;
	    }

	};

	// 에러 후처리
	scwin.fnErrCallBack = function (e) {
	    switch (scwin.svcId) {
	        case "selectTcfCodeTreeList":
	            break;
	        case "selectTcfCodeList":
	            break;
	        case "saveTcfCode":
	            break;
	    }
	}

	// 팝업 결과 후처리
	scwin.popCallback = function (arg) {
	    dsTcfCodeDtlPoolList.setJSON(arg);
	    var totCnt = dsTcfCodeDtlPoolList.getTotalRow();

	    if (totCnt > 0) {
	        for (var i = 0; i < totCnt; i++) {
			    //기선택건이 없으면 행추가 한다
				var matchedIdx = grdTcfCodeList.getMatchedIndex("cd", dsTcfCodeDtlPoolList.getCellData(i, "commDtlCd"));
				if( matchedIdx == "" ){
		            var rowIndex = dsTcfCodeList.insertRow();
		            dsTcfCodeList.setCellData(rowIndex, "cd", dsTcfCodeDtlPoolList.getCellData(i, "commDtlCd"));
		            dsTcfCodeList.setCellData(rowIndex, "cdNm", dsTcfCodeDtlPoolList.getCellData(i, "commDtlCdNm"));
		            dsTcfCodeList.setCellData(rowIndex, "upperCd", scwin.upperCd);
		            dsTcfCodeList.setCellData(rowIndex, "useYn", "Y");
		            dsTcfCodeList.setCellData(rowIndex, "cdLv", scwin.cdLv);
		            dsTcfCodeList.setCellData(rowIndex, "commCd", dsTcfCodeDtlPoolList.getCellData(i, "commCd"));
		            dsTcfCodeList.setCellData(rowIndex, "commDtlCd", dsTcfCodeDtlPoolList.getCellData(i, "commDtlCd"));
				}
	        }
	    }
	};

	/************************************************************************************
	 *  [5] Event 함수 
	 ************************************************************************************/
	/**************************************************************************
	 * Event: scwin.btnFind_onclick
	 * 기 능: 조회 버튼
	 **************************************************************************/
	scwin.btnFind_onclick = function () {
	    scwin.fnSearch();
	};

	/**************************************************************************
	 * Event: scwin.trvTcfCodeList_onlabelclick
	 * 기 능: 공통 트리 클릭
	 **************************************************************************/
	scwin.trvTcfCodeList_onlabelclick = function (value, nodeElement, index) {
	    scwin.cdLv = parseInt(dsTcfCodeTreeList.getCellData(index - 1, "cdLv")) + 1;
	    scwin.upperCd = value;
	    //트리클릭시 공통코드 조회
	    scwin.fnTreeSearch(scwin.upperCd);
	};

	/**************************************************************************
	 * Event: scwin.grdTcfCodeList_onviewchange
	 * 기 능: 공통 상세코드 그리드 셀 변경
	 **************************************************************************/
	scwin.grdTcfCodeList_onviewchange = function (info) {
	    if (info.colIndex == 0) {
	        if (info.oldValue != info.newValue && info.newValue != '') {
	            dsTcfCodeList.setCellData(info.rowIndex, "commDtlCd", info.newValue);
	            dsTcfCodeList.setCellData(info.rowIndex, "commCd", "");
	        }
	    }
	};

	/**************************************************************************
	 * Event: scwin.btnFindPool_onclick
	 * 기 능: 공통코드Pool 검색 버튼
	 **************************************************************************/
	scwin.btnFindPool_onclick = function () {
	    scwin.fnComCdPool();
	};

	/**************************************************************************
	 * Event: scwin.btnSave_onclick
	 * 기 능: 저장 버튼
	 **************************************************************************/
	scwin.btnSave_onclick = function () {
	    if (!scwin.fnValid()) {
	        return;
	    }
	    scwin.fnSave();
	};

	/**************************************************************************
	 * Event: scwin.btnAddRow_onclick
	 * 기 능: 추가 버튼
	 **************************************************************************/
	scwin.btnAddRow_onclick = function () {
	    scwin.fnAdd();
	};

	/**************************************************************************
	 * Event: scwin.btnDelRow_onclick
	 * 기 능: 삭제 버튼
	 **************************************************************************/
	scwin.btnDelRow_onclick = function () {
	    scwin.fnDel();
	};

	/**************************************************************************
	 * Event: scwin.ibxCommCd_onkeyup
	 * 기 능: 공통코드 입력후 Enter시 처리
	 **************************************************************************/
	scwin.ibxCommCd_onkeyup = function (e) {
	    if (e.keyCode == '13') {
	        scwin.fnSearch();
	    }
	};

	/**************************************************************************
	 * Event: scwin.ibxCommCdNm_onkeyup
	 * 기 능: 공통코드명 입력후 Enter시 처리
	 **************************************************************************/
	scwin.ibxCommCdNm_onkeyup = function (e) {
	    if (e.keyCode == '13') {
	        scwin.fnSearch();
	    }
	};
