head.script:
    /***************************************************************************
     * Program ID : CF00250000U.xml 
     * Program Name : 메시지 관리 
     * Author : 강상국 (tpksg)
     * Date : 2019.02.15
     * 
     * History 
     * ======================================== 
     * 변경일자     	  수정자   내 용 
     * ======================================== 
     * 2019.02.15   강상국   최초작성
     *     
     **************************************************************************/

    /************************************************************************************
     *  [1] 전역변수 
     ************************************************************************************/
    scwin.svcId = "";
    scwin.ingStatus = "";

    /************************************************************************************
     *  [2] 초기화 
     ************************************************************************************/
    scwin.onpageload = function () {
        // 업무구분[CF035],// 메세지유형[CF036]
        var opt = [
                    {"CF035": ["sbxKeyJobGbCd", "sbxJjobGbCd", "grdTcfMsgInfoList:jobGbCd"]}
                  , {"CF036": ["sbxKeyMsgTypeCd", "sbxMsgTypeCd", "grdTcfMsgInfoList:msgTypeCd"]}
                  ];
        // 1. callback 처리가 필요없는 경우
        com.setCdComp(opt, scwin.cmmCb);        
    };

    /**************************************************************************
     *  [3] 사용자정의 함수
     **************************************************************************/

    /**************************************************************************
     * Event: scwin.fnRefresh
     * 기 능: 탭을 선택하면 호출되는 함수(최초 OPEN시에는 호출 안됨)
     **************************************************************************/
    scwin.fnRefresh = function () {
        //dataCollection 초기화
        dsSearchParam.setEmptyValue();
        com.setAuthInfo('CF00250000U');
        com.authBtnCtrl();
        scwin.fnSearch();
    }

    /**************************************************************************
     * Event: scwin.fnSearch
     * 기 능: 메시지 조회
     **************************************************************************/
    scwin.fnSearch = function () {
        scwin.svcId = "searchTcfMsgInfo";
   
        ajaxLib.ajax("/cf/CF00250000U/selectTcfMsgInfoVOList", {
            mode: "asynchronous",
            method: "POST",
            requestData: dsSearchParam.getJSON(),
            callback: "fnCallback",
            errCallback: "fnErrCallBack"
        });
    };

    /**************************************************************************
     * Event: scwin.fnNew
     * 기 능: 메시지 신규
     **************************************************************************/
    scwin.fnNew = function () {
        scwin.svcId = "createTcfMsgInfo";
        if (confirm("등록 하시겠습니까?")) {
            ajaxLib.ajax("/cf/CF00250000U/postTcfMsgInfoVO", {
                mode: "asynchronous",
                method: "POST",
                requestData: dsViewParam.getJSON(),
                callback: "fnCallback",
                errCallback: "fnErrCallBack"
            });
        }
    };

    /**************************************************************************
     * Event: scwin.fnModify
     * 기 능: 메시지 수정
     **************************************************************************/
    scwin.fnModify = function () {
        scwin.svcId = "updateTcfMsgInfo";
        if (confirm("수정 하시겠습니까?")) {
           
            ajaxLib.ajax("/cf/CF00250000U/putTcfMsgInfoVO", {
                mode: "asynchronous",
                method: "POST",
                requestData: dsViewParam.getJSON(),
                callback: "fnCallback",
                errCallback: "fnErrCallBack"
            });
        }
    };

    /**************************************************************************
     * Event: scwin.fnDelete
     * 기 능: 메시지 삭제
     **************************************************************************/
    scwin.fnDelete = function () {
        scwin.svcId = "deleteTcfMsgInfo";
        if (confirm("삭제 하시겠습니까?")) {
            ajaxLib.ajax("/cf/CF00250000U/deleteTcfMsgInfoVO", {
                mode: "asynchronous",
                method: "Delete",
                requestData: dsViewParam.getJSON(),
                callback: "fnCallback",
                errCallback: "fnErrCallBack"
            });
        }
    }

    /**************************************************************************
     * Event: scwin.fnValid
     * 기 능: Valid 검사
     **************************************************************************/
    scwin.fnValid = function () {
        if (strLib.isEmpty(sbxJjobGbCd.getValue(), false)) {
            alert('업무구분을 선택 하세요.');
            sbxJjobGbCd.focus();
            return false;
        }
        if (strLib.isEmpty(sbxMsgTypeCd.getValue(), false)) {
            alert('메세지유형을 선택 하세요.');
            sbxMsgTypeCd.focus();
            return false;
        }
        
        if (strLib.isEmpty(txaMsgCn.getValue(), false)) {
            alert('메세지 내용을 입력 하세요.');
            txaMsgCn.focus();
            return false;
        }
        return true;
    };

    /**************************************************************************
     *  [4] Callback
     **************************************************************************/
    // 결과 후처리
    scwin.fnCallback = function (result, e) {
        switch (scwin.svcId) {
            case "searchTcfMsgInfo":
            	console.log(result.data.msgList);
                dsTcfMsgInfoList.setJSON(result.data.msgList);
                com.setUdcValue("dsTcfMsgInfoListTotalCount", "udcListCount", dsTcfMsgInfoList.getTotalRow());
                scwin.ingStatus = "S";
                break;

            case "createTcfMsgInfo":
                dsViewParam.setEmptyValue();
                alert('저장되었습니다.');
                scwin.ingStatus = "";
                grpMsgView.init();
                scwin.fnSearch();
                break;

            case "updateTcfMsgInfo":
                dsViewParam.setEmptyValue();
                alert('수정되었습니다.');
                scwin.ingStatus = "";
                grpMsgView.init();
                scwin.fnSearch();
                break;

            case "deleteTcfMsgInfo":
                dsViewParam.setEmptyValue();
                alert('삭제되었습니다.');
                scwin.ingStatus = "";
                grpMsgView.init();
                scwin.fnSearch();
                break;
        }
    };

    // 에러 후처리
    scwin.fnErrCallBack = function (e) {
        switch (scwin.svcId) {
            case "searchTcfMsgInfo":
                break;
            case "createTcfMsgInfo":
                break;

            case "updateTcfMsgInfo":
                break;

            case "deleteTcfMsgInfo":
                break;
        }
    };

    /************************************************************************************
     *  [5] Event 함수 
     ************************************************************************************/
    /**************************************************************************
     * Event: scwin.btnFind_onclick
     * 기 능: 조회 버튼
     **************************************************************************/
    scwin.btnFind_onclick = function () {
        scwin.fnSearch();
    };

    /**************************************************************************
     * Event: scwin.grdTcfMsgInfoList_onrowindexchange
     * 기 능: 메시지 목록 그리드 Row 변경
     **************************************************************************/
    scwin.grdTcfMsgInfoList_onrowindexchange = function (row, oldRow) {
        var data = dsTcfMsgInfoList.getRowJSON(row);
        sbxJjobGbCd.setValue(data.jobGbCd);
        sbxMsgTypeCd.setValue(data.msgTypeCd);
        ibxMsgCd.setValue(data.msgCd);
        txaMsgCn.setValue(data.msgCn);
        txaRefrCn.setValue(data.refrCn);
        dsViewParam.set("whrJobGbCd", data.jobGbCd);
        dsViewParam.set("whrMsgTypeCd", data.msgTypeCd);
        dsViewParam.set("whrMsgCd", data.msgCd);
        scwin.ingStatus = "V";
    };

    /**************************************************************************
     * Event: scwin.btnNew_onclick
     * 기 능: 신규 버튼
     **************************************************************************/
    scwin.btnNew_onclick = function () {
        if (!scwin.fnValid()) {
            return;
        }
        scwin.fnNew();
    };

    /**************************************************************************
     * Event: scwin.btnSave_onclick
     * 기 능: 저장 버튼
     **************************************************************************/
    scwin.btnSave_onclick = function () {
    
    	if( ibxMsgCd.getValue() != "" ){
    		// 수정
	        if (scwin.ingStatus != "V") {
	            alert('목록에서 수정 할 데이터를 선택 하세요.');
	            return;
	        }
	        if (!scwin.fnValid()) {
	            return;
	        }
	        scwin.fnModify();    		
    	
    	}else{
    		// 등록
    		if (!scwin.fnValid()) {
            	return;
        	}
        	scwin.fnNew();
    	}
    };

    /**************************************************************************
     * Event: scwin.btnDelete_onclick
     * 기 능: 삭제 버튼
     **************************************************************************/
    scwin.btnDelete_onclick = function () {
        if (scwin.ingStatus != "V") {
            alert('목록에서 삭제 할 데이터를 선택 하세요.');
            return;
        }
        scwin.fnDelete();
    };

    /**************************************************************************
     * Event: scwin.btnInit_onclick
     * 기 능: 초기화 버튼
     **************************************************************************/
    scwin.btnInit_onclick = function () {
        dsViewParam.setEmptyValue();
    };

    /**************************************************************************
     * Event: scwin.ibxKeyMsgCd_onkeyup
     * 기 능: 메시지 코드 엔터 이벤트
     **************************************************************************/
    scwin.ibxKeyMsgCd_onkeyup = function (e) {
        if (e.keyCode == 13) {
            scwin.fnSearch();
        }
    };

    /**************************************************************************
     * Event: scwin.ibxKeyMsgCn_onkeyup
     * 기 능: 메세지내용 엔터 이벤트
     **************************************************************************/
    scwin.ibxKeyMsgCn_onkeyup = function (e) {
        if (e.keyCode == 13) {
            scwin.fnSearch();
        }
    };
