    /************************************************************************************ 
     *  [1] onpageload
     ************************************************************************************/
    scwin.onpageload = function () {

        //SelectBox 코드값 맵핑 
        com.selCdList("sbxKeyEnprGbCd", "CF009", "Y");		//기업구분[CF009]
        com.selCdList("sbxEnprGbCd", "CF009", "Y");			//기업구분[CF009]
        com.selCdList("sbxEnprFormCd", "CF010", "Y");		//기업형태[CF010]
        com.selCdList("sbxEnprScalCd", "CF011", "Y");		//기업규모[CF011]
        com.selCdList("sbxBankCd", "CF034", "Y");           //은행코드[CF034]
        //com.selCdList("sbxTradeVendrTypeCd", "CF082", "Y");	//거래처업체유형코드[CF082]
        //com.selCdList("sbxTaxnTypeCd", "CF073", "Y");		//과세유형코드[CF073]
        //com.selCdList("sbxChgbfTaxnTypeCd", "CF073", "Y");	//변경전_과세유형코드[CF073]

        //주소창 크기 지정
        com.getUdcComp("udcAddr",  "addrBasic").setStyle("width",  "48%");
        com.getUdcComp("udcAddr",  "addrDetail").setStyle("width",  "182px");
        //전화번호, 팩스번호 크기 지정
        com.getUdcComp("udcTelNo",  "telNo").setStyle("width",  "100%");
        com.getUdcComp("udcFaxNo",  "telNo").setStyle("width",  "100%");

        //필수값 지정
        com.getUdcComp("udcICnumber", "icNumber").addClass("req");	//주민등록번호UDC_Input 필수처리
        com.getUdcComp("udcBusiness", "bizNo").addClass("req");		//사업자등록번호UDC_Input 필수처리
        com.getUdcComp("udcDept", "deptNm").addClass("req");		//부서UDC_Input 필수처리
        com.getUdcComp("udcSite", "siteNm").addClass("req");		//현장UDC_Input 필수처리
        com.getUdcComp("udcUser", "userNm").addClass("req");		//사원UDC_Input 필수처리
        com.getUdcComp("udcTelNo", "telNo").addClass("req");		//전화번호UDC_Input 필수처리
        
        //문제거래처 Default 값 "N"
        ibxQstTradeYn.setValue( "N" );
        
        //UDC Display 변경
        scwin.fnDisplayUdc("");

        //UDC 초기화
        scwin.fnInitUdc();
        labRegNo.setLabel("기업구분선택");

		//삭제버튼 hide
   		btnDel.addClass( "hide" );
    };

    /************************************************************************************
     *  [1] 초기화 
     ************************************************************************************/
    // 화면 초기화(필수)
    scwin.initScreen = function () {
        newData = true;				//신규 데이터

        dsSrchUseGrp.reset();		//거래처정보 조회KEY
        dsTcfCodeList.clear();		//거래처목록
        dsTradeInfo.reset();		//거래처정보
        dsUseGrpList.clear();		//업무선택목록

		grdTradeList.initGrid();	//거래처목록 초기화
        grpTradeInfo.init();		//거래처정보 영역 초기화
        grdUseGrpList.initGrid();	//업무선택목록 초기화

        sbxEnprGbCd.setReadOnly(false);	//기업구분 ReadOnly 풀기
        labRegNo.setLabel("기업구분선택");
        com.setUdcValue("udcListCount", "udcListCount", 0);
    	pageRowStart = 0;			//페이지 시작행번호
		//구주소 hide처리
        labAddr.setLabel("사업장소재지");
   		ibxOldAddr.addClass( "hide" );	//구주소 hide
		ibxOldAddr.setValue( "" );
		//법인번호 필수처리 RemoveClass처리
		labCorpRegNo.removeClass( "req" );
		ibxCorpRegNo.removeClass( "req" );
		//대표자명 필수처리 RemoveClass처리
		labReprNm.removeClass( "req" );
		ibxReprNm.removeClass( "req" );
		//사업장소재지 필수처리 RemoveClass처리
		labAddrClass.removeClass( "req" );
        com.getUdcComp("udcAddr", "zipNo").removeClass("req");
		//업태 필수처리 RemoveClass처리
		labBzcdNm.removeClass( "req" );
		ibxBzcdNm.removeClass( "req" );
		//종목 필수처리 RemoveClass처리
		labBztpNm.removeClass( "req" );
		ibxBztpNm.removeClass( "req" );
		//설립일자 필수처리 RemoveClass처리
		labFoundDt.removeClass( "req" );
		calFoundDt.removeClass( "req" );
    };

    /************************************************************************************
     *  [2] 전역변수 
     ************************************************************************************/
    var svcId = "";
    var ingStatus = "";
    var newData = true; 	//신규 데이터
    var pageRowStart = 0; 	//페이지 시작행번호
    var pageRowCount = 50; 	//페이지 요청건수

    /************************************************************************************
     *  [3] Event 함수 
     ************************************************************************************/
    // 거래처코드 입력 후 엔터
	scwin.keyTradeCd_onkeyup = function(e) {
        if (e.keyCode == 13) {
            btnFind.trigger("onClick");
        }
	};

    // 거래처명 입력 후 엔터
    scwin.ibxKeyTradeNm_onkeyup = function (e) {
        if (e.keyCode == 13) {
            btnFind.trigger("onClick");
        }
    };

    // 사업자번호 입력 후 엔터
    scwin.ibxKeyBzprRegNo_onkeyup = function (e) {
        if (e.keyCode == 13) {
            btnFind.trigger("onClick");
        }
    };

    // 법인번호 입력 후 엔터
    scwin.ibxKeyCorpRegNo_onkeyup = function (e) {
        if (e.keyCode == 13) {
            btnFind.trigger("onClick");
        }
    };
    
    // 조회 버튼
    scwin.btnFind_onclick = function () {
    	//초기화
        scwin.initScreen();

		dsSrch.set("pageRowStart", pageRowStart);
		dsSrch.set("pageRowCount", pageRowCount);
		
		//조회
        scwin.fnSearchList();
    };

    // scrollend시 처리
	scwin.grdTradeList_onscrollend = function() {
		//총건수가 (페이지 시작행번호 + 페이지 요청건수) 보다 작으면 SKIP
		if (dsTradeList.getTotalRow() < (dsSrch.get("pageRowStart") + dsSrch.get("pageRowCount"))){
			return;
		}

		dsSrch.set("pageRowStart", pageRowStart);
		dsSrch.set("pageRowCount", pageRowCount);
		
		//조회
        scwin.fnSearchList();
	};

    // 그리드 row 선택 시
    scwin.grdTradeList_oncellclick = function (row, col) {
        ingStatus = "V";

        var data = dsTradeList.getRowJSON(row);
        var tradeCd = dsTradeList.getCellData(row, "tradeCd");
        // Binding
        dsTradeInfo.setJSON(data)
        dsTradeInfo.set( "mbzplBzprRegNo" , data.mbzplBzprRegNo );

        // 전화번호 설정
        com.setUdcValue("udcTelNo", "telNo", dsTradeInfo.get("telNo"));	    //전화번호

        // 팩스번호 설정
        com.setUdcValue("udcFaxNo", "telNo", dsTradeInfo.get("faxNoCn")); 	//팩스번호

        // 주소정보 설정
        if (dsTradeInfo.get("zipCd").length > 5) {
//추후사용가능			com.showMessage("CFA0013", "사업장소재지");		//$1이(가) 구주소입니다. 신주소로 바꿔주십시요.
	    	com.setUdcValue("udcAddr", "zipNo", ""); 		//우편번호
			com.setUdcValue("udcAddr", "addrBasic", ""); 	//기본 주소
			//구주소 show처리
	        labAddr.setLabel("사업장소재지<br>(구주소)");
			ibxOldAddr.removeClass( "hide" );	//구주소 show
			var oldAddr = "[" + dsTradeInfo.get("zipCd") + "] " + dsTradeInfo.get("baseAddr") + " " + dsTradeInfo.get("dtlAddr");
			ibxOldAddr.setValue( oldAddr );
        } else {
			com.setUdcValue("udcAddr", "zipNo", dsTradeInfo.get("zipCd")); 			//우편번호
			com.setUdcValue("udcAddr", "addrBasic", dsTradeInfo.get("baseAddr")); 	//기본 주소
			//구주소 hide처리
	        labAddr.setLabel("사업장소재지");
	   		ibxOldAddr.addClass( "hide" );	//구주소 hide
			ibxOldAddr.setValue( "" );
        }
        com.setUdcValue("udcAddr", "addrDetail", dsTradeInfo.get("dtlAddr")); 		//상세 주소

        // 기존 데이터 선택
        newData = false;

	    // 그리드 row 선택시 기업구분별 값 설정
	    scwin.fnRowSelSet();

        // 업무선택 조회
        dsSrchUseGrp.set("tradeCd", tradeCd);
        scwin.fnSearchUseGrp();
    };

    // 기업구분 변경
    scwin.sbxEnprGbCd_onchange = function () {
        // 입력항목 변경
        scwin.fnInputControl();
    };

    // 신규 버튼
    scwin.btnNew_onclick = function () {
        newData = true;					//신규 데이터

        dsTradeInfo.reset();			//거래처정보
        dsUseGrpList.clear();			//업무선택목록
        grpTradeInfo.init();			//거래처정보 영역 초기화
        grdUseGrpList.initGrid();		//업무선택목록 초기화
        sbxEnprGbCd.setReadOnly(false);	//기업구분 ReadOnly 풀기
        //UDC 초기화
        scwin.fnInitUdc();
        labRegNo.setLabel("기업구분선택");
        ibxQstTradeYn.setValue( "N" );  //문제거래처 "N"
    };

    // 저장 버튼
    scwin.btnSave_onclick = function () {
        scwin.fnSave();
    };

    // 삭제 버튼
    scwin.btnDel_onclick = function () {
        scwin.fnDelete();
    };

    // 업무선택 버튼
    scwin.btnConfirm_onclick = function () {
        var pRowIndex = 0;

        if (newData != true) {
            pRowIndex = grdTradeList.getFocusedRowIndex();

            if (pRowIndex == -1) {
				com.showMessage("CFW0004", "거래처 목록");			//$1을(를) 선택하십시오.
                return;
            } else {
                scwin.fnComCdPop();
            }
        } else {
            scwin.fnComCdPop();
        }
    };

    // 행삭제 버튼
    scwin.btnDelRow_onclick = function () {
        var cRowIndex = grdUseGrpList.getFocusedRowIndex();
        dsUseGrpList.removeRow(cRowIndex);
    };

    /**************************************************************************
     *  [4] Callback
     **************************************************************************/
    // 결과 후처리
    scwin.fnCallback = function (result, e) {

        switch (svcId) {
            case "selectTcfTradeInfoPageList":
                dsTradeList.setJSON(result.data.dsTcfTradeInfoList, true);
                com.setUdcValue("udcListCount", "udcListCount", dsTradeList.getTotalRow());
                pageRowStart = pageRowStart + pageRowCount;		//다음 조회갯수 Setting

                // UDC 초기화
                scwin.fnInitUdc();

                ingStatus = "S";
                break;

            case "searchTcfTradeInfo":
                dsTradeInfo.setJSON(result.data.dsTcfTradeInfo);
                ingStatus = "S";
                break;

            case "saveTcfTradeInfo":
				com.showMessage("SCN0002", "저장");			//$1 되었습니다.
                ingStatus = "";

                scwin.initScreen();
                scwin.fnSearchList();
                break;

            case "deleteTcfTradeInfo":
                dsTradeInfo.setEmptyValue();
				com.showMessage("SCN0002", "삭제");			//$1 되었습니다.
                ingStatus = "";
                scwin.fnSearchList();
                break;

            case "selectTcfTradeUseGrpList":
                dsUseGrpList.setJSON(result.data.dsTcfTradeUseGrpList);
                ingStatus = "S";
                break;
        }
    };

    // 에러 후처리
    scwin.fnErrCallBack = function (e) {
//        console.log(e);
    };

    // 팝업 결과 후처리
    scwin.popCallback = function (arg) {
        dsTcfCodeList.setJSON(arg);
        var totCnt = dsTcfCodeList.getTotalRow();
        var cRowIndex = 0;

        for (var i = 0; i < totCnt; i++) {
		    //기선택건이 없으면 행추가 한다
			var matchedIdx = grdUseGrpList.getMatchedIndex("useGrpCd", dsTcfCodeList.getCellData(i, "cd"));
			if( matchedIdx == "" ){
	            cRowIndex = dsUseGrpList.insertRow();
	            dsUseGrpList.setCellData(cRowIndex, "useGrpCd", dsTcfCodeList.getCellData(i, "cd"));
	            dsUseGrpList.setCellData(cRowIndex, "useGrpCdNm", dsTcfCodeList.getCellData(i, "cdNm"));
			}
        }
    };
	
    /**************************************************************************
     *  [5] 사용자정의 함수
     **************************************************************************/
    // 조회 List
    scwin.fnSearchList = function () {

        svcId = "selectTcfTradeInfoPageList";

        ajaxLib.ajax("/cf/CF00300000U/selectTcfTradeInfoPageList", {
            mode: "asynchronous",
            method: "POST",
            requestData: dsSrch.getJSON(),
            callback: "fnCallback",
            errCallback: "fnErrCallBack"
        });
    };

    // 저장
    scwin.fnSave = function () {
        var sGbCd = sbxEnprGbCd.getValue();

        /***********************
         * udc 데이터 가져오기
         ***********************/
        if (sGbCd == "CF00901") { // 개인
            dsTradeInfo.set("secResno", com.getUdcValue("udcICnumber", "icNumber"));	//주민등록번호
            dsTradeInfo.set("bzprRegNo", dsTradeInfo.get("secResno").substr(0, 7));		//사업자등록번호(주민등록번호 앞 7자)

        } else if (sGbCd == "CF00902") { 
//        || //법인사업자 sGbCd == "CF00903") { //개인사업자
            dsTradeInfo.set("bzprRegNo", com.getUdcValue("udcBusiness", "bizNo"));	           //사업자등록번호
            dsTradeInfo.set("mbzplBzprRegNo", com.getUdcValue("udcMbzplBzprRegNo", "bizNo"));  //본점사업자번호
        } else if (sGbCd == "CF00909") { //기타
            dsTradeInfo.set("bzprRegNo", ibxRegNo10.getValue());	//기타사업자번호(임의10자리)
        }
//        } else if (sGbCd == "CF00904")   //부서
//		                               {  
//            dsTradeInfo.set("bzprRegNo", com.getUdcValue("udcDept", "deptCd")); 	//부서코드
//
//        } else if (sGbCd == "CF00905")   //현장
//		                               {  
//            dsTradeInfo.set("bzprRegNo", com.getUdcValue("udcSite", "siteCd")); 	//현장코드
//
//        } else if (sGbCd == "CF00906") { //사원
//            dsTradeInfo.set("bzprRegNo", com.getUdcValue("udcUser", "userEmpno"));	//사원번호
//
//        } else if (sGbCd == "CF00907") { //지자체
//            dsTradeInfo.set("bzprRegNo", ibxRegNo4.getValue());		//지자체번호(4자리)
//
//        } else if (sGbCd == "CF00909") { //기타
//            dsTradeInfo.set("bzprRegNo", ibxRegNo10.getValue());	//기타사업자번호(임의10자리)
//        }

        dsTradeInfo.set("telNo", com.getUdcValue("udcTelNo", "telNo"));		    //전화번호
        dsTradeInfo.set("faxNoCn", com.getUdcValue("udcFaxNo", "telNo"));		//팩스번호
        dsTradeInfo.set("zipCd", com.getUdcValue("udcAddr", "zipNo"));			//우편번호
        dsTradeInfo.set("baseAddr", com.getUdcValue("udcAddr", "addrBasic"));	//기본 주소
        dsTradeInfo.set("dtlAddr", com.getUdcValue("udcAddr", "addrDetail"));   //상세 주소

        // 데이터 검증
        if (!scwin.fnValidateData()) {
            return;
        }

        svcId = "saveTcfTradeInfo";
        if (confirm("저장 하시겠습니까?")) {
            ajaxLib.ajax("/cf/CF00300000U/saveTcfTradeInfo", {
                mode: "asynchronous",
                method: "POST",
                requestData: {
                    "tradeInfoVO": dsTradeInfo.getJSON()			// 거래처정보
                   ,"tradeUseGrpListVO": dsUseGrpList.getAllJSON()	// 업무선택목록
                }
                ,
                callback: "fnCallback",
                errCallback: "fnErrCallBack"
            });
        }
    };

    // 삭제
    scwin.fnDelete = function () {
        svcId = "deleteTcfTradeInfo";
        if (confirm("삭제 하시겠습니까?")) {

            ajaxLib.ajax("/cf/CF00300000U/deleteTcfTradeInfo", {
                mode: "asynchronous",
                method: "Delete",
                requestData: dsTradeInfo.getJSON(),
                callback: "fnCallback",
                errCallback: "fnErrCallBack"
            });
        }
    };

    // 업무선택조회
    scwin.fnSearchUseGrp = function () {
        svcId = "selectTcfTradeUseGrpList";

        ajaxLib.ajax("/cf/CF00300000U/selectTcfTradeUseGrpList", {
            mode: "asynchronous",
            method: "POST",
            requestData: dsSrchUseGrp.getJSON(),
            callback: "fnCallback",
            errCallback: "fnErrCallBack"
        });
    };

    // 공통코드 팝업 호출
    scwin.fnComCdPop = function () {
        var popOps = {
            popup_name: "공통코드 검색",
            url: "/ux/cf/CF00000004P.xml",
            data: {
                upperCd: "CF048",
                useYn: "Y"
            }
            //,width:"1047"
            ,
            widType: "M",
            height: "520",
            callbackFn: "scwin.popCallback"
        };
        com.popup_open(popOps);
    };

    // 은행계좌 확인
    scwin.btnBank_onclick = function() {
        if( ibxTradeCd.getValue() != "" ) {
            var popOps = {
                    pid: "TmSelPopup"+"_" +new Date().getTime()
                    ,url:"/ux/cf/CF00300004P.xml"
                    ,modal : true
                    ,popup_name:"은행계좌 확인"
                    ,widType : "M"
                    ,height : "520"
                    ,data: {
                          tradeCd: ibxTradeCd.getValue()
                         ,tradeNm: ibxTradeNm.getValue()
                     }
            };
            com.popup_open(popOps);
        }
    };

    // 기업구분에 따른 입력항목 변경
    scwin.fnInputControl = function () {
	    //그리드 row 선택시는 SKIP
		if (ingStatus == "V") {
			return;
		}
		
        var sGbCd = sbxEnprGbCd.getValue();

        //UDC 초기화
        scwin.fnInitUdc();

        //기업구분에 따른 분기처리
        if (sGbCd == "CF00901") { //개인
            labRegNo.setLabel("주민등록번호");
            scwin.fnDisplayUdc("udcICnumber");
            com.setUdcValue("udcICnumber", "icNumber", dsTradeInfo.get("secResno")); //주민등록번호
            ibxMbzplYn.setValue( "Y" );                        //주사업장여부 "Y"
            com.setUdcValue("udcMbzplBzprRegNo", "bizNo", ""); //본점사업자등록번호
            com.getUdcComp("udcMbzplBzprRegNo", "bizNo").setReadOnly(true);
        } else if (sGbCd == "CF00902" || //법인사업자
