    // UDC Display
    scwin.fnDisplayUdc = function (arg) {
        com.getUdcComp("udcICnumber",  "icNumber").hide();	//주민등록번호 UDC
        com.getUdcComp("udcDept",  "deptNm").hide();		//부서 UDC
        com.getUdcComp("udcDept",  "btnSrch").hide();		//부서 UDC
        com.getUdcComp("udcDept",  "deptCd").hide();		//부서 UDC
        com.getUdcComp("udcSite",  "siteNm").hide();		//현장 UDC
        com.getUdcComp("udcSite",  "btnSrch").hide();		//현장 UDC
        com.getUdcComp("udcSite",  "siteCd").hide();		//현장 UDC        
        com.getUdcComp("udcUser",  "userNm").hide();		//사용자 UDC
        com.getUdcComp("udcUser",  "btnSrch").hide();		//사용자 UDC
        com.getUdcComp("udcUser",  "userId").hide();		//사용자 UDC
        com.getUdcComp("udcUser",  "userEmpno").hide();		//사용자 UDC
        com.getUdcComp("udcBusiness",  "bizNo").hide();		//사업자등록번호 UDC
   		ibxRegNo4.addClass( "hide" );						//지자체번호(4자리)
   		ibxRegNo10.addClass( "hide" );						//기타사업자등록번호(임의10자리)
		
        if (arg == "udcICnumber") {
            com.getUdcComp(arg, "icNumber").show();	//주민등록번호 UDC

        } else if (arg == "udcDept") {
            com.getUdcComp(arg, "deptNm").show("");		//부서 UDC
            com.getUdcComp(arg, "btnSrch").show("");	//부서 UDC
            com.getUdcComp(arg, "deptCd").show("");		//부서 UDC

        } else if (arg == "udcSite") {
            com.getUdcComp(arg, "siteNm").show("");		//현장 UDC
            com.getUdcComp(arg, "btnSrch").show("");	//현장 UDC
            com.getUdcComp(arg, "siteCd").show("");		//현장 UDC

        } else if (arg == "udcUser") {
            com.getUdcComp(arg, "userNm").show("");		//사용자 UDC
            com.getUdcComp(arg, "btnSrch").show("");	//사용자 UDC
            com.getUdcComp(arg, "userId").show("");		//사용자 UDC
            com.getUdcComp(arg, "userEmpno").show("");	//사용자 UDC

        } else if (arg == "udcBusiness") {
            com.getUdcComp(arg, "bizNo").show();		//사업자등록번호 UDC

        } else if (arg == "ibxRegNo4") {
			ibxRegNo4.removeClass( "hide" );			//지자체번호(4자리)

        } else if (arg == "ibxRegNo10") {
			ibxRegNo10.removeClass( "hide" );			//기타사업자번호(임의10자리)
        }
    };

    // UDC 초기화
    scwin.fnInitUdc = function () {
        com.getUdcComp("udcICnumber", "icNumber").setReadOnly(false);
        com.getUdcComp("udcBusiness", "bizNo").setReadOnly(false);
        com.getUdcComp("udcDept", "deptNm").setReadOnly(false);
        com.getUdcComp("udcDept", "btnSrch").setDisabled(false);
        com.getUdcComp("udcSite", "siteNm").setReadOnly(false);
        com.getUdcComp("udcSite", "btnSrch").setDisabled(false);        
        com.getUdcComp("udcUser", "userNm").setReadOnly(false);
        com.getUdcComp("udcUser", "btnSrch").setDisabled(false);
        ibxRegNo4.setReadOnly(false);
        ibxRegNo10.setReadOnly(false);

        com.setUdcValue("udcICnumber", "icNumber", "");	//주민등록번호
        com.setUdcValue("udcBusiness", "bizNo", "");	//사업자등록번호
        com.setUdcValue("udcDept", "deptNm", "");		//부서명
        com.setUdcValue("udcDept", "deptCd", "");		//부서코드
        com.setUdcValue("udcSite", "siteNm", "");		//현장명
        com.setUdcValue("udcSite", "siteCd", "");		//현장코드        
        com.setUdcValue("udcUser", "userNm", "");		//사원명
        com.setUdcValue("udcUser", "userId", "");		//사원아이디
        com.setUdcValue("udcUser", "userEmpno", "");	//사원사번
        ibxRegNo4.setValue("");							//지자체번호(4자리)
        ibxRegNo10.setValue("");						//기타사업자번호(임의10자리)
		//법인번호 필수처리 RemoveClass처리
		labCorpRegNo.removeClass( "req" );
		ibxCorpRegNo.removeClass( "req" );
		//대표자명 필수처리 RemoveClass처리
		labReprNm.removeClass( "req" );
		ibxReprNm.removeClass( "req" );
		//사업장소재지 필수처리 RemoveClass처리
		labAddrClass.removeClass( "req" );
        com.getUdcComp("udcAddr", "zipNo").removeClass("req");
		//업태 필수처리 RemoveClass처리
		labBzcdNm.removeClass( "req" );
		ibxBzcdNm.removeClass( "req" );
		//종목 필수처리 RemoveClass처리
		labBztpNm.removeClass( "req" );
		ibxBztpNm.removeClass( "req" );
		//설립일자 필수처리 RemoveClass처리
		labFoundDt.removeClass( "req" );
		calFoundDt.removeClass( "req" );
        //주사업장여부 필수처리 RemoveClass처리
        labMbzplYn.removeClass( "req" );
        ibxMbzplYn.removeClass( "req" );
    };

