    // 데이터 검증
    scwin.fnValidateData = function () {
        // 거래처명
        if (strLib.isEmpty(dsTradeInfo.get("tradeNm"))) {
			com.showMessage("SAA0043", "거래처명", "", "", ibxTradeNm.id);						//$1은(는) 필수입력사항입니다.
            ibxTradeNm.focus();
            return false;
        }

        // 기업구분
        if (strLib.isEmpty(dsTradeInfo.get("enprGbCd"))) {
			com.showMessage("SAA0043", "기업구분", "", "", sbxEnprGbCd.id);						//$1은(는) 필수입력사항입니다.
            return false;
        }

        // 전화번호
        if (strLib.isEmpty(com.getUdcValue("udcTelNo", "telNo"))) {
           	var focusId = com.getUdcComp("udcTelNo", "telNo").id;
			com.showMessage("SAA0043", "전화번호", "", "", focusId);							//$1은(는) 필수입력사항입니다.
            return false;
        }

        // 설립일자
	    if (!com.isDate(calFoundDt.getValue())){
			com.showMessage("CFA0019", "설립일자", "", "", calFoundDt.id);						//$1을(를) 확인하십시오.
            return false;
	    }

