            if (strLib.isEmpty(dsTradeInfo.get("bzprRegNo"))) {
            	var focusId = com.getUdcComp("udcDept",  "deptNm").id;
				com.showMessage("SAA0043", "부서코드", "", "", focusId);						//$1은(는) 필수입력사항입니다.
                return false;
            }

        } else if (sGbCd == "CF00905") //현장
		                               { 
            if (strLib.isEmpty(dsTradeInfo.get("bzprRegNo"))) {
            	var focusId = com.getUdcComp("udcSite",  "siteNm").id;
				com.showMessage("SAA0043", "현장코드", "", "", focusId);						//$1은(는) 필수입력사항입니다.
                return false;
            }

        } else if (sGbCd == "CF00906") { //사원
            if (strLib.isEmpty(dsTradeInfo.get("bzprRegNo"))) {
            	var focusId = com.getUdcComp("udcUser",  "userNm").id;
				com.showMessage("SAA0043", "사원번호", "", "", focusId);						//$1은(는) 필수입력사항입니다.
                return false;
            }

        } else if (sGbCd == "CF00907") { //지자체번호(4자리)
            if (strLib.isEmpty(dsTradeInfo.get("bzprRegNo"))) {
				com.showMessage("SAA0043", "지자체번호", "", "", ibxRegNo4.id);					//$1은(는) 필수입력사항입니다.
                return false;
            }
*/
        } else if (sGbCd == "CF00909") { //기타사업자번호(임의10자리)
            if (strLib.isEmpty(dsTradeInfo.get("bzprRegNo"))) {
				com.showMessage("SAA0043", "기타번호", "", "", ibxRegNo10.id);			//$1은(는) 필수입력사항입니다.
                return false;
            }

        } else {
			com.showMessage("CFW0004", "기업구분", "", "", sbxEnprGbCd.id);						//$1을(를) 선택하십시오.
            return false;
        }
        return true;
    };
    
    //주사업장여부 변경 이벤트
	scwin.ibxMbzplYn_onchange = function() {
		if (sbxEnprGbCd.getValue() == "CF00902" && ibxMbzplYn.getValue() == "Y") {
            //신규 & 사업자번호 미입력시
            if (com.getUdcValue("udcBusiness", "bizNo") == "" && dsTradeInfo.get("tradeCd") == "") { 
                com.showMessage("CFA0017", "사업자번호"); //$1을(를) 입력하십시오.
                ibxMbzplYn.setValue( "" );                //사업자번호 복사 위해 먼저 입력 요청
                return false;
            }else {
                com.setUdcValue("udcMbzplBzprRegNo", "bizNo", com.getUdcValue("udcBusiness", "bizNo"));  //사업자번호 복사
                com.getUdcComp("udcMbzplBzprRegNo", "bizNo").setReadOnly(true); 
            }
        }else if (sbxEnprGbCd.getValue() == "CF00902" && ibxMbzplYn.getValue() == "N") {
            com.setUdcValue("udcMbzplBzprRegNo", "bizNo", "");  
            com.getUdcComp("udcMbzplBzprRegNo", "bizNo").setReadOnly(false); 
        }
	};
