	scwin.svcId = "";
	scwin.param = com.getAllParam();
	scwin.sngOffrNo    = ""; 
	scwin.InqrRegGubun = "";
	scwin.officeManagemnetDiv     = "N";         
	scwin.saveYn       = "N";
	scwin.fileYn       = "N";
	scwin.manggemnetEmpno = "";
	
    /************************************************************************************ 
     *  [2] onpageload(초기화포함)
     ************************************************************************************/   
    scwin.onpageload = function() {
    	
    	/*
    	var opt = [
	        		 {"CF03002":["sbxUseSealGbCd"]}    	// 사용인감관리상태[CF03003]
        		   ];
	    com.setCdComp(opt,scwin.userSealBbCd);*/
	    
	    scwin.initScreen();
		
		//신규 인지 상세조회인지 구분
		scwin.fnCRCheck();	
    };

    scwin.onpageunload = function() {
		
	};

	// 화면 초기화(필수)
	scwin.initScreen = function() {
	
		var deptCd              = sessionStorage.getItem("deptCd")          || '';
		var sealimpOfficeDeptcd = scwin.fnGetUseSealBusinessDeptCd();       //영업소부서코드
		var userEmpno           = sessionStorage.getItem("userEmpno")          || '';
		
		//전략경영본부 사번가져오기
		scwin.fnSelectManagementEmpno();
		
		//영업소 에서 접근시
		if(sealimpOfficeDeptcd.indexOf(deptCd) > -1)
		{
			scwin.officeManagemnetDiv = "Y";
			//법원인감 선택못하게 set
			sbxUseSealGbCd.setItemDisabled( 0 , true );
		}
		else if(scwin.manggemnetEmpno.indexOf(userEmpno) > -1 ){
			scwin.officeManagemnetDiv = "M";
			//사용인감 선택못하게 set
			dsTcfUseSealMngMap.set( "regYn" , "Y" );
			sbxUseSealGbCd.setItemDisabled( 1 , true );
		}
		else
		{
			scwin.officeManagemnetDiv = "N";
		}
		
		//서명고무인 보이지 않도록  인감번호가  73인경우만  show
		$("#"+txtCeoSeal.id).hide();
		
		//초기 버튼 Control hide
		scwin.setAuthorityInit();
		
		//파일첨부 초기화
		scwin.fnFileSet();
		
	}; 
	

	/**************************************************************************
	 *  [3] 사용자정의 함수
	 **************************************************************************/
	/**************************************************************************
	* 함 수 명: scwin.fnSelectManagementEmpno
	* 기 능: 전략경영본부 사번 가져요기
	**************************************************************************/
	scwin.fnSelectManagementEmpno = function(){
		
		ajaxLib.ajax("/cf/CF00000002U/selectCdList", {
		     mode        : "synchronous"
		    ,method      : "POST"
		    ,requestData : {"upperCd": "CF088"}
		    , callback    : function(result,e){
            	var dsCdList = result.data.dsCdList;
            	
            	if(dsCdList == null) return; 
            	
            	$.each(dsCdList,function(index,item){
            		if(index > 0){
            			scwin.manggemnetEmpno += ",";
            		}
            		
            		scwin.manggemnetEmpno += item.charValN1;
            	});
            }
            , errCallback : "fnErrCallBack"      
   		});		
	}; 
	 
	 
	/**************************************************************************
	* 함 수 명: scwin.fnGetUseSealAdminDeptCd
	* 기 능: 사용인감 영업부서코드 가지고오기
	**************************************************************************/
	scwin.fnGetUseSealBusinessOfficeEmpno = function(){
		var businessOfficeEmpno = "";
		if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().com;
            		 
            	if (typeof(parentObj["fnGetUseSealBusinessEmpno"]) == "function"){
            	  businessOfficeEmpno = parentObj["fnGetUseSealBusinessEmpno"]();
            	}
            }
		}else {
			businessOfficeEmpno = com.fnGetUseSealBusinessEmpno();
		}
		
		return businessOfficeEmpno;
	} 
	 
	/**************************************************************************
	* 함 수 명: scwin.fnGetUseSealAdminDeptCd
	* 기 능: 사용인감 영업부서코드 가지고오기
	**************************************************************************/ 
	scwin.fnGetUseSealBusinessDeptCd = function(){
		
		var sealimpOfficeDeptcd = "";       //영업소부서코드
		
		if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().com;
            		 
            	if (typeof(parentObj["fnGetUseSealBusinessDeptCd"]) == "function"){
            	  sealimpOfficeDeptcd = parentObj["fnGetUseSealBusinessDeptCd"]();
            	}
            }
		}else {
			sealimpOfficeDeptcd = com.fnGetUseSealBusinessDeptCd();
		}
		
		return sealimpOfficeDeptcd;
	} 
	 
    scwin.fnFileSet = function(){
    
    	var fileExt = "gif,jpg,tif,tiff,doc,xls,ppt,pdf,txt,xlsx,png,pptx,docx,hwp,zip";
    	
    	//증빙서류
    	com.setUdcValue("uFileListEvdcDocNo", "jobGbCd", "CF");
        com.setUdcValue("uFileListEvdcDocNo", "menuId", "CF05000001U");
        com.setUdcValue("uFileListEvdcDocNo", "aflId", "");
        com.setUdcValue("uFileListEvdcDocNo", "fileExt", fileExt);
        com.setUdcValue("uFileListEvdcDocNo", "usage", "INS");
        com.setUdcValue("uFileListEvdcDocNo", "fileSize", "");
        com.getUdcComp("uFileListEvdcDocNo", "gen_aflList").addClass("col2");
        
        //날인서류
    	com.setUdcValue("uFileListSngDocNo", "jobGbCd", "CF");
        com.setUdcValue("uFileListSngDocNo", "menuId", "CF05000001U");
        com.setUdcValue("uFileListSngDocNo", "aflId", "");
        com.setUdcValue("uFileListSngDocNo", "fileExt", fileExt);
        com.setUdcValue("uFileListSngDocNo", "usage", "INS");
        com.setUdcValue("uFileListSngDocNo", "fileSize", "");
        com.getUdcComp("uFileListSngDocNo", "gen_aflList").addClass("col2");
        
        //날인원본
    	com.setUdcValue("uFileListSngOrigDocNo", "jobGbCd", "CF");
        com.setUdcValue("uFileListSngOrigDocNo", "menuId", "CF05000001U");
        com.setUdcValue("uFileListSngOrigDocNo", "aflId", "");
        com.setUdcValue("uFileListSngOrigDocNo", "fileExt", fileExt);
        com.setUdcValue("uFileListSngOrigDocNo", "usage", "INS");
        com.setUdcValue("uFileListSngOrigDocNo", "fileSize", "");
        com.getUdcComp("uFileListSngOrigDocNo", "gen_aflList").addClass("col2");
        
    };	 
	 
	/**************************************************************************
	* 함 수 명: scwin.setAuthorityInit
	* 기 능: 초기버튼 hide
	**************************************************************************/ 
	scwin.setAuthorityInit = function(){
	
		//법무팀 확인
		$("#"+grpJdafTr.id).hide();
		//인사총무팀 확인
		$("#"+grpHrTr.id).hide();
		//영업소 확인 
		$("#"+grpBizTr.id).hide();
	
		//초기 버튼 hide
		btnBzofConfirm.setStyle( "display" , "none" );  //영업소확인
		btnJdafConfirm.setStyle( "display" , "none" );  //법무팀확인 
		btnJdafReject.setStyle ( "display" , "none" );  //법무팀반려
		btnHrConfrim.setStyle  ( "display" , "none" );  //인사총무팀확인
		btnHrReject.setStyle   ( "display" , "none" );  //인사총무팀반려
		btnSngRequest.setStyle ( "display" , "none" );  //날인신청
		btnApprove.setStyle    ( "display" , "none" );  //결재
		btnApprForm.setStyle   ( "display" , "none" );  //기안문서보기
		btnSave.setStyle       ( "display" , "none" );  //저장
		btnDel.setStyle        ( "display" , "none" );  //삭제
		
		btnSelectSealNo.setStyle( "display" , "none" );  //등록본인 span hide 
		spanOnlyUser.setStyle( "display" , "none" );  //등록본인 span hide 
		chkOnlyUser.setStyle( "display" , "none" );   //등록본인 check box hide
		btnUserSelPop.setStyle( "display" , "none" ); //회원조회 팝업 버튼 hide
		
		grpSngOrigDocNoArea.setStyle( "display" , "none" ); //날인원본 hide
		
		com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").addClass("hide"); //증빙서류
		com.getUdcComp("uFileListSngDocNo", "btnAddFile").addClass("hide");  //날인서류
		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide");  //날인원본
		
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnSwitchBtnControl
	* 기 능: 사용인감 버튼 컨트롤
	**************************************************************************/	 
	scwin.fnSwitchBtnControl = function(){
		
		 if(scwin.officeManagemnetDiv == "N"){ 
 			scwin.fnBtnControl();
		 } else if(scwin.officeManagemnetDiv == "M") {
		 	scwin.fnManagemnetBtnControl();
		 } else if(scwin.officeManagemnetDiv == "Y") {
			scwin.fnBusinessOfficeBtnControl();
		 }
	}
	
	/**************************************************************************
	* 함 수 명: scwin.fnManagemnetBtnControl
	* 기 능: 전략경영팀  버튼 control
	**************************************************************************/
	scwin.fnManagemnetBtnControl = function(){
		
		var deptCd              = sessionStorage.getItem("deptCd")          || '';
		var deptName            = sessionStorage.getItem("deptNm")          || '';
	    var userEmpno           = sessionStorage.getItem("userEmpno")       || '';
	    var userName            = sessionStorage.getItem("userNm")       || '';
		var useSealGbCd         = dsTcfUseSealMngMap.get("useSealGbCd")      || ""; //사용인감구분코드[CF03002]
	    var useSealMngStusCd    = dsTcfUseSealMngMap.get("useSealMngStusCd") || ""; //사용인감관리상태코드
	    var aplntEmpno          = dsTcfUseSealMngMap.get("aplntEmpno")       || ""; //신청자사번
	    var sngOffrNo           = dsTcfUseSealMngMap.get("sngOffrNo")        || ""; //날인신청번호
		var sngOffrDeptCd       = dsTcfUseSealMngMap.get("sngOffrDeptCd")    || ""; //날인신청부서코드
		var sngOrigDocNo        = dsTcfUseSealMngMap.get("sngOrigDocNo")     || ""; //날인_원본_문서번호
		var aprvNo              = dsTcfUseSealMngMap.get("aprvNo")           || ""; //결재번호
		
		var adminCheck  = false;
		
		if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().com;
            		 
            	if (typeof(parentObj["fnUseSealAdminCheck"]) == "function"){
            	  adminCheck = parentObj["fnUseSealAdminCheck"]();
            	}
            }
		}else{
			adminCheck = com.fnUseSealAdminCheck();
		}	
	    
		if(scwin.InqrRegGubun == "REG"){
			dsTcfUseSealMngMap.set( "aplntEmpno"    , userEmpno ); //신청자사번
	    	dsTcfUseSealMngMap.set( "aplntNm"       , userName );  //신청자명
	    	dsTcfUseSealMngMap.set( "sngOffrDeptCd" , deptCd );    //날인싱청부서코드
	    	dsTcfUseSealMngMap.set( "offrDeptNm"    , deptName);   //신청부서명
		}
		
		//저장버튼
		if( (useSealMngStusCd == "CF0300301" ||  useSealMngStusCd == "")  &&  (aplntEmpno == "" ||  deptCd ==  sngOffrDeptCd) )
		{
			scwin.fnMagementSaveShowStyle();
			
			//증빙서류 , 날인서류 show
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").removeClass("hide"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").removeClass("hide");  //날인서류
			
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").addClass("show"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").addClass("show");  //날인서류
		}
		else
		{
			scwin.saveYn = "N";
		}
		
		//날인신청
		if(aprvNo  == "" && useSealMngStusCd == "CF0300301" && deptCd ==  sngOffrDeptCd  )
		{
			btnSngRequest.setStyle( "display" , "" );  //날인신청
		}
		
		//삭제
		if(useSealMngStusCd == "CF0300301" && deptCd ==  sngOffrDeptCd )
		{
			btnDel.setStyle( "display" , "" );  //삭제	
		}
		
		//법무팀확인
		if(   deptCd          == "K400020" 
		   && adminCheck 
		   && useSealMngStusCd == "CF0300309" )
		{
			btnJdafConfirm.setStyle( "display" , "" );  //법무팀확인 
			btnJdafReject.setStyle ( "display" , "" );  //법무팀반려
		} 
		
		//인사총무팀확인 버튼 control (법인인감일시 인사총무팀 확인)
		if(        deptCd      == "K400060" 
		   && useSealGbCd.indexOf("CF0300201") > -1  
		   && adminCheck 
		   && useSealMngStusCd == "CF0300304" )
		{
			btnHrConfrim.setStyle  ( "display" , "" );  //인사총무팀확인
			btnHrReject.setStyle   ( "display" , "" );  //인사총무팀반려 
		}
		
		if(useSealGbCd = "CF0300201" && useSealMngStusCd == "CF0300305" && sngOffrDeptCd == deptCd  )
		{//법인인감 && 인사총무팀확인
			scwin.fileYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 hide
			
		 	if(sngOrigDocNo == "")
		 	{
		 		scwin.saveYn = "Y";
		 		btnSave.setStyle( "display" , "" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("hide"); //날인원본
				com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("show");  //날인원본
		 	}
		 	else
		 	{
		 		btnSave.setStyle( "display" , "none" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		 	}
		}
		else if((useSealMngStusCd == "CF0300304" || useSealMngStusCd == "CF0300305" ) && sngOffrDeptCd != deptCd )
		{    
			scwin.fileYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 show
			
			com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		}
		
		scwin.fnReadOnly();
		
		
		
		
		
	}
	
	/**************************************************************************
	* 함 수 명: scwin.fnBusinessOfficeBtnControl
	* 기 능: 영업소 버튼 control
	**************************************************************************/
	scwin.fnBusinessOfficeBtnControl = function(){
		var deptCd              = sessionStorage.getItem("deptCd")          || '';
	    var userEmpno           = sessionStorage.getItem("userEmpno")       || '';
	    var sealimpOfficeEmpno = scwin.fnGetUseSealBusinessOfficeEmpno();   //영업소 사용자 사번코드
		var useSealMngStusCd    = dsTcfUseSealMngMap.get("useSealMngStusCd") || ""; //사용인감관리상태코드
		var useSealGbCd         = dsTcfUseSealMngMap.get("useSealGbCd")      || ""; //사용인감구분코드[CF03002]
		var aplntEmpno          = dsTcfUseSealMngMap.get("aplntEmpno")       || ""; //신청자사번
		var aprvNo              = dsTcfUseSealMngMap.get("aprvNo")           || ""; //결재번호
		var sngOffrNo           = dsTcfUseSealMngMap.get("sngOffrNo")        || ""; //날인신청번호
		var sngOffrDeptCd       = dsTcfUseSealMngMap.get("sngOffrDeptCd")    || ""; //날인신청부서코드
		var sngOrigDocNo        = dsTcfUseSealMngMap.get("sngOrigDocNo")     || ""; //날인_원본_문서번호
		var gwDocNo             = dsTcfUseSealMngMap.get("gwDocNo")          || ""; //텔레피아 문서번호
		
		/*
        영업소확인 (상태코드값이) 로그인한사용자의 부서코드가 영업소 , 사용인감관리상태코드가 저장중 , 로그인한사용자의 사번이 하기와 같은시 
        K80112C	남부영업소   정형선차장 037264
        K80128C	중부영업소   김미남차장 030478
		K80140C	수도권영업소 문정권부장 023506*/
		if(useSealMngStusCd == "CF0300301" && sealimpOfficeEmpno.indexOf(userEmpno) > -1 )
		{
			btnBzofConfirm.setStyle( "display" , "" );  //영업소확인	
		}
		
		//저장버튼
		if(useSealMngStusCd != "CF0300308" && ( sngOffrNo == "" || sngOffrDeptCd == deptCd ) )
		{
			scwin.fnSaveShowStyle();
			
			//증빙서류 , 날인서류 show
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").removeClass("hide"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").removeClass("hide");  //날인서류
			
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").addClass("show"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").addClass("show");  //날인서류
		}
		else
		{
			scwin.saveYn = "N";
		}
		
		//삭제버튼
		if(sngOffrNo != "" && useSealMngStusCd != "CF0300308"  && sngOffrDeptCd == deptCd )
		{
			btnDel.setStyle( "display" , "" );  //삭제
		}
		
		//날인원본 
		if( useSealMngStusCd == "CF0300308" && sngOffrDeptCd == deptCd && sngOrigDocNo == "") 
		{// 영업소확인 && 해다부서 && 날인원본이 없을시
			scwin.fileYn = "Y";
			scwin.saveYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 hide
		 	btnSave.setStyle( "display" , "" );       //저장 버튼
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("hide"); //날인원본
			com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("show");  //날인원본
		 	
		} else if(useSealMngStusCd == "CF0300308" || sngOrigDocNo != "")
		{// 영업소확인 or 날인원본 
			scwin.fileYn = "Y";
			
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 hide
		 	btnSave.setStyle( "display" , "none" );       //저장 버튼
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		}
		
		scwin.fnReadOnly();
	}
	 
	/**************************************************************************
	* 함 수 명: scwin.fnBtnControl
	* 기 능: 사용인감관리 버튼 control;
	**************************************************************************/
	scwin.fnBtnControl = function(){
		
	    var deptCd              = sessionStorage.getItem("deptCd")          || '';
	    var userEmpno           = sessionStorage.getItem("userEmpno")       || '';
		var useSealMngStusCd    = dsTcfUseSealMngMap.get("useSealMngStusCd") || ""; //사용인감관리상태코드
		var useSealGbCd         = dsTcfUseSealMngMap.get("useSealGbCd")      || ""; //사용인감구분코드[CF03002]
		var aplntEmpno          = dsTcfUseSealMngMap.get("aplntEmpno")       || ""; //신청자사번
		var aprvNo              = dsTcfUseSealMngMap.get("aprvNo")           || ""; //결재번호
		var sngOffrNo           = dsTcfUseSealMngMap.get("sngOffrNo")        || ""; //날인신청번호
		var sngOffrDeptCd       = dsTcfUseSealMngMap.get("sngOffrDeptCd")    || ""; //날인신청부서코드
		var sngOrigDocNo        = dsTcfUseSealMngMap.get("sngOrigDocNo")     || ""; //날인_원본_문서번호
		var gwDocNo             = dsTcfUseSealMngMap.get("gwDocNo")          || ""; //텔레피아 문서번호
		
		var adminCheck  = false;
		
		if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().com;
            		 
            	if (typeof(parentObj["fnUseSealAdminCheck"]) == "function"){
            	  adminCheck = parentObj["fnUseSealAdminCheck"]();
            	}
            }
		}else{
			adminCheck = com.fnUseSealAdminCheck();
		}	
		
		//법무팀확인 버튼 control (모든날인신청)
		if(   deptCd          == "K400020" 
		   && adminCheck 
		   && aprvNo           != "" 
		   && useSealMngStusCd == "CF0300303" )
		{
			btnJdafConfirm.setStyle( "display" , "" );  //법무팀확인 
			btnJdafReject.setStyle ( "display" , "" );  //법무팀반려
		} 
		
		//인사총무팀확인 버튼 control (법인인감일시 인사총무팀 확인)
		if(        deptCd      == "K400060" 
		   && useSealGbCd.indexOf("CF0300201") > -1  
		   && adminCheck 
		   && aprvNo           != "" 
		   && useSealMngStusCd == "CF0300304" )
		{
			btnHrConfrim.setStyle  ( "display" , "" );  //인사총무팀확인
			btnHrReject.setStyle   ( "display" , "" );  //인사총무팀반려 
		}
		
		//결재 버튼 control 
		if(sngOffrNo != "" && aprvNo == "" && sngOffrDeptCd == deptCd  )
		{
			btnApprove.setStyle( "display" , "" );  //결재버튼 
		}
		
		//기안문서보기
		if(sngOffrNo != "" && aprvNo != "" && useSealMngStusCd != "CF0300302" && (deptCd == sngOffrDeptCd || adminCheck) && gwDocNo != "" )
		{
			btnApprForm.setStyle( "display" , "" );  //기안문서보기
		}
		
		//저장버튼
		if(aprvNo == "" && ( sngOffrNo == "" || sngOffrDeptCd == deptCd ) )
		{
			scwin.fnSaveShowStyle();
			
			//증빙서류 , 날인서류 show
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").removeClass("hide"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").removeClass("hide");  //날인서류
			
			com.getUdcComp("uFileListEvdcDocNo", "btnAddFile").addClass("show"); //증빙서류
			com.getUdcComp("uFileListSngDocNo", "btnAddFile").addClass("show");  //날인서류
		}
		else
		{
			scwin.saveYn = "N";
		}
		
		//삭제버튼
		if(sngOffrNo != "" && aprvNo == "" && sngOffrDeptCd == deptCd )
		{
			btnDel.setStyle( "display" , "" );  //삭제
		}
		
		//날인원본
		if(useSealGbCd = "CF0300202" && useSealMngStusCd == "CF0300304" && sngOffrDeptCd == deptCd)
		{//사용인감 && 법무팀확인 &&
			
			scwin.fileYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 hide
		 	
		 	if(sngOrigDocNo == "")
		 	{
		 		scwin.saveYn = "Y";
		 		btnSave.setStyle( "display" , "" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("hide"); //날인원본
				com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("show");  //날인원본
		 	}
		 	else
		 	{
		 		btnSave.setStyle( "display" , "none" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		 	}
		}
		else if(useSealGbCd = "CF0300201" && useSealMngStusCd == "CF0300305" && sngOffrDeptCd == deptCd  )
		{//법인인감 && 인사총무팀확인
			scwin.fileYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 hide
			
		 	if(sngOrigDocNo == "")
		 	{
		 		scwin.saveYn = "Y";
		 		btnSave.setStyle( "display" , "" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("hide"); //날인원본
				com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("show");  //날인원본
		 	}
		 	else
		 	{
		 		btnSave.setStyle( "display" , "none" );       //저장 버튼
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 		com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		 	}
		}
		else if((useSealMngStusCd == "CF0300304" || useSealMngStusCd == "CF0300305" ) && sngOffrDeptCd != deptCd )
		{    
			scwin.fileYn = "Y";
			scwin.saveYn = "Y";
			grpSngOrigDocNoArea.setStyle( "display" , "" ); //날인원본 show
			
			com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		}
		else
		{
			scwin.fileYn = "N";
			grpSngOrigDocNoArea.setStyle( "display" , "none" ); //날인원본 show
			com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").removeClass("show"); //날인원본
		 	com.getUdcComp("uFileListSngOrigDocNo", "btnAddFile").addClass("hide"); //날인원본
		}
		
		scwin.fnReadOnly();
		
	};
	
	scwin.fnMagementSaveShowStyle = function(){
		scwin.saveYn = "Y";
		btnSave.setStyle( "display" , "" );       //저장 버튼
	} 
	
	scwin.fnSaveShowStyle = function(){
		scwin.saveYn = "Y";
		btnSave.setStyle( "display" , "" );       //저장 버튼
		btnSelectSealNo.setStyle( "display" , "" ); //인감선택 팝업 버튼
		spanOnlyUser.setStyle( "display" , "" );  //등록본인 span show 
		chkOnlyUser.setStyle( "display" , "" );   //등록본인 check box
		btnUserSelPop.setStyle( "display" , "" ); //회원조회 팝업 버튼
	}
	
	
	
	/**************************************************************************
	* 함 수 명: scwin.fnReadOnly
	* 기 능: 저장버튼 활성화 유무에 따른 input , select , textarea readOnly set
	**************************************************************************/
	scwin.fnReadOnly = function(){
		
		if(scwin.saveYn == "Y")
		{
			$("#"+grpInputArea.id).find("input").each(function(){
				var inputId = $(item).attr("id");
				var comp    = $w.getComponentById(inputId);
				var orgId   = comp.org_id; 
				
				
				if(orgId == undefined){
				 	return true;
				}
				
				//id 가 ibx로시작하는 것만 setReadOnly true
				if(orgId != undefined && !orgId.startsWith('ibx')){
				 	return true;
				}
				
				comp.setReadOnly(false);
			});
			
			//일자 readOnly true
			icaSngOffrDt.setReadOnly(false);
			
			//신청구분 readOnly true
			sbxUseSealGbCd.setReadOnly(false);
			
			//내용 readOnly true
			txaSngOffrCn.setReadOnly(false);
			
		}
		else {
			$("#"+grpInputArea.id).find("input").each(function(index,item){
				var inputId = $(item).attr("id");
				var comp    = $w.getComponentById(inputId);
				var orgId   = comp.org_id;
				
				if(orgId == undefined){
				 	return true;
				}
				
				//id 가 ibx로시작하는 것만 setReadOnly true
				if(orgId != undefined && !orgId.startsWith('ibx')){
				 	return true;
				}
				
				comp.setReadOnly(true);
			});
			
			//일자 readOnly true
			icaSngOffrDt.setReadOnly( true );
			
			//신청구분 readOnly true
			sbxUseSealGbCd.setReadOnly(true);
			
			//내용 readOnly true
			txaSngOffrCn.setReadOnly(true);
		}
	}
	 
	/**************************************************************************
	* 함 수 명: scwin.fnCRCheck
	* 기 능: 상세조회인지 , 등록인지 check
	**************************************************************************/
	 scwin.fnCRCheck = function(){
	 
	 	//신규등록
	 	if(scwin.param.sngOffrNo == undefined)
	 	{
	 		var nowDate = new Date();
	 		var curentDate = $w.getFormattedDate( nowDate , "yyyyMMdd" );
	 		scwin.InqrRegGubun = "REG";
	 		icaSngOffrDt.setValue( curentDate );
	 		
	 		scwin.fnSwitchBtnControl();
	 	}
	 	else 
	 	{// 조회후 업데이트 가능
	 		scwin.InqrRegGubun = "INQR";
	 		
	 		scwin.fnSearchInfoList();
	 	}
	 };
	
	/**************************************************************************
	* 함 수 명: scwin.fnSearchInfoList
	* 기 능: 상세정보 조회
	**************************************************************************/
    scwin.fnSearchInfoList = function() {
    	
    	scwin.svcId = "selectTcfUseSealMng";
    	
    	var sngOffrNo = "";
    	
    	if(scwin.param.sngOffrNo != undefined){
    		sngOffrNo = scwin.param.sngOffrNo;
    	}
    	else {
    		sngOffrNo = scwin.sngOffrNo;
    	}	
    	
		
        ajaxLib.ajax("/cf/CF05000001U/selectTcfUseSealMng", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : { 
                                sngOffrNo     : sngOffrNo      //날인신청번호
                               
                            }
            , callback    : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };
    
    /**************************************************************************
	* 함 수 명: scwin.fnSetNameJbclNm
	* 기 능: 법무팀 , 인사총무팀 , 영업소확인 확인자 직급 세팅
	**************************************************************************/
    scwin.fnSetNameJbclNm = function(){
    	//법무팀 확인자 성명 직급 세팅
		var jdafTeamConprNmStr = "";
		var jdafTeamStr = "";
		var jdafTeamConprNm     = dsTcfUseSealMngMap.get( "jdafTeamConprNm" );
		var jdafTeamConprJbclNm = dsTcfUseSealMngMap.get( "jdafTeamConprJbclNm" );
		var jdafTeamAproOpinCn  = dsTcfUseSealMngMap.get( "jdafTeamAproOpinCn" );
		var jdafTeamConfDtm     = dsTcfUseSealMngMap.get( "jdafTeamConfDtm" );
		
		jdafTeamConprNmStr += jdafTeamConprNm; 
		
		if("" != jdafTeamConprJbclNm ){
			jdafTeamConprNmStr += " "; 
			jdafTeamConprNmStr += jdafTeamConprJbclNm; 
		}
		if(jdafTeamConfDtm != ""){
			jdafTeamStr += jdafTeamConfDtm;
		}
		
		if(jdafTeamConprNmStr != ""){
			jdafTeamStr += "&nbsp;&nbsp;&nbsp;&nbsp;";
			jdafTeamStr += jdafTeamConprNmStr;
		}
		
		if(jdafTeamAproOpinCn != ""){
			jdafTeamStr += "&nbsp;&nbsp;&nbsp;&nbsp;";
			jdafTeamStr += jdafTeamAproOpinCn;
		}
		
		spnJdafTeamStr.setLabel( jdafTeamStr );
		
		//인사총무친 성명 직급 세팅
		var hrTeamConprNm        = dsTcfUseSealMngMap.get("hrTeamConprNm");
		var hrTeamConprJbclNm    = dsTcfUseSealMngMap.get("hrTeamConprJbclNm"); 
		var hrTeamConfDtm        = dsTcfUseSealMngMap.get("hrTeamConfDtm"); 
		var hrGeatmAproOpinCn    = dsTcfUseSealMngMap.get("hrGeatmAproOpinCn"); 
		var hrTeamConprJbclNmStr = "";
		var hrTeamtr = "";
		
		hrTeamConprJbclNmStr += hrTeamConprNm;
		
		if("" != hrTeamConprJbclNm){
			hrTeamConprJbclNmStr += " ";
 			hrTeamConprJbclNmStr += hrTeamConprJbclNm;
		}
		
		if(hrTeamConfDtm != ""){
			hrTeamtr += hrTeamConfDtm
		}
		
		if(hrTeamConprJbclNmStr != ""){
			hrTeamtr += "&nbsp;&nbsp;&nbsp;&nbsp;";	
			hrTeamtr += hrTeamConprJbclNmStr
		}
		
		if(hrGeatmAproOpinCn != ""){
			hrTeamtr += "&nbsp;&nbsp;&nbsp;&nbsp;";	
			hrTeamtr += hrGeatmAproOpinCn
		}
		
		spnHrGeatmStr.setLabel( hrTeamtr );
		
		//영업소
		var bzofConprNm        = dsTcfUseSealMngMap.get( "bzofConprNm" );
		var bzofConprJbclNm    = dsTcfUseSealMngMap.get( "bzofConprJbclNm" );	
		var bzofConfDtm    = dsTcfUseSealMngMap.get( "bzofConfDtm" );	
		var bzofConprJbclNmStr = ""
		var bzofStr = ""
		
		bzofConprJbclNmStr += bzofConprNm;
		
		if("" != bzofConprJbclNm){
	 		bzofConprJbclNmStr += " ";
	 		bzofConprJbclNmStr += bzofConprJbclNm;
		}
		
		if(bzofConfDtm != ""){
			bzofStr += bzofConfDtm; 
		}
		
		if(bzofConprJbclNmStr != ""){
			bzofStr += "&nbsp;&nbsp;&nbsp;&nbsp;";  
			bzofStr += bzofConprJbclNmStr; 
		}
		
		bizOfConfrimStr.setLabel( bzofStr );
		
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnValidate
	* 기 능: 유효성체크
	**************************************************************************/
	scwin.fnValidate = function() {
		
		dsTcfUseSealMngMap.set("sngDocNo"    , com.getUdcComp("uFileListSngDocNo"    , "aflId").getValue()); //날인서류
		dsTcfUseSealMngMap.set("evdcDocNo"   , com.getUdcComp("uFileListEvdcDocNo"   , "aflId").getValue()); //증빙서류
	    dsTcfUseSealMngMap.set("sngOrigDocNo", com.getUdcComp("uFileListSngOrigDocNo", "aflId").getValue()); //날인원본
	
		if (!com.validateGroup([grpInputArea])) {
            return false;
        }
        
        if(scwin.fileYn == "N" ) { //날인서류
        	if(dsTcfUseSealMngMap.get("sngDocNo") == ""){
        		com.showMessage("CFW0009", "날인서류");
            	return false;
        	}
        	
        } else if(scwin.fileYn == "Y") { //날인원본
        	if(dsTcfUseSealMngMap.get("sngOrigDocNo") == ""){
        		com.showMessage("CFW0009", "날인원본");
            	return false;
        	}
        }
	    
	    return true;
	};
	
	scwin.fnGetUseSealGbArr = function(){
		var useSealGbNm = sbxUseSealGbCd.getText();
		var useSealGbTxtArr = null;
		
		if(useSealGbNm == "" ) return useSealGbTxtArr;
		
		if(useSealGbNm.indexOf(" ") > -1)
		{
			useSealGbTxtArr = useSealGbNm.split(" ");
		}
		else {
			useSealGbTxtArr = new Array();
			useSealGbTxtArr.push(useSealGbNm);		 
		}
		
		return useSealGbTxtArr;
	}	
	
	/**************************************************************************
	* 함 수 명: scwin.fnSave
	* 기 능: 저장
	**************************************************************************/
	scwin.fnSave = function() {
    	//유효성체크
        if( !scwin.fnValidate() ) return;
        
        var useSealGbArr =  scwin.fnGetUseSealGbArr();
        var useSealGbStr = "";
        
        if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
        
        //console.log("useSealGbStr >> " + useSealGbStr);
        var confirmStr = "";
        if(scwin.InqrRegGubun == "REG"){
        	dsTcfUseSealMngMap.set( "rowStatus" , "C" );
        	confirmStr = useSealGbStr + " 날인 신청을 저장하시겠습니까?";
        } else if(scwin.InqrRegGubun == "INQR"){
        	dsTcfUseSealMngMap.set( "rowStatus" , "U" );
        	confirmStr = useSealGbStr + " 날인 신청을 수정하시겠습니까?";
        }
        
        if(!confirm(confirmStr)) return;
        
        ajaxLib.ajax("/cf/CF05000001U/saveTcfUseSealMng", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            
            	com.showMessage("SCN0002", "저장");			//$1 되었습니다.
            	
            	scwin.sngOffrNo = result.data;
            	scwin.InqrRegGubun = "INQR";
            	
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();

            }
            , errCallback : "fnErrCallBack"
        });
    	
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnDelete
	* 기 능: 삭제
	**************************************************************************/
	scwin.fnDelete = function(){
		var useSealGbArr =  scwin.fnGetUseSealGbArr();
        var useSealGbStr = "";
        
        if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
        
        var confirmStr = useSealGbStr + " 날인 신청을 삭제하시겠습니까?";
        
        if(!confirm(confirmStr)) return;
        
        ajaxLib.ajax("/cf/CF05000001U/deleteTcfUseSealMng", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	com.showMessage("SCN0002", "삭제");			//$1 되었습니다.
	   			scwin.btnList_onclick();
            }
            , errCallback : "fnErrCallBack"
        });
        
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnStatusReject
	* 기 능: 저장중으로 반려처리
	**************************************************************************/
	scwin.fnStatusReject = function(){
		ajaxLib.ajax("/cf/CF05000001U/updateTcfUseSealMngReject", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();	
            	
            }
            , errCallback : "fnErrCallBack"
        });
	};
	
    /**************************************************************************
	 *  [4] Callback
	 **************************************************************************/
	
	/* 결과 후처리*/
	scwin.fnCallback = function(result, e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfUseSealMng" :
	   			var data = result.data.resultData;
	   			
	   			
	   			dsTcfUseSealMngMap.setJSON( data );
	   			
	   			var useSealGbCd          = dsTcfUseSealMngMap.get("useSealGbCd");
	   			var sealimpOfficeDeptcd = scwin.fnGetUseSealBusinessDeptCd();       //영업소부서코드
				var sngOffrDeptCd       = dsTcfUseSealMngMap.get("sngOffrDeptCd")    || ""; //날인신청부서코드
				var regYn               = dsTcfUseSealMngMap.get("regYn")    || ""; //경영전략 등록여부
				
				//경영전략등록여부 값이 Y일시 경영전략 으로 세팅
				if(regYn == "Y"){
					scwin.officeManagemnetDiv = "M";
					//사용인감 선택못하게 set
					sbxUseSealGbCd.setItemDisabled( 1 , true );
				}
				
				// 날인신청부서가 영업소부서코드일시 
				if(sealimpOfficeDeptcd.indexOf(sngOffrDeptCd) > -1)
				{
					scwin.officeManagemnetDiv = "Y"
					//법원인감 선택못하게 set
					sbxUseSealGbCd.setItemDisabled( 0 , true );
				}
				  //  SDCIT                          전략경영본부
//	   			if((scwin.officeManagemnetDiv == "N" || scwin.officeManagemnetDiv == "M" ) && "CF0300201" == useSealGbCd){//법인인감 일시 법무팀 확인 , 인사총무팀 확인 show
                                                                                               // 법인인감, 법인인감+사용인감인 경우
                if((scwin.officeManagemnetDiv == "N" || scwin.officeManagemnetDiv == "M" ) &&  (useSealGbCd.indexOf("CF0300201") > -1) ){
	   				$("#"+grpJdafTr.id).show();
	   				$("#"+grpHrTr.id).show();
	   			}else if(scwin.officeManagemnetDiv == "N" && "CF0300202" == useSealGbCd){//사용인감 일시 법무팀 확인만 show
	   				$("#"+grpJdafTr.id).show();
	   			} else if(scwin.officeManagemnetDiv == "Y") {  //영업소 확인
					$("#"+grpBizTr.id).show();
				}

	   			// 인감번호가 73인경우 (서명고무인)
	   			if(dsTcfUseSealMngMap.get("sealNo")=="73"){
	   			   $("#"+txtCeoSeal.id).show();
	   			} else{
	   			   $("#"+txtCeoSeal.id).hide();
	   			}
	   			
	   			//법무팀 , 인사총무팀 , 영업소 확인자 성명 직급 세팅
	   			scwin.fnSetNameJbclNm();
	   			
	   			//증빙서류
	   			com.setUdcValue("uFileListEvdcDocNo", "aflId", dsTcfUseSealMngMap.get("evdcDocNo"));
	   			
	   			//날인서류
	   			com.setUdcValue("uFileListSngDocNo", "aflId", dsTcfUseSealMngMap.get("sngDocNo"));
	   			
	   			//날인원본  
	   			com.setUdcValue("uFileListSngOrigDocNo", "aflId", dsTcfUseSealMngMap.get("sngOrigDocNo"));
	   			
	   			scwin.fnSwitchBtnControl();
	   			
	   			break;
	   		default :
	   			break;
	   }        
	};
	
	/* 에러 후처리 */
    scwin.fnErrCallBack = function(e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfUseSealMng" :
	   			console.log('search Error!!!!!!!!!!!');
	   			break;
	   		case "saveTcfUseSealMng" :
	   			console.log('save Error!!!!!!!!!!!');
	   			break;	
	   }        
    };
    
    /**************************************************************************
	* 함 수 명: scwin.fnPopupCallback
	* 기 능: 신청자 조회팝업 callBack method
	**************************************************************************/
	scwin.fnPopupCallback = function(result) {
	  if(result != ""){
		dsTcfUseSealMngMap.set( "aplntEmpno"    , result.userEmpno ); //신청자사번
	    dsTcfUseSealMngMap.set( "aplntNm"       , result.userNm ); //신청자명
	    dsTcfUseSealMngMap.set( "sngOffrDeptCd" , result.deptCd ); //날인싱청부서코드
	    dsTcfUseSealMngMap.set( "offrDeptNm"    , result.deptNm ); //신청부서명	
	  }
	};
     
