	/**************************************************************************
	 * Event: scwin.chkOnlyUser_onlabelclick
	 * 기 능: 등록본인 체크시 session 정보 set
	**************************************************************************/
	scwin.chkOnlyUser_onlabelclick = function(index,checked,value) {
	    var userEmpno =  sessionStorage.getItem("userEmpno") || "";
	    var userNm    =  sessionStorage.getItem("userNm") || "";
	    var deptCd    =  sessionStorage.getItem("deptCd") || "";
	    var deptNm    =  sessionStorage.getItem("deptNm") || "";
	    
	    if(checked == true)
	    {
	    	dsTcfUseSealMngMap.set( "aplntEmpno"    , userEmpno ); //신청자사번
	    	dsTcfUseSealMngMap.set( "aplntNm"       , userNm );    //신청자명
	    	dsTcfUseSealMngMap.set( "sngOffrDeptCd" , deptCd );    //날인싱청부서코드
	    	dsTcfUseSealMngMap.set( "offrDeptNm"    , deptNm );    //신청부서명
	    } 
	    else
	    {
	    	dsTcfUseSealMngMap.set( "aplntEmpno"    , "" ); //신청자사번
	    	dsTcfUseSealMngMap.set( "aplntNm"       , "" ); //신청자명
	    	dsTcfUseSealMngMap.set( "sngOffrDeptCd" , "" ); //날인싱청부서코드
	    	dsTcfUseSealMngMap.set( "offrDeptNm"    , "" ); //신청부서명
	    }
		
	};
	
