    scwin.aprvPublicAction = function() {
		
		//결재번호
		var aprvNo = com.getAprvNo();		
		console.log("결재번호 = " + aprvNo);
		
		dsTcfUseSealMngMap.set( "aprvNo" , aprvNo );
		
		 ajaxLib.ajax("/cf/CF05000001U/updateTcfUseSealMngAprno", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();
            }
            , errCallback : "fnErrCallBack"
        });
		
    };
    
    /**************************************************************************
	 * Event: scwin.fnSetAproveFileStr
	 * 기 능: 결재 첨부파일 증빙서류 , 날인서류 
	**************************************************************************/
    scwin.fnSetAproveFileStr = function(){
    	var arr       = new Array();
    	var sngDocNo  = dsTcfUseSealMngMap.get("sngDocNo"); 
        var evdcDocNo = dsTcfUseSealMngMap.get("evdcDocNo");
        var aflId     = ""
        
        if(sngDocNo != ""){
        	arr.push(sngDocNo);
        }
        
        if(evdcDocNo != ""){
        	arr.push(evdcDocNo);
        }
        
        $.each(arr,function(index,item){
        	if(index > 0){
        		aflId += ",";
        	}
        	
        	aflId += item;
        });
        
        return aflId;
    };
	
	/**************************************************************************
	 * Event: scwin.btnApprove_onclick
	 * 기 능: 결재 기안 호출
	**************************************************************************/
	scwin.btnApprove_onclick = function() {
		/* 1. 기본 파라미터 설정 */
		var paramObj = new Object();  
        var aflId    = scwin.fnSetAproveFileStr();

        paramObj.jobGbCd = "CF";                                         // 업무구분코드
        paramObj.importUrl = "/ux/cf/cf0500/CF05000001E.xml";            // 출력할 결재본문페이지
        paramObj.aprvTtl = "["+dsTcfUseSealMngMap.get("sngOffrNo")+"] ["+dsTcfUseSealMngMap.get( "offrDeptNm" )+"] 인감날인 신청 품의 ("+dsTcfUseSealMngMap.get("sngOffrTtl")+")";  // 결재제목
        paramObj.rvwrEmpno = "";                                   // 검토자_사번
        paramObj.fnlAprprEmpno = "";                                     // 최종승인자_사번
        paramObj.aprvPrcgNm = "";                                        // 후처리결재처리명
        paramObj.aprvPrcgEventNm = "/cf/CF05000001U/notifyAprvUseSeal";  // 후처리결재처리 이벤트명(후처리 실행을 위한 URL을 입력한다)
        paramObj.aprvDocModiPsblYn = "Y";                                // 결재문서 수정불가기능 Y/N  //////////////////
        paramObj.aprlnModiPsblYn = "N";                                  // 결재선 수정불가기능 Y/N    //////////////////
        paramObj.aprvAtchModiPsblYn = "Y";                               // 결재첨부 수정불가기능 Y/N  //////////////////
        paramObj.aprvAddAflId = aflId;                                   // 첨부용 첨부파일ID(콤머로 구분해서 보내준다)
        paramObj.popupWidth = "1350";                                    // 팝업창 가로크기
        paramObj.popupHeight = "800";                                    // 팝업창 세로크기
        paramObj.relatedDocumentIds = "";                                // 텔레피아 관련문서 ID (콤머로 구분해서 보내준다) : 필수값 아님
        
        // 첨부파일영역보기 : 필수값 아님
        paramObj.aprvAtchAreaYn = "P";
        com.aprvSend(paramObj); //일반결재 호출
	};
	
	/**************************************************************************
	 * Event: scwin.sbxUseSealGbCd_onchange
	 * 기 능: 신청구분 선택에 따른 사용인감번호 선택 영역 show hide validate set
	**************************************************************************/
	scwin.sbxUseSealGbCd_onchange = function() {
		var useSealGbTxt =  this.getValue();
		
		grpSealNoThArea.removeClass("req");
		ibxSealNo.removeClass("req");
		ibxSealNo.setMandatory(false);
		grpSealNoArea.setStyle( "display" , "" );  //사용인감 영역
		
		//법인인감 체크되어있을시 사용인감번호 선택영역 hide
		if(useSealGbTxt.indexOf("CF0300201") > -1){
			grpSealNoArea.setStyle( "display" , "none" );  //사용인감 영역 hide

			if(useSealGbTxt.indexOf("CF0300202") < 0){//사용인감 미 체크 인경우 
			     ibxSealNo.setValue("");  // 법인인감 선택시  사용인감 번호는 제거
			}
		}
		 
		//사용인감 체크되어있을시 사용인감번호 선택 영역 show 및 유효성체크 세팅 
		if(useSealGbTxt.indexOf("CF0300202") > -1){
			grpSealNoThArea.addClass("req");
			ibxSealNo.addClass("req");
			ibxSealNo.setMandatory(true);
			grpSealNoArea.setStyle( "display" , "" );  //사용인감 영역 shwo
		}
	
	};
	
