	};
	
	/**************************************************************************
	 * Event: scwin.btnJdafConfirm_onclick
	 * 기 능: 법무팀확인
	**************************************************************************/
	scwin.btnJdafConfirm_onclick = function() {
		var aprvNo           = dsTcfUseSealMngMap.get( "aprvNo" );
		var regYn            = dsTcfUseSealMngMap.get( "regYn" );
		var useSealMngStusCd = dsTcfUseSealMngMap.get( "useSealMngStusCd" );
		var useSealTkotStusNm = dsTcfUseSealMngMap.get("useSealTkotStusNm") ;// 사용인감 반출 가능 여부 (N:반출중.. 사용불가)
		var sealNo  = dsTcfUseSealMngMap.get("sealNo");
		var useSealGbCd =  dsTcfUseSealMngMap.get("useSealGbCd") ;     // CF0300201 :법인인감
		var deptNm    = "법무팀";
		var deptGubun = "JADF_CONFIRM";
		
		
		if(regYn == "Y" && useSealMngStusCd == "CF0300309" ){  //CF0300309	전략경영본부 신청
			scwin.fnConfirmPopup(deptNm,deptGubun);
		}
		else{
			if(aprvNo == "") return; 
			
			if(useSealMngStusCd != "CF0300303") return;   //CF0300303	결재완료
			
			// 반출중인 인감은 법무팀 승인 불가  by shryu1 2020-01-09   ==> 2020.01.13 원복요청함.
//			if( useSealGbCd !="CF0300201"  && sealNo !="" && useSealTkotStusNm == "N") {
//			   com.showMessage("CFW0010", "반출 인감임으로 날인이 불가합니다.");
//			   return ;
//			}
		    scwin.fnConfirmPopup(deptNm,deptGubun);
		}
		
	};
	
	scwin.fnConfirmPopup = function(deptNm,deptGubun){
	
		var useSealGbStr      = "";
		var useSealGbArr      =  scwin.fnGetUseSealGbArr();
		if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
        
        var popOps = {
				popup_name : deptNm +" 확인의견"
		       ,url        :"/ux/cf/cf0500/CF05000003P.xml"
		       ,data       :  {  deptNm      : deptNm
		                        ,deptGubn    : deptGubun 
		                        ,useSealGbNm : useSealGbStr
		                      }
		       ,widht       : "350"
			   ,height		: "250"
		       ,callbackFn : "scwin.fnConfirmPopupCallback"
		    };
		com.popup_open(popOps);
	
	};
	
	scwin.fnConfirmPopupCallback = function(data){
	
		if(data == "") return;
		
		var deptGubn          = "";
		var txaConfrimOpinion = "";
		var confirmStr        = "";
        var useSealGbStr      = "";
		var useSealGbArr      =  scwin.fnGetUseSealGbArr();
		var sngOrigFileCnt    = Number(dsTcfUseSealMngMap.get('sngOrigFileCnt'));
        
        if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
		
		if(data.deptGubn != undefined){
			deptGubn = data.deptGubn;
		}
		
		if(data.txaConfrimOpinion != undefined){
			txaConfrimOpinion = data.txaConfrimOpinion;
		}
		
		dsTcfUseSealMngMap.set( "changeStatusDiv"    , "" );
		if(deptGubn == "JADF_CONFIRM"){
			//법무팀 확인 
			dsTcfUseSealMngMap.set( "jdafTeamAproOpinCn" , txaConfrimOpinion ); //법무팀 확인의견
			dsTcfUseSealMngMap.set( "changeStatusDiv"    , "JDAF" );
			confirmStr = "[법무팀] "+useSealGbStr+" 날인신청을 승인하시겠습까?";
		} else if(deptGubn == "HR_CONFIRM"){
			//인사총무팀 확인
			dsTcfUseSealMngMap.set( "hrGeatmAproOpinCn" , txaConfrimOpinion ); //인사총무팀 확인의견
			dsTcfUseSealMngMap.set( "changeStatusDiv"    , "HR" );
			confirmStr = "[인사총무팀] "+useSealGbStr+" 날인신청을 승인하시겠습까?";
		}
		
		if(sngOrigFileCnt > 0)
		{
			confirmStr += "\n 기존 인감신청중 날인원본 미등록이 "+sngOrigFileCnt+"건 있습니다.";
		}
		
		if(!confirm(confirmStr)) return; 
		
		ajaxLib.ajax("/cf/CF05000001U/updateTcfUseSealMngConfrim", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();	
            	
            }
            , errCallback : "fnErrCallBack"
        });
		
	};
	
	/**************************************************************************
	 * Event: scwin.btnJdafReject_onclick
	 * 기 능: 법무팀반려
	**************************************************************************/
	scwin.btnJdafReject_onclick = function() {
		var useSealGbArr =  scwin.fnGetUseSealGbArr();
        var useSealGbStr = "";
        
        if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
        
        var confirmStr = "[법무팀]"+ useSealGbStr + " 날인 신청을 반려하시겠습니까";
        
        if(!confirm(confirmStr)) return;
        
        //반려처리 
        scwin.fnStatusReject();
	};
	
	/**************************************************************************
	 * Event: scwin.btnHrConfrim_onclick
	 * 기 능: 인사총무팀확인
	**************************************************************************/
	scwin.btnHrConfrim_onclick = function() {
		var aprvNo           = dsTcfUseSealMngMap.get( "aprvNo" );
		var useSealMngStusCd = dsTcfUseSealMngMap.get( "useSealMngStusCd" );
		var regYn            = dsTcfUseSealMngMap.get( "regYn" );
		var deptNm    = "인사총무팀";
		var deptGubun = "HR_CONFIRM";
		
		if(regYn == "Y" && useSealMngStusCd == "CF0300304"){
			scwin.fnConfirmPopup(deptNm,deptGubun);
		}else{
			if(aprvNo == "") return; 
			
			if(useSealMngStusCd != "CF0300304") return;
			
			scwin.fnConfirmPopup(deptNm,deptGubun);
		}
		
	};
	
	/**************************************************************************
	 * Event: scwin.btnHrReject_onclick
	 * 기 능: 인사총무팀반려
	**************************************************************************/
	scwin.btnHrReject_onclick = function() {
		var useSealGbArr =  scwin.fnGetUseSealGbArr();
        var useSealGbStr = "";
        
        if(useSealGbArr.length > 1){
        	useSealGbStr = useSealGbArr[0]; 
        } else {
        	useSealGbStr = useSealGbArr[useSealGbArr.length-1]; 
        }  
        
        var confirmStr = "[인사총무팀]"+ useSealGbStr + " 날인 신청을 반려하시겠습니까";
        
        if(!confirm(confirmStr)) return;
        
        //반려처리
        scwin.fnStatusReject();
	};
	
	/**************************************************************************
	 * Event: scwin.btnBzofConfirm_onclick
	 * 기 능: 영업소확인
	**************************************************************************/
	scwin.btnBzofConfirm_onclick = function() {
		var sngOffrNo = dsTcfUseSealMngMap.get( "sngOffrNo" );
		
		if(sngOffrNo == "") return;
		
		var confirmStr = "[영업소] 사용인감 날인 신청을 승인하시겠습니까";
        
        if(!confirm(confirmStr)) return;
        
        dsTcfUseSealMngMap.set( "changeStatusDiv"    , "BUSINESS_OFFICE" );
		
		ajaxLib.ajax("/cf/CF05000001U/updateTcfUseSealMngConfrim", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();	
            	
            }
            , errCallback : "fnErrCallBack"
        });
		
	};
	
	/**************************************************************************
	 * Event: scwin.btnSngRequest_onclick
	 * 기 능: 전략경영 날인신청
	**************************************************************************/
	scwin.btnSngRequest_onclick = function() {
		var sngOffrNo           = dsTcfUseSealMngMap.get( "sngOffrNo" );
		var useSealMngStusCd    = dsTcfUseSealMngMap.get("useSealMngStusCd") || ""; //사용인감관리상태코드
		
		if(sngOffrNo == "") return;
		
		//사용인감상태코드 값이 저장중이 아니면 return;
		if(useSealMngStusCd !="CF0300301") return;
		
		var confirmStr = "법인인감 날인을 요청하시겠습니까";
        
        if(!confirm(confirmStr)) return;
        
        dsTcfUseSealMngMap.set( "changeStatusDiv"    , "GROUP_REQUEST" );
		
		ajaxLib.ajax("/cf/CF05000001U/updateTcfUseSealMngConfrim", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsTcfUseSealMngMap.getJSON()
            , callback    : function(result,e){
            	
            	//초기 버튼 Control hide
				scwin.setAuthorityInit();
		
				scwin.fnSearchInfoList();	
            	
            }
            , errCallback : "fnErrCallBack"
        });
	};
