    var userNm = sessionStorage.getItem("userNm") || '';

    /************************************************************************************
     *  [2] onpageload(초기화포함)
     ************************************************************************************/
    scwin.onpageload = function() {
        scwin.initScreen();
    };

    scwin.onpageunload = function() {
    };

    // 화면 초기화(필수)
    scwin.initScreen = function() {
    
        userId = sessionStorage.getItem("userId") || '';
        userNm = sessionStorage.getItem("userNm") || '';

        // 데이터가 있을 경우에만 조회    
        var chgParams = com.getAllParam();

        if( chgParams.courtDocParam != undefined && chgParams.courtDocParam != "" ) {
            dsKeyTcfCourtDocMap.setJSON(JSON.parse(com.getAllParam().courtDocParam));
        }
        
        // 첨부파일
        com.setUdcValue("uFileList", "jobGbCd", "CF");
        com.setUdcValue("uFileList", "menuId", "CF05100001U");
        com.setUdcValue("uFileList", "aflId", "");
        com.setUdcValue("uFileList", "fileExt", "gif,jpg,jpeg,tif,tiff,doc,xls,ppt,pdf,txt,xlsx,png,pptx,docx,hwp");
        com.setUdcValue("uFileList", "usage", "INS");
        com.setUdcValue("uFileList", "fileSize", "INS");
        com.getUdcComp("uFileList", "gen_aflList").addClass("col2");

        scwin.fnGrdCourtDocSearch();
    };

    /**************************************************************************
     *  [3] 사용자정의 함수
     **************************************************************************/
    /**************************************************************************
     * 함 수 명: scwin.fnGrdCourtDocSearch
     * 기 능: 법원문서관리 조회
     **************************************************************************/
    scwin.fnGrdCourtDocSearch = function(){
        ajaxLib.ajax("/cf/CF05100000U/selectTcfCourtDoc", {
              mode        : "asynchronous"
            , method      : "POST"
            , requestData : dsKeyTcfCourtDocMap.getJSON()
            , callback    : "fnSearchCallback"
            , errCallback : "fnSearchErrCallBack"
        });

    };
    
    /**************************************************************************
     * 함 수 명: scwin.fnCourtDocInsert
     * 기 능: 법원문서관리 저장
     **************************************************************************/
    scwin.fnCourtDocInsert_onclick = function(){

        // 첨부파일ID 저장
        dsTcfCourtDocMap.set("aflId", com.getUdcComp("uFileList", "aflId").getValue());

        //유효성체크
        if( !scwin.fnValidate() ) return;

        if( dsTcfCourtDocMap.get("codocSeq") != "" ) {
            if( !confirm("수정하시겠습니까?") ) return;
            scwin.svcId = "updateTcfCourtDoc";
            ajaxLib.ajax("/cf/CF05100000U/updateTcfCourtDoc", {
                  mode        : "asynchronous"
                , method      : "POST"
                , requestData : dsTcfCourtDocMap.getJSON()
                , callback    : "fnCallback"
                , errCallback : "fnErrCallBack"
            });
        } else {
            if( !confirm("저장하시겠습니까?") ) return;
            scwin.svcId = "insertTcfCourtDoc";
            ajaxLib.ajax("/cf/CF05100000U/insertTcfCourtDoc", {
                  mode        : "asynchronous"
                , method      : "POST"
                , requestData : dsTcfCourtDocMap.getJSON()
                , callback    : "fnCallback"
                , errCallback : "fnErrCallBack"
            });
        } 
    };

    /**************************************************************************
     * 함 수 명: scwin.deleteTcfCourtDoc_onclick
     * 기 능: 법원문서관리 삭제
     **************************************************************************/
    scwin.deleteTcfCourtDoc_onclick = function() {
        if( !confirm("법원문서를 삭제하시겠습니까?") ) return;
        
        // 첨부파일ID 저장
        dsTcfCourtDocMap.set("aflId", com.getUdcComp("uFileList", "aflId").getValue());

        scwin.svcId = "deleteTcfCourtDoc";
        ajaxLib.ajax("/cf/CF05100000U/deleteTcfCourtDoc", {
            mode : "asynchronous",
            method : "POST",
            requestData : dsTcfCourtDocMap.getJSON(),
            callback    : "fnCallback",
            errCallback : "fnErrCallBack"
        });
    };

    /**************************************************************************
     * 함 수 명: scwin.updateJdafTeamOpinCn_onclick
     * 기 능: 법무팀의견 저장
     **************************************************************************/
    scwin.updateJdafTeamOpinCn_onclick = function() {
