        });
    };

    /**************************************************************************
     * 함 수 명: scwin.updateTcfCourtDocConfirm_onclick
     * 기 능: 법원문서관리 승인 
     **************************************************************************/
    scwin.updateTcfCourtDocConfirm_onclick = function() {
    
