    /**************************************************************************
     * 함 수 명: scwin.fnValidate
     * 기 능: 유효성체크
     **************************************************************************/
    scwin.fnValidate = function() {
        if (!com.validateGroup([menuInfoView])) {
            return false;
        }
        if( !com.isDate(codocRcvDt.getValue()) ) {
            com.showMessage("SCA0027","","","",codocRcvDt.id);
            return false;
        }
        if( codocRplyTmltDt.getValue() != "" && !com.isDate(codocRplyTmltDt.getValue()) ) { // 회신기한이 입력되어 있을 경우 형식이 맞지 않는다면
            com.showMessage("SCA0027","","","",codocRplyTmltDt.id);
            return false;
        }    
        if( dsTcfCourtDocMap.get("chrgTm1Plist") == "" || dsTcfCourtDocMap.get("chrgTm1Plist") == "[]" ) {
            com.showMessage("CFA0017", "담당팀 담당자");
            return false;
        }
        if( dsTcfCourtDocMap.get("jdafTm1Plist") == "" || dsTcfCourtDocMap.get("jdafTm1Plist") == "[]" ) {
            com.showMessage("CFA0017", "법무팀1차 담당자");
            return false;
        }
        if( dsTcfCourtDocMap.get("jdafTm2Plist") == "" || dsTcfCourtDocMap.get("jdafTm2Plist") == "[]" ) {
            com.showMessage("CFA0017", "법무팀2차 담당자");
            return false;
        }
        if( dsTcfCourtDocMap.get("aflId") == "" ) {
            com.showMessage("CFW0009", "문서사본");
            return false;
        }        
        
        return true;
    };

    /**************************************************************************
     *  [4] Callback
     **************************************************************************/

    /* 검색 결과 후처리*/
    scwin.fnSearchCallback = function(result, e) {
        // 조회데이터
        dsTcfCourtDocMap.setJSON(result.data.tcfCourtDoc);

        // 첨부파일
        com.setUdcValue("uFileList", "aflId", dsTcfCourtDocMap.get("aflId"));

        dsTcfCourtDocMap.set("chrgTm1PlistOrg", result.data.tcfCourtDoc.chrgTm1Plist);
        dsTcfCourtDocMap.set("chrgTm2PlistOrg", result.data.tcfCourtDoc.chrgTm2Plist);
        dsTcfCourtDocMap.set("rltnTmPlistOrg", result.data.tcfCourtDoc.rltnTmPlist);
        dsTcfCourtDocMap.set("jdafTm1PlistOrg", result.data.tcfCourtDoc.jdafTm1Plist);
        dsTcfCourtDocMap.set("jdafTm2PlistOrg", result.data.tcfCourtDoc.jdafTm2Plist);

        // 최초 데이터 로드시
        if( dsTcfCourtDocMap.get("jdafTm2Plist") == "" ) {
            // 김형수 부장 고정
            var empJdaf = new Object();
            empJdaf.empno = "047207";
            empJdaf.confStafNm = "김형수";
            empJdaf.conprJbclNm = "부장";
            empJdaf.deptNm = "법무팀";
            var empJdafArray = new Array();
            empJdafArray.push(empJdaf);
            dsTcfCourtDocMap.set("jdafTm2Plist", JSON.stringify(empJdafArray));
        }

        // 화면 구분값 세팅
        scwin.fnMakeGubnStr();

        // 화면 권한처리
        scwin.setAuthority();

    };
    
    /* 결과 후처리*/
    scwin.fnCallback = function(result, e) {
//        console.log(e);

        switch(scwin.svcId) {
            case "insertTcfCourtDoc" :
//                scwin.btnList_onclick(); // 저장 후 목록으로 이동 
                  dsKeyTcfCourtDocMap.set("codocSeq",result.data);                 
                  scwin.fnGrdCourtDocSearch();  // 저장후 조회로 이동(팀 확인요청을 바로 신청하도록)
                break;
            case "updateTcfCourtDoc" :
                scwin.fnGrdCourtDocSearch();
                break;
            case "deleteTcfCourtDoc" :
                scwin.btnList_onclick(); // 삭제 후 목록으로 이동
                break;
            case "updateTcfCourDocConfReq" :
                scwin.fnSendMail(); // 이메일보내기
                scwin.fnGrdCourtDocSearch(); // 확인후 다시조회
                break;
            case "updateTcfCourtDocConfirm" :
                scwin.fnGrdCourtDocSearch(); // 승인후 다시조회
                break;
            case "updateTcfCourtDocReject" :
                // 법무팀 2차담당자 반려시 이메일전송
                if( dsTcfCourtDocMap.get("codocMngTeamGbCd") == "CF03302004" ) {
                    scwin.fnSendMail(); // 이메일보내기
                }
                scwin.fnGrdCourtDocSearch(); // 반려후 다시조회
                break;
       }
    };

    /* 검색 에러 후처리 */
    scwin.fnSearchErrCallBack = function(e) {
        console.log('search Error!!!!!!!!!!!');
    }

    /* 에러 후처리 */
    scwin.fnErrCallBack = function(e) {
        console.log(e);

        switch(scwin.svcId) {
            case "selectTcfCourtDoc" :
                console.log('search Error!!!!!!!!!!!');
                break;
            case "insertTcfCourtDoc" :
                console.log('insert Error!!!!!!!!!!!');
                break;
            case "updateTcfCourtDoc" :
                console.log('update Error!!!!!!!!!!!');
                break;
            case "deleteTcfCourtDoc" :
                console.log('delete Error!!!!!!!!!!!');
                break;
            case "updateTcfCourDocConfReq" :
                console.log('request Error!!!!!!!!!!!');
                break;
            case "updateTcfCourtDocConfirm" :
                console.log('confirm Error!!!!!!!!!!!');
                break;
            case "updateTcfCourtDocReject" :
                console.log('Reject Error!!!!!!!!!!!');
                break;
       }
    };

