    /* 목록으로 이동 */
    scwin.btnList_onclick = function() {
        var scrnParam = com.getAllParam();
        com.detailOpen("법원문서관리조회", "CF0510", "/ux/cf/cf0510/CF05100000U.xml", "1", scrnParam);
    };

    /* 배열로 구분에 표현해줄 스트링 생성 */
    scwin.fnMakeGubnStr = function() {
    
        // 화면 출력 세팅
        var makeStr = function(pListStr, pListObj){

            if( pListStr == "" ) return;

            var empArray = JSON.parse(pListStr);
            var plistArray = new Array();
    
            $.each(empArray, function(idx, item){
                var object = item.confStafNm + " " + item.conprJbclNm + "[" + item.deptNm + "]";
                plistArray.push(object);
            });
    
            pListObj.setValue(plistArray.join(" ,"));
        };

        makeStr(dsTcfCourtDocMap.get("chrgTm1Plist"), chrgTm1Plist); // 구분 담당팀1 세팅
        makeStr(dsTcfCourtDocMap.get("rltnTmPlist"), rltnTmPlist);   // 구분 관련팀  세팅
        makeStr(dsTcfCourtDocMap.get("jdafTm1Plist"), jdafTm1Plist); // 구분 법무팀1 세팅
        makeStr(dsTcfCourtDocMap.get("jdafTm2Plist"), jdafTm2Plist); // 구분 법무팀2 세팅
    };
    
    /* 화면 권한처리 최초 */
    scwin.setAuthorityInit = function(){
        btnCtrlConf.setStyle( "display" , "none" );
        btnCtrlRejt.setStyle( "display" , "none" );
        btnCtrlSave.setStyle( "display" , "none" );
        btnCtrlDel.setStyle( "display" , "none" );
        btnCtrlReq.setStyle( "display" , "none" );
        ctrlPopup1.setStyle( "display" , "none" );
        ctrlPopup2.setStyle( "display" , "none" );
        ctrlPopup3.setStyle( "display" , "none" );
        inputCtrlOpin.setStyle( "display" , "none" );        
    };
    
    /* 화면 권한처리 */
    scwin.setAuthority = function(){

        // 최초처리
        scwin.setAuthorityInit();

        // 승인/반려 가능
        if( dsTcfCourtDocMap.get("btnCtrlConf") == "Y" ) {
            btnCtrlConf.setStyle( "display" , "" );
            btnCtrlRejt.setStyle( "display" , "" );
        }
        
        // 저장 가능
        if( dsTcfCourtDocMap.get("btnCtrlSave") == "Y" ) {
            btnCtrlSave.setStyle( "display" , "" );
        }
        
        // 삭제 가능
        if( dsTcfCourtDocMap.get("btnCtrlDel") == "Y" ) {
            btnCtrlDel.setStyle( "display" , "" );
        }
        
        // 팀 확인요청 가능
        if( dsTcfCourtDocMap.get("btnCtrlReq") == "Y" ) {
            btnCtrlReq.setStyle( "display" , "" );
        }

        // 팝업 등 가능
        if( dsTcfCourtDocMap.get("btnCtrlPopup") == "Y" ) {
            ctrlPopup1.setStyle( "display" , "" );
            ctrlPopup2.setStyle( "display" , "" );
            ctrlPopup3.setStyle( "display" , "" );
        }

        // 법무팀의견저장 가능
        if( dsTcfCourtDocMap.get("inputCtrlOpin") == "Y" ) {
            inputCtrlOpin.setStyle( "display" , "" );
        }

        // 기타 컨트롤 입력 불가처리 (기본 : 입력가능)
        if( dsTcfCourtDocMap.get("inputCtrl") == "N" ) {
            docNm.setDisabled(true); docNm.removeClass("w2input_disabled");
            sndlcNm.setDisabled(true); sndlcNm.removeClass("w2input_disabled");
            evtNo.setDisabled(true); evtNo.removeClass("w2input_disabled");
            pltfNm.setDisabled(true); pltfNm.removeClass("w2input_disabled");
            lawsPyrqAmt.setDisabled(true); lawsPyrqAmt.removeClass("w2input_disabled");
            dfdntNm.setDisabled(true); dfdntNm.removeClass("w2input_disabled");
            debtrNm.setDisabled(true); debtrNm.removeClass("w2input_disabled");
            codocRcvDt.setReadOnly(true);
            codocRplyTmltDt.setReadOnly(true);
        }
        
        // 파일쓰기 불가
        if( dsTcfCourtDocMap.get("btnCtrlAfl") == "R" ) {
            com.getUdcComp("uFileList", "btnAddFile").removeClass("show");
            com.getUdcComp("uFileList", "btnAddFile").addClass("hide");
        }

    };

