    /**************************************************************************
     * 함 수 명: scwin.fnSendMail
     * 기 능: 메일전송
     **************************************************************************/
    scwin.fnSendMail = function () {
        ajaxLib.ajax("/cf/CFTelepia/sendMail", {
            mode: "asynchronous", // 이메일 처리 부분은 동기식으로 처리
            method: "POST", 
            requestData: {
                title: "[법무팀] 법원문서 관리시스템 등록사항 확인 요청의 건",
                content: "<html><body><table border='0' cellspacing='0' cellpadding='0'><tr align='center'><td nowrap ><div style='overflow:auto; width:800px; height:80px;padding-left:0px'><table width='800' border='0' cellspacing='0' cellpadding='0'><tr><td height='80' valign='top'  >금일(메일 발송일 기준) 본점지사에 접수된 법원문서가 관리시스템에 등록되었습니다. <br><br>담당팀 및 관련팀 담당자께서는 문서내용을 확인하신 후 승인 바랍니다.<br><br>※ QUICK MENU>주요신청조회>법원문서관리</td></tr></table></div></td></tr></table></body></html>",
                senderAccount: userId+"@sdcit.sdcitGroup.com", //보낸이계정
                senderMailAddress: userId+"@sdcit.sdcitGroup.com", //보낸이 메일 주소
                senderName: userNm, //보낸이 성명
                receiverTos: scwin.fnMakeReceiveTosList(), //받는이 이메일 , 받는이 이름 세팅
                aflId: com.getUdcComp("uFileList", "aflId").getValue(), //파일첨부 aflId
                lockContent: "N", //내용변경금지 Y/N
                isScheduled: "N", //예약전송여부 Y/N
                isSaveInSent: "N", //보낸메일저장 Y/N
                isUi: "Y" //Y : UI가있는 메일발송 , N: 다이렉트 메일발송
            },
            callback: "fnCallbackEmail",
            errCallback: "fnErrCallBackEmail"
        });
    };

    /**************************************************************************
     * 함 수 명: scwin.fnMakeReceiveTosList
     * 기 능: 수신자 정보 set
     **************************************************************************/
    // 동일한 사람 중복 제거 추가요망
    scwin.fnMakeReceiveTosList = function () {
        var rcvrEmail = dsTcfCourtDocMap.get("rcvrEmail").split(",");
        var rcvrEmailUserNm = dsTcfCourtDocMap.get("rcvrEmailUserNm").split(",");
        
        var array = new Array();
        $.each(rcvrEmail, function(idx, item){
            var object = new Object();
            object.mailAddress = item;
            object.name = rcvrEmailUserNm[idx];
            array.push(object);
        });

        return array;
    };

    /* 이메일 결과 후처리*/
    scwin.fnCallbackEmail = function (result, e) {
        var data = result.data.mailData;
        var isUi = data.isUi;
        var xml = data.xml;
        var url = data.url;
        if ("Y" == isUi) {
            var $form = $("<form></form>");
    
            var newwin = window.open('about:blank', 'gw_mail', 'top=0, left=0, width=1014, height=694, status=yes, toolbar=no,location=no,directories=no,menubar=no,scrollbars=yes,resizable=no');
            newwin.focus(); //새창을 띄울때 항상 위
    
            $form.attr("target", "gw_mail");
            $form.attr("action", url);
            $form.attr("method", "post")
            $form.attr("accept-language", "ko")
            $form.appendTo('body');
            var $input = $("<input></input>");
            $input.attr({
                type: "hidden",
                name: "Message",
                id: "Message",
                value: xml
            });
            $input.appendTo($form);
            $form.submit();
        } else {
            com.showMessage("CFA0016","다이렉트 메일 발송이");
        }
    };

    /* 이메일 에러 후처리 */
    scwin.fnErrCallBackEmail = function (e) {
        console.log('search Error!!!!!!!!!!!');
    };
