	/************************************************************************************
     *  [1] 전역변수 
     ************************************************************************************/
	scwin.svcId   = "";       
	scwin.nowPage = 1; 
	scwin.adminCheck = false;
	
    /************************************************************************************ 
     *  [2] onpageload(초기화포함)
     ************************************************************************************/   
    scwin.onpageload = function() { 

		scwin.initScreen();
		
		//페이지리스트 초기화
     	com.pageListObj.init();
     	
    };

    scwin.onpageunload = function() {
		
	};

	// 화면 초기화(필수)
	scwin.initScreen = function() {
		var deptCd     = sessionStorage.getItem("deptCd") || '';
     	
     	grpSearchArea.init();
     	grdOfdcSndList.initGrid();
     	grdOfdcSndExcelList.initGrid();
     	
     	if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().scwin;
            		 
            	if (typeof(parentObj["fnAdminChk"]) == "function"){
            	  scwin.adminCheck = parentObj["fnAdminChk"]();
            	}
            }
		}else{
			scwin.adminCheck = scwin.fnAdminChk();
		}
		
		//관리자 가 아닐시 자기팀만 검색
		if(!scwin.adminCheck){
			dsSearchMap.set( "keyOfdcSndDeptCd" , deptCd );
			ofdcSndDeptNmTh.setStyle( "display" , "none" );
			ofdcSndDeptNmTd.setStyle( "display" , "none" );
			ibxKeyOfdcSndDeptNm.setReadOnly( true );
		}
		else
		{
			btnExcelDwn.setStyle( "display" , "none" );
		}
		
		var nowDate   = $w.getCurrentServerDate();
		//var startDate = dateLib.addMonths(nowDate,-12);
		var startDate=  com.transDateType("frQuarter1");
		
		//com.setUdcValue( "udcPeriodcalendar", "frDate", startDate);
        //com.setUdcValue( "udcPeriodcalendar", "toDate", nowDate);
		
		var param = com.getAllParam();
		com.setParam(gcm.tab_main.getSelectedTabID()); 
		
		if(param.ofdcSndNo == undefined){
			scwin.pageMove(scwin.nowPage);
		} else if(param.ofdcSndNo != "") {
			dsSearchMap.setJSON(param);
			
			com.setUdcValue( "udcPeriodcalendar", "frDate", param.keyStartOfdcSndDt);
	        com.setUdcValue( "udcPeriodcalendar", "toDate", param.keyEndOfdcSndDt);
	        
	        // 리스트 조회
	        scwin.nowPage = dsSearchMap.get("pageRowStart")/14+1;  // 현재페이지
	        scwin.pageMove(scwin.nowPage);
		}
	}; 

	/**************************************************************************
	 *  [3] 사용자정의 함수
	 **************************************************************************/
	scwin.fnRefresh = function(){
		scwin.pageMove(scwin.nowPage);
	} 
	/**************************************************************************
	* 함 수 명: scwin.fnAdminChk
	* 기 능: ADMIN CHECK
	**************************************************************************/ 
	scwin.fnAdminChk = function(){
		var userEmpno       = sessionStorage.getItem("userEmpno") || '';
		var tkotAdminEmpNo  = gcm.khProp.tkotAdminEmpno;
		
		 //인감관리 사번
    	if(tkotAdminEmpNo.indexOf(userEmpno) > -1)
    	{
	        return true;
	    }
	    
	    return false;
	}
	/**************************************************************************
	* 함 수 명: scwin.fnSearchList
	* 기 능: 조회 List
	**************************************************************************/
    scwin.fnSearchList = function() {
    
    	var fromDate = com.getUdcValue("udcPeriodcalendar","frDate");
		var toDate   = com.getUdcValue("udcPeriodcalendar","toDate");
		
		//UDC 에서 해당 데이터 데이터맵에 SET
		dsSearchMap.set( "keyStartOfdcSndDt" , fromDate );
		dsSearchMap.set( "keyEndOfdcSndDt"   , toDate );
    	
    	scwin.svcId = "selectTcfOfdcSndList";
		
        ajaxLib.ajax("/cf/CF05150030U/selectTcfOfdcSndList", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsSearchMap.getJSON()
            , callback    : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };	

	/**************************************************************************
	 * Event: scwin.sbx_SelectCount_onviewchange
	 * 기 능: 페이지 목록 갯수 선택이벤트(10개,20개,30개,40개,50개)
	 **************************************************************************/	
	scwin.sbx_SelectCount_onviewchange = function(info) {
		if(info.oldValue != info.newValue) {
			grdOfdcSndList.options.visibleRowNum = sbx_SelectCount.getValue();
		}
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.pageMove
	 * 기 능: 조회 시작값, 가져올 행수 지정후 리스트 조회
	 **************************************************************************/
	scwin.pageMove = function(current){
		
		var end = sbx_SelectCount.getValue();		
		if( end == null || end == "") end = 10;
		var start = Math.abs( (current-1) ) * end;
		
		scwin.nowPage = current;
		dsSearchMap.set( "pageRowStart" , start );
		dsSearchMap.set( "pageRowCount" , end );
		
		scwin.fnSearchList();
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnSndDtFormatter
	* 기 능: 발신일자 포캣터
	**************************************************************************/
	scwin.fnSndDtFormatter = function(value,formatedNm, row, col){
		
		var str = "";
		
		if(value == "") return "";
		
		if(value != "")
		{
			str = com.transDate(value,'dash') ;
		}
		
		return str;
		
	}
	
	/**************************************************************************
	* 함 수 명: scwin.fnAflFileFormatter
	* 기 능: 첨부파일 이미지 포맷터
	**************************************************************************/
	scwin.fnAflFileFormatter = function(value,formatedNm, row, col){
	
		var str = "";
		
		if(value == "") return "";
		
		if(value != "")
		{
			str = '<img src="/resources/images/content/ico_file_list.png" style=" margin-top : 6px; width : 15px; height : 15px; cursor:default; " />' ;
		}
		
		return str;
		
	};
		
	/**************************************************************************
	 *  [4] Callback
	 **************************************************************************/
	
	/* 결과 후처리*/
	scwin.fnCallback = function(result, e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfOfdcSndList" :
	   		
	   			//전체 조회건수
	   			var pageTotalCount = result.data.pageTotalCount;
	   		
				//페이지 네비게이션 값 설정
	   			var pageRowCount = 10;
	   			var pageSelectCount = sbx_SelectCount.getValue();
	   			if(pageSelectCount != null && pageSelectCount > 0){
	   				pageRowCount = pageSelectCount;
	   			}
	   			var pageNaviCount = Math.ceil(pageTotalCount / pageRowCount);
	   			
	   			//전체 조회 건수
				com.setUdcValue("dsTotalCount", "udcListCount", pageTotalCount);
	   			
	   			dsTcfOfdcSndList.setJSON(result.data.dsTcfOfdcSndList);
	   			
	   			//페이지 네비게이션				
				var paramObj = { 
                    		  max     :pageNaviCount  // 페이지의 총 Count를 입력합니다. [필수] 
                    		 ,svcId   :scwin.svcId 	  // 해당서비스 id 고정[필수] 이부분은 그대로 사용합니다.
                    		 ,current :scwin.nowPage  // 현재페이지 
                 			};
                 			
  				com.pageListObj.max(paramObj); 	   
  				
  				if(!scwin.adminCheck){
  					grdOfdcSndList.setColumnVisible( "ofdcSndDeptNm" , false );
  				}	

	   		break;
	   }        
	};
	
	/* 에러 후처리 */
    scwin.fnErrCallBack = function(e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfOfdcSndList" :
	   			console.log('search Error!!!!!!!!!!!');
	   		break;
	   }        
    };
     
    /************************************************************************************
     *  [5] Event 함수 
     ************************************************************************************/
    /**************************************************************************
	* Event: scwin.btnFind_onclick
	* 기 능: 공문발신 목록 조회
	**************************************************************************/
	scwin.btnFind_onclick = function() {
		scwin.pageMove(1);
	};
	
	/**************************************************************************
	* Event: scwin.btnNew_onclick
	* 기 능: 발신공문작성
	**************************************************************************/
	scwin.btnNew_onclick = function() {
	
		var param = new Object();
		param.keyOfdcSndTtl         = dsSearchMap.get('keyOfdcSndTtl'); 
		param.keyOfdcSndChrgrNm     = dsSearchMap.get('keyOfdcSndChrgrNm');
		param.keyOfdcSndDeptCd = dsSearchMap.get('keyOfdcSndDeptCd');
		param.keyStartOfdcSndDt     = dsSearchMap.get('keyStartOfdcSndDt');
		param.keyEndOfdcSndDt       = dsSearchMap.get('keyEndOfdcSndDt');
		param.keyOfdcSndDocNo       = dsSearchMap.get('keyOfdcSndDocNo');
		
		param.callId                = "CF05150030U_CF05150031U";
		param.callPageUrl           = "/ux/cf/cf0515/CF05150030U.xml"
		param.callTitle             = "발신공문";
		
		com.detailOpen("발신공문(상세)", "CF05150030U_CF05150031U", "/ux/cf/cf0515/CF05150031U.xml", "1", param) ;
		
	};
	
	/**************************************************************************
	* Event: grdOfdcSndList_oncellclick
	* 기 능: 공문발신 그리드 원클릭시 
	**************************************************************************/
	scwin.grdOfdcSndList_oncellclick = function(row,col) {
		var rowObj  = dsTcfOfdcSndList.getRowJSON(row);
		var colId   = grdOfdcSndList.getColumnID(col);
		
		if(colId == "ofdcSndTtl"){
			var param            = dsSearchMap.getJSON();
			param.ofdcSndNo      = rowObj.ofdcSndNo;
			param.callId         = "CF05150030U_CF05150031U";
			param.callPageUrl    = "/ux/cf/cf0515/CF05150030U.xml"
			param.callTitle      = "발신공문";
		
			com.detailOpen("발신공문(상세)", "CF05150030U_CF05150031U", "/ux/cf/cf0515/CF05150031U.xml", "1", param) ;
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcSndTtl_onkeyup
	* 기 능: 검색박스 제목 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcSndTtl_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcSndChggnNm_onkeyup
	* 기 능: 검색박스 담당자 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcSndChggnNm_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcSndDocNo_onkeyup
	* 기 능: 검색박스 문서번호 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcSndDocNo_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcSndDeptNm_onkeyup
	* 기 능: 검색박스 담당부서 엔터키 입력시
	**************************************************************************/
	scwin.ibxKeyOfdcSndDeptNm_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: scwin.btnExcelDwn_onclick
	* 기 능: 엑셀다운로드
	**************************************************************************/
	scwin.btnExcelDwn_onclick = function() {
		 ajaxLib.ajax("/cf/CF05150030U/selectTcfOfdcSndExcelList", {
            mode: "asynchronous",
            method: "POST",
            requestData: dsSearchMap.getJSON(),
            callback: function(result,e) {
				var tcfOfdcSndExcelList = result.data.dsTcfOfdcSndExcelList;
				
				dsTcfOfdcSndExcelList.setJSON(tcfOfdcSndExcelList);
				
				if(grdOfdcSndExcelList.getTotalRow() <= 0){
		            com.showMessage("CMW0042");     //다운로드할 내역이 존재하지 않습니다.
		            return;
		        }else{
		            var excelOpt = {};  //excel download 옵션
		            var nowDate   = $w.getCurrentServerDate('yyyyMMddHHmmss');
		            excelOpt.fileName = "발신공문_"+nowDate+".xlsx";    //또는 xlsx 확장자 사용
		            grdOfdcSndExcelList.advancedExcelDownload( excelOpt );
		        }
				          	
            },
            errCallback: function(result,e) { 
	        }
        });		
	};
