	/************************************************************************************
     *  [1] 전역변수 
     ************************************************************************************/
	scwin.svcId   = "";       
	scwin.nowPage = 1; 
	scwin.adminCheck = false;
	
    /************************************************************************************ 
     *  [2] onpageload(초기화포함)
     ************************************************************************************/   
    scwin.onpageload = function() { 

		scwin.initScreen();
		
		//페이지리스트 초기화
     	com.pageListObj.init();
     	
    };

    scwin.onpageunload = function() {
		
	};

	// 화면 초기화(필수)
	scwin.initScreen = function() {
		var deptCd     = sessionStorage.getItem("deptCd") || '';
     	
     	grpSearchArea.init();
     	grdOfdcCntrtList.initGrid();
     	grdOfdcCntrtExcelList.initGrid();
     	
     	if($w.isPopup()){
			var winPopParam = JSON.parse(sessionStorage.winpopParam);
            var parentId    = winPopParam.dataObj.data.pframeId;
            if(parentId != undefined && parentId != "") {
            	var parentObj = opener.frames[parentId].getWindow().scwin;
            		 
            	if (typeof(parentObj["fnAdminChk"]) == "function"){
            	  scwin.adminCheck = parentObj["fnAdminChk"]();
            	}
            }
		}else{
			scwin.adminCheck = scwin.fnAdminChk();
		}
		
		//관리자 가 아닐시 자기팀만 검색
		if(!scwin.adminCheck){
			dsSearchMap.set( "keyOfdcCntrtDeptCd" , deptCd );
			ofdcCntrtDeptNmTh.setStyle( "display" , "none" );
			ofdcCntrtDeptNmTd.setStyle( "display" , "none" );
			ibxKeyOfdcCntrtDeptNm.setReadOnly( true );
		}
		else
		{
			btnExcelDwn.setStyle( "display" , "none" );
		}
		
		var nowDate   = $w.getCurrentServerDate();
		//var startDate = dateLib.addMonths(nowDate,-12);
		var startDate=  com.transDateType("frQuarter1");
		
		//com.setUdcValue( "udcPeriodcalendar", "frDate", startDate);
        //com.setUdcValue( "udcPeriodcalendar", "toDate", nowDate);
		
		var param = com.getAllParam();
		com.setParam(gcm.tab_main.getSelectedTabID()); 
		
		if(param.ofdcCntrtNo == undefined){
			scwin.pageMove(scwin.nowPage);
		} else if(param.ofdcCntrtNo != "") {
			dsSearchMap.setJSON(param);
			
			com.setUdcValue( "udcPeriodcalendar", "frDate", param.keyStartOfdcCntrtDt);
	        com.setUdcValue( "udcPeriodcalendar", "toDate", param.keyEndOfdcCntrtDt);
	        
	        // 리스트 조회
	        scwin.nowPage = dsSearchMap.get("pageRowStart")/14+1;  // 현재페이지
	        scwin.pageMove(scwin.nowPage);
		}
	}; 

	/**************************************************************************
	 *  [3] 사용자정의 함수
	 **************************************************************************/
	scwin.fnRefresh = function(){
		scwin.pageMove(scwin.nowPage);
	} 
	/**************************************************************************
	* 함 수 명: scwin.fnAdminChk
	* 기 능: ADMIN CHECK
	**************************************************************************/ 
	scwin.fnAdminChk = function(){
		var userEmpno       = sessionStorage.getItem("userEmpno") || '';
		var tkotAdminEmpNo  = gcm.khProp.tkotAdminEmpno;
		
		 //인감관리 사번
    	if(tkotAdminEmpNo.indexOf(userEmpno) > -1)
    	{
	        return true;
	    }
	    
	    return false;
	}
	/**************************************************************************
	* 함 수 명: scwin.fnSearchList
	* 기 능: 조회 List
	**************************************************************************/
    scwin.fnSearchList = function() {
    
    	var fromDate = com.getUdcValue("udcPeriodcalendar","frDate");
		var toDate   = com.getUdcValue("udcPeriodcalendar","toDate");
		
		//UDC 에서 해당 데이터 데이터맵에 SET
		dsSearchMap.set( "keyStartOfdcCntrtDt" , fromDate );
		dsSearchMap.set( "keyEndOfdcCntrtDt"   , toDate );
    	
    	scwin.svcId = "selectTcfOfdcCntrtList";
		
        ajaxLib.ajax("/cf/CF05150050U/selectTcfOfdcCntrtList", {
        	  mode        : "asynchronous"
        	, method      : "POST"
            , requestData : dsSearchMap.getJSON()
            , callback    : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };	

	/**************************************************************************
	 * Event: scwin.sbx_SelectCount_onviewchange
	 * 기 능: 페이지 목록 갯수 선택이벤트(10개,20개,30개,40개,50개)
	 **************************************************************************/	
	scwin.sbx_SelectCount_onviewchange = function(info) {
		if(info.oldValue != info.newValue) {
			grdOfdcCntrtList.options.visibleRowNum = sbx_SelectCount.getValue();
		}
	};
	
	/**************************************************************************
	 * 함 수 명: scwin.pageMove
	 * 기 능: 조회 시작값, 가져올 행수 지정후 리스트 조회
	 **************************************************************************/
	scwin.pageMove = function(current){
		
		var end = sbx_SelectCount.getValue();		
		if( end == null || end == "") end = 10;
		var start = Math.abs( (current-1) ) * end;
		
		scwin.nowPage = current;
		dsSearchMap.set( "pageRowStart" , start );
		dsSearchMap.set( "pageRowCount" , end );
		
		scwin.fnSearchList();
	};
	
	/**************************************************************************
	* 함 수 명: scwin.fnSndDtFormatter
	* 기 능: 계약일자 포캣터
	**************************************************************************/
	scwin.fnSndDtFormatter = function(value,formatedNm, row, col){
		
		var str = "";
		
		if(value == "") return "";
		
		if(value != "")
		{
			str = com.transDate(value,'dash') ;
		}
		
		return str;
		
	}
	
	/**************************************************************************
	* 함 수 명: scwin.fnAflFileFormatter
	* 기 능: 첨부파일 이미지 포맷터
	**************************************************************************/
	scwin.fnAflFileFormatter = function(value,formatedNm, row, col){
	
		var str = "";
		
		if(value == "") return "";
		
		if(value != "")
		{
			str = '<img src="/resources/images/content/ico_file_list.png" style=" margin-top : 6px; width : 15px; height : 15px; cursor:default; " />' ;
		}
		
		return str;
		
	};
		
	/**************************************************************************
	 *  [4] Callback
	 **************************************************************************/
	
	/* 결과 후처리*/
	scwin.fnCallback = function(result, e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfOfdcCntrtList" :
	   		
	   			//전체 조회건수
	   			var pageTotalCount = result.data.pageTotalCount;
	   		
				//페이지 네비게이션 값 설정
	   			var pageRowCount = 10;
	   			var pageSelectCount = sbx_SelectCount.getValue();
	   			if(pageSelectCount != null && pageSelectCount > 0){
	   				pageRowCount = pageSelectCount;
	   			}
	   			var pageNaviCount = Math.ceil(pageTotalCount / pageRowCount);
	   			
	   			//전체 조회 건수
				com.setUdcValue("dsTotalCount", "udcListCount", pageTotalCount);
	   			
	   			dsTcfOfdcCntrtList.setJSON(result.data.dsTcfOfdcCntrtList);
	   			
	   			//페이지 네비게이션				
				var paramObj = { 
                    		  max     :pageNaviCount  // 페이지의 총 Count를 입력합니다. [필수] 
                    		 ,svcId   :scwin.svcId 	  // 해당서비스 id 고정[필수] 이부분은 그대로 사용합니다.
                    		 ,current :scwin.nowPage  // 현재페이지 
                 			};
                 			
  				com.pageListObj.max(paramObj); 	   
  				
  				if(!scwin.adminCheck){
  					grdOfdcCntrtList.setColumnVisible( "ofdcCntrtDeptNm" , false );
  				}	

	   		break;
	   }        
	};
	
	/* 에러 후처리 */
    scwin.fnErrCallBack = function(e) {
        console.log(e);
        
	   	switch(scwin.svcId) {
	   		case "selectTcfOfdcCntrtList" :
	   			console.log('search Error!!!!!!!!!!!');
	   		break;
	   }        
    };
     
    /************************************************************************************
     *  [5] Event 함수 
     ************************************************************************************/
    /**************************************************************************
	* Event: scwin.btnFind_onclick
	* 기 능: 공문계약 목록 조회
	**************************************************************************/
	scwin.btnFind_onclick = function() {
		scwin.pageMove(1);
	};
	
	/**************************************************************************
	* Event: scwin.btnNew_onclick
	* 기 능: 계약작성
	**************************************************************************/
	scwin.btnNew_onclick = function() {
	
		var param = new Object();
		param.keyOfdcCntrtTtl         = dsSearchMap.get('keyOfdcCntrtTtl'); 
		param.keyOfdcCntrtChrgrNm     = dsSearchMap.get('keyOfdcCntrtChrgrNm');
		param.keyOfdcCntrtDeptCd      = dsSearchMap.get('keyOfdcCntrtDeptCd');
		param.keyStartOfdcCntrtDt     = dsSearchMap.get('keyStartOfdcCntrtDt');
		param.keyEndOfdcCntrtDt       = dsSearchMap.get('keyEndOfdcCntrtDt');
		param.keyOfdcCntrtDocNo       = dsSearchMap.get('keyOfdcCntrtDocNo');
		
		param.callId                = "CF05150050U_CF05150051U";
		param.callPageUrl           = "/ux/cf/cf0515/CF05150050U.xml"
		param.callTitle             = "계약";
		
		com.detailOpen("계약(상세)", "CF05150050U_CF05150051U", "/ux/cf/cf0515/CF05150051U.xml", "1", param) ;
		
	};
	
	/**************************************************************************
	* Event: grdOfdcCntrtList_oncellclick
	* 기 능: 공문계약 그리드 원클릭시 
	**************************************************************************/
	scwin.grdOfdcCntrtList_oncellclick = function(row,col) {
		var rowObj  = dsTcfOfdcCntrtList.getRowJSON(row);
		var colId   = grdOfdcCntrtList.getColumnID(col);
		
		if(colId == "ofdcCntrtTtl"){
			var param            = dsSearchMap.getJSON();
			param.ofdcCntrtNo      = rowObj.ofdcCntrtNo;
			param.callId         = "CF05150050U_CF05150051U";
			param.callPageUrl    = "/ux/cf/cf0515/CF05150050U.xml"
			param.callTitle      = "계약";
		
			com.detailOpen("계약(상세)", "CF05150050U_CF05150051U", "/ux/cf/cf0515/CF05150051U.xml", "1", param) ;
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcCntrtTtl_onkeyup
	* 기 능: 검색박스 제목 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcCntrtTtl_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcCntrtChggnNm_onkeyup
	* 기 능: 검색박스 담당자 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcCntrtChggnNm_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcCntrtDocNo_onkeyup
	* 기 능: 검색박스 문서번호 엔터키 입력시 
	**************************************************************************/
	scwin.ibxKeyOfdcCntrtDocNo_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: ibxKeyOfdcCntrtDeptNm_onkeyup
	* 기 능: 검색박스 담당부서 엔터키 입력시
	**************************************************************************/
	scwin.ibxKeyOfdcCntrtDeptNm_onkeyup = function(e) {
		if (e.keyCode == '13') {
			scwin.pageMove(1);
		}
	};
	
	/**************************************************************************
	* Event: scwin.btnExcelDwn_onclick
	* 기 능: 엑셀다운로드
	**************************************************************************/
	scwin.btnExcelDwn_onclick = function() {
		 ajaxLib.ajax("/cf/CF05150050U/selectTcfOfdcCntrtExcelList", {
            mode: "asynchronous",
            method: "POST",
            requestData: dsSearchMap.getJSON(),
            callback: function(result,e) {
				var tcfOfdcCntrtExcelList = result.data.dsTcfOfdcCntrtExcelList;
				
				dsTcfOfdcCntrtExcelList.setJSON(tcfOfdcCntrtExcelList);
				
				if(grdOfdcCntrtExcelList.getTotalRow() <= 0){
		            com.showMessage("CMW0042");     //다운로드할 내역이 존재하지 않습니다.
		            return;
		        }else{
		            var excelOpt = {};  //excel download 옵션
		            var nowDate   = $w.getCurrentServerDate('yyyyMMddHHmmss');
		            excelOpt.fileName = "계약_"+nowDate+".xlsx";    //또는 xlsx 확장자 사용
		            grdOfdcCntrtExcelList.advancedExcelDownload( excelOpt );
		        }
				          	
            },
            errCallback: function(result,e) { 
	        }
        });		
	};
