 /**************************************************************************
 * [3] 사용자정의 함수
 **************************************************************************/

  	/**************************************************************************
	 * 함 수 명: scwin.fnTcmSitedRpts
	 * 기 능: 현설청취보고서 & 현설참가업체 LIST & 첨부파일 LIST SELECT
	 **************************************************************************/
     scwin.fnTcmSitedRpts = function() {
		svcId = "selectTcmSitedRpts";
        ajaxLib.ajax("/cm/cm0000/CM00000505U/selectTcmSitedRpts", {
            mode : "asynchronous",
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : "fnCallback",
            errCallback : "fnErrCallback"
        });
    };


  	/**************************************************************************
	 * 함 수 명: scwin.fnSave
	 * 기 능: 현설청취보고서 저장
	 **************************************************************************/
     scwin.fnSave = function() {
		// 필수입력
		if (!scwin.fnValidate()) return false;

		if (dsTcmSitedRptsMap.getModifiedJSON(true).length  == 0) {
		 	com.showMessage("CFE0031");	 //수정할 자료가 없습니다.
			return false;
		}

    	if (!confirm("저장 하시겠습니까?")) return;

		// 포함_공사_값 SET
		var sChk = cbxIncluConstVal.getValue( "," );
		dsTcmSitedRptsMap.set( "incluConstVal" , sChk );

		//console.log("file Id : " + com.getUdcValue("udcAflId", "aflId"));

		// 첨부파일 ID SET
		dsTcmSitedRptsMap.set( "aflId" , com.getUdcValue("udcAflId", "aflId") );

		svcId = "saveTcmSitedRpts";
        ajaxLib.ajax("/cm/cm0000/CM00000505U/saveTcmSitedRpts", {
            mode : "synchronous",
            method : "POST",
            requestData : {
            		tcmSitedRpts : dsTcmSitedRptsMap.getJSON()
            	},
            callback : "fnCallback",
            errCallback : "fnErrCallback"
        });
    };

  	/**************************************************************************
	 * 함 수 명: scwin.fnValidate
	 * 기 능: 저장 시 Validate
	 **************************************************************************/
	scwin.fnValidate = function() {

	    if ( dsTcmSitedRptsMap.get("bzdpTndrProcCd").length == 0){
	        com.showMessage("CFW0003", "입찰절차");// $1 항목을 확인하십시오.
	        sbxBzdpTndrProcCd.focus();
	        return false;
	    }
	    if ( dsTcmSitedRptsMap.get("sbidrSelnCd").length == 0){
	        com.showMessage("CFW0003", "낙찰자선정");// $1 항목을 확인하십시오.
	        sbxSbidrSelnCd.focus();
	        return false;
	    }
	    if ( dsTcmSitedRptsMap.get("sbidrSelnCd") == "ES00504"){
	    	// 기타인 경우 Text 입력 Check
	    	if ( dsTcmSitedRptsMap.get("sbidrSelnRmkCn").length == 0){
		        com.showMessage("CFW0003", "낙찰자선정_기타내용");// $1 항목을 확인하십시오.
		        ibxSbidrSelnRmkCn.focus();
		        return false;
		    }
		}

	    return true;
	};

  	/**************************************************************************
	 * 함 수 명: scwin.fnSelectAfter
	 * 기 능: 조회 후 후처리
	 **************************************************************************/
	scwin.fnSelectAfter = function() {
		// 포함공사 CHECK 처리
		var sIncluConstVal = dsTcmSitedRptsMap.get( "incluConstVal" );
		var sChkSplit = sIncluConstVal.replace(/,/gi, " ");	// setValue 사용 시 공백으로 대체함

		cbxIncluConstVal.setValue( sChkSplit );

		// 버튼 처리 관련
		var sAprvNo = dsTcmSitedRptsMap.get( "aprvNo" );
		var sAprvStusCd = dsTcmSitedRptsMap.get( "aprvStusCd" );
		var sExistYn = dsTcmSitedRptsMap.get( "existYn" );

//		console.log("sExistYn : " + sExistYn);
//		console.log("sAprvNo : " + sAprvNo);

		if (sExistYn == "N") {
			btnAprv.setDisabled( true );
			btnDel.setDisabled( true );
			btnSave.setDisabled( false );
		} else {
			if (sAprvNo == "") {
				btnAprv.setDisabled( false );
				btnDel.setDisabled( false );
				btnSave.setDisabled( false );
			} else {
				btnAprv.setDisabled( true );
				btnDel.setDisabled( true );
				btnSave.setDisabled( true );
			}
		}

		// 첨부파일 처리
		// 팝업입력 버튼 활성화 : 결재진행 시 비활성화로 변경해야 함 : 현재 활성화
        //com.getUdcComp("udcAflId", "btnAddFile").removeClass("hide");
        //com.getUdcComp("udcAflId", "btnAddFile").addClass("show");
        // 첨부파일 LIST 3개씩 : UDC.xml 참조
		com.getUdcComp("udcAflId", "gen_aflList").addClass("col3");

		var sJobGbCd = "CM";			// 업무구분
		var sMenuId  = "CM00000505U";	// 화면ID
		var sAflId   = dsTcmSitedRptsMap.get("aflId");	// 첨부파일ID
		var sFileExt = "*.*";

		com.setUdcValue( "udcAflId", "jobGbCd", sJobGbCd);
		com.setUdcValue( "udcAflId", "menuId", sMenuId);
		com.setUdcValue( "udcAflId", "aflId", sAflId);
		com.setUdcValue( "udcAflId", "fileExt", sFileExt);
		if (sAprvNo == "") {
			com.setUdcValue( "udcAflId", "usage", "INS"); // 버튼숨기기처리 : 필수값
		} else {
			com.getUdcComp("udcAflId", "btnAddFile").addClass("hide");//파일첨부 비활성화
			com.setUdcValue( "udcAflId", "usage", ""); // 버튼숨기기
		}
        com.setUdcValue( "udcAflId", "fileSize", "INS"); // 용량표시안함(표시함:INS) : 필수값
	};


  	/**************************************************************************
	 * 함 수 명: scwin.fnDelete
	 * 기 능: 현설청취보고서 삭제
	 **************************************************************************/
     scwin.fnDelete = function() {
	    if ( dsTcmSitedRptsMap.get("existYn") == "N"){
	        com.showMessage("CFE0032");// 삭제할 자료가 없습니다.
	    	return false;
		}

    	if (!confirm("현설청취보고서 자료를 삭제하시겠습니까?")) return;

		svcId = "deleteTcmSitedRpts";
        ajaxLib.ajax("/cm/cm0000/CM00000505U/deleteTcmSitedRpts", {
            mode : "synchronous",
            method : "POST",
            requestData : {
            		tcmSitedRpts : dsTcmSitedRptsMap.getJSON()
            	},
            callback : "fnCallback",
            errCallback : "fnErrCallback"
        });
    };


/**************************************************************************
 * [4] Callback
 **************************************************************************/
    scwin.fnCallback = function(result, e) {
        switch(svcId) {
            case "selectTcmSitedRpts" :	// 초기 화면 로딩 시
                dsTcmSitedRptsMap.setJSON(result.data.tcmSitedRpts);
                scwin.fnSelectAfter();
                break;
            case "saveTcmSitedRpts" :
	        	com.showMessage("CFA0012");// 저장되었습니다.
				scwin.fnTcmSitedRpts();
            	break;
            case "deleteTcmSitedRpts" :
	        	com.showMessage("CMW0035");// 삭제되었습니다.
				scwin.fnTcmSitedRpts();
            	break;
            case "updateAprvNo" ://결재번호 UPDATE
				scwin.fnTcmSitedRpts();
            	break;
        }
    }

    scwin.fnErrCallback = function(result, e) {
       console.log(result);
    }


 /**************************************************************************
 * [5] Event 정의
 **************************************************************************/
    /**************************************************************************
     * Event: scwin.sbxSbidrSelnCd_onchange
     * 기 능: 낙찰자선정 SELECT BOX Change
     **************************************************************************/
	scwin.sbxSbidrSelnCd_onchange = function() {
		//console.log("sbxSbidrSelnCd_onchange");
		var sSel = sbxSbidrSelnCd.getValue();

		//console.log("sSel : " + sSel);
		if (sSel == "ES00504")	{ // 기타일 경우에만 Input Box 활성화
			ibxSbidrSelnRmkCn.setDisabled( false );
		} else {
			ibxSbidrSelnRmkCn.setDisabled( true );
			dsTcmSitedRptsMap.set( "sbidrSelnRmkCn" , "" );//비고_내용 Clear
		}

	};


    /**************************************************************************
     * Event: scwin.cbxIncluConstVal_onchange
     * 기 능: 포함공사 CHECK BOX Change
     **************************************************************************/
	scwin.cbxIncluConstVal_onchange = function() {
		var sChk = cbxIncluConstVal.getValue( "," );
		var sEtc = "ES00711";		// 기타 코드

		if (sChk.indexOf(sEtc) == -1) {
			ibxIncluConstEtcCn.setDisabled( true );
			dsTcmSitedRptsMap.set( "incluConstEtcCn" , "" );//비고_내용 Clear
		} else {
			ibxIncluConstEtcCn.setDisabled( false );
		}


//		console.log("sChk : " + sChk);
//
//		var sChkSplit = sChk.split(',');
//
//		for (var i = 0; i < sChkSplit.length; i++) {
//			var _constVal = sChkSplit[i];
//
//			console.log("_constVal : " + _constVal);
//		}
	};

