	};


   	/**************************************************************************
	 * 함 수 명: scwin.btnList_onclick
	 * 기 능: 현설청취보고서목록 화면으로 이동
	 **************************************************************************/
	scwin.btnList_onclick = function() {
    	com.detailOpen("현설청취보고서목록", "CM00000500", "/ux/cm/cm0000/CM00000500U.xml", "1", {
            // 상단검색조건 SET
			schPjtNm : dsSearchMap.get("schPjtNm"),				//프로젝트명
			schUserNm : dsSearchMap.get("schUserNm"),			//현설참가자
			schPublCvlGbCd : dsSearchMap.get("schPublCvlGbCd"),	//공공민간구분코드
			schScdlDtFrom : dsSearchMap.get("schScdlDtFrom"),	//현설참가일_시작
			schScdlDtTo : dsSearchMap.get("schScdlDtTo"),		//현설참가일_종료
			schReqDtSel : dsSearchMap.get("schReqDtSel"),		//의뢰일조건
			schReqDtFrom : dsSearchMap.get("schReqDtFrom"),		//의뢰일_시작
			schReqDtTo	: dsSearchMap.get("schReqDtTo")			//의뢰일_종료
        });
	};

	//결재 클릭 이벤트
	scwin.btnAprv_onclick = function() {

		//결재본문 조회조건
		dsSrch.set( "pjtId" , dsSearchMap.get( "pjtId" ) );//프로젝트ID

        var paramObj = new Object();
        paramObj.jobGbCd = "CM";                                         // 업무구분코드
        paramObj.importUrl = "/ux/cm/cm5500/CM55000045E.xml";            // 출력할 결재본문페이지
        paramObj.aprvTtl = "[" + dsTcmSitedRptsMap.get( "pjtNm" ).replace(/'/gi, " ") + " 현설청취보고서";  // 결재제목
        paramObj.rvwrEmpno = "";        								 // 검토자_사번
        paramObj.fnlAprprEmpno = "";                               		 // 최종승인자_사번
        paramObj.aprvPrcgNm = "";                                        // 후처리결재처리명
        paramObj.aprvPrcgEventNm = "/cm/cmif/CMIfTelepiaPostProcess/notifyAprvCallSitedRpts";// 후처리결재처리 이벤트명
        paramObj.aprvDocModiPsblYn = "Y";                                // 결재문서 수정불가기능 Y/N
        paramObj.aprlnModiPsblYn = "N";                                  // 결재선 수정불가기능 Y/N
        paramObj.aprvAtchModiPsblYn = "Y";                               // 결재첨부 수정불가기능 Y/N
        paramObj.aprvAflId = dsTcmSitedRptsMap.get( "aflId" );           // 본문링크용 첨부파일 id (콤머로 구분해서 보내준다)
        paramObj.popupWidth = "1350";                                    // 팝업창 가로크기
        paramObj.popupHeight = "800";                                    // 팝업창 세로크기

        com.aprvSend(paramObj); //일반결재 호출
	};

   /**************************************************************************
	 * Event: scwin.aprvPublicAction
	 * 기 능: 결재처리후 결재번호 받아서 처리할 자체 업무이벤트 등록
	 **************************************************************************/
    scwin.aprvPublicAction = function() {
		dsTcmSitedRptsMap.set( "aprvNo" , com.getAprvNo() );//결재번호

        svcId = "updateAprvNo";
        ajaxLib.ajax("/cm/cm0000/CM00000505U/updateAprvNo", {
            mode : "asynchronous"
            , method : "POST"
            , requestData : dsTcmSitedRptsMap.getJSON()
            , callback : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };
