   /**************************************************************************
	 * Event: scwin.aprvPublicAction
	 * 기 능: 결재처리후 결재번호 받아서 처리할 자체 업무이벤트 등록
	 **************************************************************************/
    scwin.aprvPublicAction = function() {
		dsReportInfoMap.set( "aprvNo" , com.getAprvNo() );//결재번호

        svcId = "updateAprvNo";
        ajaxLib.ajax("/cm/cm0015/CM00150525T/updateAprvNo", {
            mode : "asynchronous"
            , method : "POST"
            , requestData : dsReportInfoMap.getJSON()
            , callback : "fnCallback"
            , errCallback : "fnErrCallBack"
        });
    };

