head.script:
/**************************************************************************
 * [1] onpageload(초기화포함)
 **************************************************************************/
    scwin.onpageload = function() {
        if (com.isEmpty(gcm.tab_main)) {
            dsSearchMap.setJSON(JSON.parse($w.getPopupParam()).data);
        } else {
            dsSearchMap.setJSON(com.getAllParam());
        }
        //com.selCdListVrbl("grdActbgtWorkList:itexpGb", {"upperCd": "CM004100","setCd": "charValN1"});
        grpTcmSiteMap.init();
        grpTcmActbgtHistAcumMap.init();

        //비목구분
        com.selCdListVrbl("grdTcmActbgtHistChgList:itexpGb", {
            "upperCd": "CM004100",
            "setCd": "charValN1"
        });

        // 첨부파일 처리
        // 팝업입력 버튼 활성화 : 결재진행 시 비활성화로 변경해야 함 : 현재 활성화
        com.getUdcComp("udcAflId", "btnAddFile").removeClass("show");
        com.getUdcComp("udcAflId", "btnAddFile").addClass("hide");
        com.getUdcComp("udcAflId", "gen_aflList").addClass("col3");                     // 첨부파일 LIST 3개씩 : UDC.xml 참조

        com.commonUI();
        scwin.fnSearch(tacTcmActbgtHist.getSelectedTabIndex());
    };

/************************************************************************************
 *  [2] 사용자 정의 함수
 ************************************************************************************/

    //조회
    scwin.fnSearch = function(tabIndex) {

        //tacTcmActbgtHist.getTabID( tabIndex )
        if (tacTcmActbgtHist.getTabID(tabIndex) == "tabTcmactbgtHist"){   //총괄표 탭
            ajaxLib.ajax("/cm/cm1510/CM15100505/selectTcmActbgtHistAcumList", {
                method: "POST",
                requestData: dsSearchMap.getJSON(),
                callback: function(result, e){
                    dsTcmSiteMap.setJSON(result.data.selectTcmSite);
                    dsTcmActbgtHistAcumMap.setJSON(result.data.selectTcmActbgtHistAcum);
                    dsTcmActbgtList.setJSON(result.data.selectTcmActbgtAdJustList);

                    com.setUdcValue( "udcAflId", "aflId", dsTcmActbgtHistAcumMap.get("aflId"));
                },
                errCallback: ""
            });
        } else if (tacTcmActbgtHist.getTabID(tabIndex) == "tabTcmactbgtHistChgList"){   //내역서 탭
            ajaxLib.ajax("/cm/cm1510/CM15100505/selectTcmActbgtHistChgList", {
                method: "POST",
                requestData: dsSearchMap.getJSON(),
                callback: function(result, e){
                    dsTcmActbgtHistChgList.setJSON(result.data);

                    //공종인 경우 파란색 폰트와 금액은 모두 null 처리.
                    for (var i=0; i<dsTcmActbgtHistChgList.getTotalRow(); i++){
                        if (dsTcmActbgtHistChgList.getCellData(i, "itemiYn") == "N"){
                            grdTcmActbgtHistChgList.setRowColor(i, "blue");
                            dsTcmActbgtHistChgList.setRowJSON(i, {
                                contQty: null,
                                contUct: null,
                                contAmt: null,
                                actbgtQty: null,
                                actbgtUct: null,
                                actbgtAmt: null,
                                plActbgtQty: null,
                                plActbgtUct: null,
                                plActbgtAmt: null,
                                chgbfItexpGb: null,
                                chgbfContQty: null,
                                chgbfContUct: null,
                                chgbfContAmt: null,
                                chgbfActbgtQty: null,
                                chgbfActbgtUct: null,
                                chgbfActbgtAmt: null,
                                chgbfPlActbgtQty: null,
                                chgbfPlActbgtUct: null,
                                chgbfPlActbgtAmt: null,
                                incContQty: null,
                                incContUct: null,
                                incContAmt: null,
                                incActbgtQty: null,
                                incActbgtUct: null,
                                incActbgtAmt: null,
                                incPlActbgtQty: null,
                                incPlActbgtUct: null,
                                incPlActbgtAmt: null,
                                },
                                true
                            );
                        }
                    }
                },
                errCallback: ""
            });
        } else {
            //"잘못된 경로입니다. 관리자에게 문의하세요.")
            return false;
        }
    };

 /************************************************************************************
 *  [3] 사용자 정의 함수
 ************************************************************************************/

    //조회 클릭
	scwin.btnFind_onclick = function() {
		scwin.fnSearch(tacTcmActbgtHist.getSelectedTabIndex());
	};

    //목록 클릭
	scwin.btnList_onclick = function() {
        dsSearchMap.set("listSearchYn", "Y");
		com.detailOpen(dsSearchMap.get("returnScrNm"), 
		"CM15100500", dsSearchMap.get("returnUrl"), "1", 
		dsSearchMap.getJSON());
	};

	scwin.tacTcmActbgtHist_ontabload = function(tabID,idx,userID) {
        if (tacTcmActbgtHist.getTabID(idx) == "tabTcmactbgtHistChgList"){
            com.selCdListVrbl("grdTcmActbgtHistChgList:itexpGb", {"upperCd": "CM004100", "setCd": "charValN1"});
            com.selCdListVrbl("grdTcmActbgtHistChgList:chgbfItexpGb", {"upperCd": "CM004100", "setCd": "charValN1"});
            scwin.fnSearch(idx);
        }

	};

    //엑셀다운 클릭
	scwin.btnExcelDwn_onclick = function() {
        grdTcmActbgtHistChgList.advancedExcelDownload({
            fileName: "실행예산변경이력상세.xlsx",
            useStyle: "true",
            indent: 1,
            useSubTotal : true,             // Sub-Total 표시 
            headerFontColor : "#FFFFFF",    // Header Font Color
            headerColor : "#D9D9D9",        // Header Back Color
            subTotalColor : "#FCE4D6",      // SubTotal Back Color
            footerColor : "#BDD7EE"         // Footer Color
        });
	};

	scwin.tacTcmActbgtHist_onchange = function(tabID,idx,userTabID) {
		com.commonUI();
	};

	scwin.btnTreeSpanAll_onclick = function() {
        if (dsTcmActbgtHistChgList.getTotalRow() <= 0) {
            return false;
        }

        if (btnTreeSpanAll.getValue() == "전체 펼치기"){
            btnTreeSpanAll.setValue("전체 접기");
            grdTcmActbgtHistChgList.spanAll(true);
        } else {
            btnTreeSpanAll.setValue("전체 펼치기");
            grdTcmActbgtHistChgList.spanAll(2);
        }
	};
