head.script:
/**************************************************************************
 * [1] onpageload(초기화포함)
 **************************************************************************/
    scwin.onpageload = function() {

        if (typeof reqJsonParam == 'undefined') {
            var dsSrch = com.getAprvParam("dsSrch");
            dsSearchMap.setJSON( dsSrch.getJSON() );
        } else {
            var jsonArray = JSON.parse(reqJsonParam);
            $.each(jsonArray, function(idx, item){
                dsSearchMap.setJSON(item);
            });
        }

        // 팝업입력 버튼 활성화 : 결재진행 시 비활성화로 변경해야 함 : 현재 활성화
//        com.getUdcComp("udcAflId", "btnAddFile").removeClass("show");
//        com.getUdcComp("udcAflId", "btnAddFile").addClass("hide");
//        com.getUdcComp("udcAflId", "gen_aflList").addClass("col3");                     // 첨부파일 LIST 3개씩 : UDC.xml 참조

        com.autoHeight();
        scwin.fnSearch();
        $("body").css("overflow","scroll");
    };

/************************************************************************************
 *  [2] 사용자 정의 함수
 ************************************************************************************/

    //조회
    scwin.fnSearch = function() {
        ajaxLib.ajax("/cm/cm1510/CM15100505/notifyTcmActbgtHistAcumList", {
            method: "POST",
            requestData: dsSearchMap.getJSON(),
            callback: function(result, e){
                
                dsTcmSiteMap.setJSON(result.data.selectTcmSite);
                dsTcmActbgtHistAcumMap.setJSON(result.data.selectTcmActbgtHistAcum);
                dsTcmActbgtList.setJSON(result.data.selectTcmActbgtAdJustList);
                dsTcmActbgtHistChgList.setJSON(result.data.selectTcmActbgtHistChgList);

                //com.setUdcValue( "udcAflId", "aflId", dsTcmActbgtHistAcumMap.get("aflId"));                
                //공종인 경우 파란색 폰트와 금액은 모두 null 처리.
                for (var i=0; i<dsTcmActbgtHistChgList.getTotalRow(); i++){
                    if (dsTcmActbgtHistChgList.getCellData(i, "itemiYn") == "N"){
                        grdTcmActbgtHistChgList.setRowColor(i, "blue");
                        dsTcmActbgtHistChgList.setRowJSON(i, {
                                contQty: null,
                                contUct: null,
                                contAmt: null,
                                actbgtQty: null,
                                actbgtUct: null,
                                actbgtAmt: null,
                                plActbgtQty: null,
                                plActbgtUct: null,
                                plActbgtAmt: null,
                                chgbfItexpGb: null,
                                chgbfContQty: null,
                                chgbfContUct: null,
                                chgbfContAmt: null,
                                chgbfActbgtQty: null,
                                chgbfActbgtUct: null,
                                chgbfActbgtAmt: null,
                                chgbfPlActbgtQty: null,
                                chgbfPlActbgtUct: null,
                                chgbfPlActbgtAmt: null,
                                incContQty: null,
                                incContUct: null,
                                incContAmt: null,
                                incActbgtQty: null,
                                incActbgtUct: null,
                                incActbgtAmt: null,
                                incPlActbgtQty: null,
                                incPlActbgtUct: null,
                                incPlActbgtAmt: null,
                            },
                            true
                        );
                    }
                }

            },
            errCallback: function(result, e){
                com.showMessage("CFE0112"); //작업 중 에러가 발생하였습니다. 관리자에게 문의하십시오
            }
        });
    };

    //탭 onload
	scwin.tacTcmActbgtHist_ontabload = function(tabID,idx,userID) {
        com.selCdListVrbl("grdTcmActbgtHistChgList:itexpGb", {"upperCd": "CM004100", "setCd": "charValN1"});
        com.selCdListVrbl("grdTcmActbgtHistChgList:chgbfItexpGb", {"upperCd": "CM004100", "setCd": "charValN1"});
	};

    //엑셀다운 클릭
	scwin.btnExcelDwn_onclick = function() {
        grdTcmActbgtHistChgList.advancedExcelDownload({
            fileName: "실행예산변경.xlsx",
            useStyle: "true",
            indent: 1
        });
	};

	scwin.tacTcmActbgtHist_onchange = function(tabID,idx,userTabID) {
        com.autoHeight();
	};

    //전체 펼치기, 접기
    scwin.btnTreeSpanAll_onclick = function() {
        if (dsTcmActbgtHistChgList.getTotalRow() <= 0) {
            return false;
        }

        if (btnTreeSpanAll.getValue() == "전체 펼치기"){
            btnTreeSpanAll.setValue("전체 접기");
            grdTcmActbgtHistChgList.spanAll(true);
        } else {
            btnTreeSpanAll.setValue("전체 펼치기");
            grdTcmActbgtHistChgList.spanAll(2);
        }
    };
