	/************************************************************************************
		DataCollection description

		dsAprvReqList: 현장관리비요청서
		dsAprvAproList: 현장관리비승인서
		dsSearchMap: 검색조건 parameter
		dsSiteMngctChgMap : 현장_관리비_변경 정보
	 ************************************************************************************/

    /************************************************************************************
     *  [1] 전역변수
     ************************************************************************************/

    /************************************************************************************
     *  [2] onpageload(초기화포함)
     ************************************************************************************/
	scwin.onpageload = function() {
        //UI팀용 함수 호출
        com.commonUI();

		dsSearchMap.set( "siteCd" , JSON.parse($p.getPopupParam()).data.paramKeySiteCd );//현장코드
		dsSearchMap.set( "exeYy" , JSON.parse($p.getPopupParam()).data.paramKeyExeYy );//실행_년도
		dsSearchMap.set( "chgDgr" , JSON.parse($p.getPopupParam()).data.paramKeyChgDgr );//변경_차수

        ajaxLib.ajax("/cm/cm1515/CM15150005P/selectAprvInfoList", {
            method : "POST",
            requestData : dsSearchMap.getJSON(),
            callback : //"fnCallback",
                function(result, e){

					dsSiteMngctChgMap.setJSON( result.data.dsSiteMngctChgMap );//현장_관리비_변경 정보
					dsAprvReqList.setJSON( result.data.dsAprvReqList );//현장관리비요청서
					dsAprvAproList.setJSON( result.data.dsAprvAproList );//현장관리비승인서

					//현장관리비요청서
					for(var i=0; i<dsAprvReqList.getRowCount(); i++){
						var gen1Idx = generator1.insertChild();
						generator1.getChild( gen1Idx , "spnReqAprvStusNm" ).setValue( dsAprvReqList.getCellData( i , "aprvStusNm" ) );//결재상태
						generator1.getChild( gen1Idx , "spnReqAprprNm" ).setValue( dsAprvReqList.getCellData( i , "aprprNm" ) );//결재자
						generator1.getChild( gen1Idx , "spnReqAprvCmtryCn" ).setValue( dsAprvReqList.getCellData( i , "aprvCmtryCn" ) );//결재의견
						generator1.getChild( gen1Idx , "spnReqAproDt" ).setValue( dsAprvReqList.getCellData( i , "aproDtm" ) );//결재일시
					}

					if(dsSiteMngctChgMap.get( "dirmgExctAproCd" ) == "CM00401305"){//반송
						var gen2Idx = generator2.insertChild();
						generator2.getChild( gen2Idx , "spnAprvStusNm" ).setValue( "반송" );//결재상태
						generator2.getChild( gen2Idx , "spnAprprNm" ).setValue( dsSiteMngctChgMap.get( "pbsvTeamRtnprNm" ) );//결재자
						generator2.getChild( gen2Idx , "spnAprvCmtryCn" ).setValue( dsSiteMngctChgMap.get( "pbsvTeamAprvOpinCn" ) );//결재의견
						generator2.getChild( gen2Idx , "spnAproDt" ).setValue( dsSiteMngctChgMap.get( "fnlModiDtm" ) );//결재일시
					}else{
						//현장관리비승인서
						for(var i=0; i<dsAprvAproList.getRowCount(); i++){
							var gen2Idx = generator2.insertChild();
							generator2.getChild( gen2Idx , "spnAprvStusNm" ).setValue( dsAprvAproList.getCellData( i , "aprvStusNm" ) );//결재상태
							generator2.getChild( gen2Idx , "spnAprprNm" ).setValue( dsAprvAproList.getCellData( i , "aprprNm" ) );//결재자
							generator2.getChild( gen2Idx , "spnAprvCmtryCn" ).setValue( dsAprvAproList.getCellData( i , "aprvCmtryCn" ) );//결재의견
							generator2.getChild( gen2Idx , "spnAproDt" ).setValue( dsAprvAproList.getCellData( i , "aproDtm" ) );//결재일시
						}
					}

					//첨부파일
					scwin.getAprvAflIdFile();

            	},
            errCallback : "fnErrCallBack"
        })
	};

	scwin.onpageunload = function() {

	};

    /************************************************************************************
     *  [3] 사용자 정의함수 함수
     ************************************************************************************/
    //첨부파일
    scwin.getAprvAflIdFile = function() {
    	if(dsSiteMngctChgMap.get( "aflId" ) != ""){
	        var aprvAflId = dsSiteMngctChgMap.get( "aflId" ).split(",");
	        for (var i = 0; i < aprvAflId.length; i++) {
	            var rtnArr = com.udcAflList(aprvAflId[i]) || [];

	            $.each(rtnArr, function(key,data){
	                var idx = gen_aflList.insertChild();
	                var gen_Obj = gen_aflList.getChild(idx,"gen_generator");
	                var t = gen_Obj.insertChild();
	                var btn_obj = gen_Obj.getChild(t,"btn_addFile");
	                var dFileSizeInit = "<span style='display:none;'>"+data.aflNm+"("+data.fileSize+"byte)</span>";
	                var dFileSize = "("+data.fileSize+"byte)";
	                btn_obj.setValue(data.aflNm + dFileSize + dFileSizeInit);
	                btn_obj.setUserData("userData",data);
	                var href = "/cf/CF00350000P/downloadMAV?aflp="+data.aflp;
	                $("#"+btn_obj.getID()).off("click").on("click", function() {
	                    $.fileDownload(href);
	                    return;
	                });
	            });
	        }
    	}
    };

    /************************************************************************************
     *  [4] Callback 함수
     ************************************************************************************/
    /** 오류시 처리 **/
    scwin.fnErrCallBack = function(result, e) {

    };
