     /************************************************************************************
        DataCollection description

        dsTcmSiteMngctDtlList: 현장관리비 현황 GRID
        dsSearchMap: 검색조건 parameter
     ************************************************************************************/

    /************************************************************************************
    *  [1] 전역변수
    ************************************************************************************/

    /************************************************************************************
    *  [2] onpageload(초기화포함)
    ************************************************************************************/
	scwin.onpageload = function() {
        //SelectBox 코드값 맵핑
        com.selCdListVrbl( "grdTcmSiteMngctDtlList:itexpGb", {"upperCd": "CM004100","setCd": "charValN1", "setNm" : "cdNm"});//비목
	};

	scwin.onpageunload = function() {

	};

    /** 화면초기화 **/
    scwin.fnInitial = function(siteCd, exeYy) {

    	dsSearchMap.set( "siteCd", siteCd );
    	dsSearchMap.set( "exeYy", exeYy );

        grdTcmSiteMngctDtlList.setFixedColumn( 9 );//틀고정

        //UI팀용 함수 호출
        com.commonUI();
        
        scwin.fnSearch();
    };

    /************************************************************************************
     *  [3] 사용자 정의함수 함수
     ************************************************************************************/
	//DataSet 초기화
	scwin.fnDataInit = function(){
		dsTcmSiteMngctDtlList.removeAll();
		dsSearchMap.setEmptyValue();
		com.setUdcValue("dsTotalCount","udcListCount",0);
	}	
	
	//조회
    scwin.fnSearch = function() {
        ajaxLib.ajax("/cm/cm1515/CM15150500U/selectTcmSiteMngctDtlList", {
            method : "POST",
            requestData : {
            				 param : dsSearchMap.getJSON()
            			},
            callback : //"fnCallback",
                function(result, e){
	                dsTcmSiteMngctDtlList.setJSON(result.data.dsTcmSiteMngctDtlList);
	                com.setUdcValue("dsTotalCount","udcListCount",dsTcmSiteMngctDtlList.getTotalRow());   //GRID ROW COUNT

	                if(dsSearchMap.get( "exeYy" ) != ""){
		               	grdTcmSiteMngctDtlList.setHeaderValue( "column289" , dsSearchMap.get( "exeYy" ) + "년도" );//헤더 TITLE변경
		               	var bfYear = parseInt(dsSearchMap.get( "exeYy" ), 10)-1;//전년도
		               	grdTcmSiteMngctDtlList.setHeaderValue( "column290" , "전년 누계실적<br/>(" + bfYear + ")" );//헤더 TITLE변경
	                }
            	},
            errCallback : "fnErrCallBack"
        });
    }

    /************************************************************************************
     *  [4] Callback 함수
     ************************************************************************************/
    /** 오류시 처리 **/
    scwin.fnErrCallBack = function(result, e) {

    };

    /************************************************************************************
     *  [5] Event 함수
     ************************************************************************************/
	//엑셀 다운로드 이벤트
	scwin.btnExcelDwn_onclick = function() {
		var result = [];
		var totalCol = grdTcmSiteMngctDtlList.getTotalCol();
		for(var i = 0; i < totalCol; i++) {
			if (!grdTcmSiteMngctDtlList.getColumnVisible(i)) {
				result.push(i);
			}
		}
		var excelOpt = {
			 fileName: "현장관리비 통제성.xlsx"    //또는 xlsx 확장자 사용
			,removeColumns: result.join(",")
		    ,useSubTotal: true    			//colMerge된 컬럼을 Merge해서 출력 할 지 여부
		    ,colMergeTextAlign: "center"    //colMerge된 컬럼의 textAlign설정 (bottom, center, top 설정)
			,colMerge : true
            ,headerFontColor : "#FFFFFF"    // Header Font Color
            ,headerColor : "#D9D9D9"        // Header Back Color
            ,subTotalColor : "#FCE4D6"      // SubTotal Back Color
            ,footerColor : "#BDD7EE"        // Footer Color
		};  //excel download 옵션
		grdTcmSiteMngctDtlList.advancedExcelDownload( excelOpt );
	};

	//현장관리비 내역 GRID 클릭 이벤트
	scwin.grdTcmSiteMngctDtlList_oncellclick = function(row,col) {
		var _columnID = grdTcmSiteMngctDtlList.getColumnID( col );
		if(_columnID.substring(0,4) == "pfmc") {//1월~12월 실적
			var popOps = {
							  popup_name:"현장관리비 실적현황"
				           	, url:"/ux/cm/cm1515/CM15150505P.xml"
				           	, data:{
										 paramKeySiteCd : dsTcmSiteMngctDtlList.getCellData( row , "siteCd" )//현장코드
										,paramKeyExeYy : dsTcmSiteMngctDtlList.getCellData( row , "exeYy" )//기준년도
										,paramKeyExeMm : _columnID.substring(4,6)//실행_월
										,paramKeyBgtItemiCd : dsTcmSiteMngctDtlList.getCellData( row , "bgtItemiCd" )//내역코드
										//,paramKeySiteNm : wfmKeySiteInfo.getWindow().ibxKeySiteNm.getValue()//현장명
										,paramKeyStndItemiNm : dsTcmSiteMngctDtlList.getCellData( row , "stndItemiNm" )//분류
										,paramKeyBgtItemiNm : dsTcmSiteMngctDtlList.getCellData( row , "bgtItemiNm" )//내역
				           	       }
				           	, callbackFn : ""
				           	, widType:"XL"
				           	, height:"580"
			             };
			com.popup_open(popOps);
		}
	};
